import { Component, OnInit } from '@angular/core';
import { FieldType, FieldTypeConfig } from '@ngx-formly/core';
import { InputNumberModule } from 'primeng/inputnumber';

@Component({
  selector: 'n2-formly-month-picker',
  template: `
    <p-calendar [placeholder]="to.placeholder"
    [formControl]="formControl" [formlyAttributes]="field"
    view="month" dateFormat="MM yy" [appendTo]="'body'">
  `,
})
export class FormlyMonthPickerComponent extends FieldType<FieldTypeConfig> implements OnInit {
  constructor() {
    super();
  }

  ngOnInit(): void {
    //
  }

  formatDatetoMonth(): void {
    //
  }
}
