import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { EnqCompany } from '../../model/company.model';

@Component({
  selector: 'n2-company-partial-csn-search',
  templateUrl: './company-partial-csn-search.component.html',
  styleUrls: ['./company-partial-csn-search.component.scss']
})
export class CompanyPartialCsnSearchComponent implements OnInit {

  @Input() resultList: EnqCompany[];
  @Output() selectEvent = new EventEmitter<string>();

  sampleData = [
    { id: "1", csn: "12345678E-PTE-01", companyName: "NCS Pte Ltd"},
    { id: "2", csn: "12345678E-PTE-02", companyName: "NCS #2 Pte Ltd"}
  ];

  constructor() {
    // to do
   }

  ngOnInit(): void {
    // to do
  }

  handleSelection(id: string): void {
    console.log("Emit event = " + id);
    this.selectEvent.emit(id);
  }

}
<p-table
    [value]="sampleData"
    styleClass="p-datatable-gridlines"
    [paginator]="true"
    [rows]="10"
    [rowsPerPageOptions]="[10,25,50]"
    currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
    [showCurrentPageReport]="true"
    selectionMode="single">
    <ng-template pTemplate="caption">
        <span icTranslate="nspay2App.enquiry.company.partialCsnSearch.enquiryResult"></span>
    </ng-template>
    <ng-template pTemplate="header">
        <tr>
            <th scope="col" style="width: 10%">SN</th>
            <th scope="col" style="width: 25%" pSortableColumn="csn">CSN
                <p-sortIcon field="csn"></p-sortIcon>
                <p-columnFilter type="text" field="csn" display="menu" matchMode="contains"></p-columnFilter>
            </th>
            <th scope="col" style="width: 65%" pSortableColumn="companyName">Company Name
                <p-sortIcon field="companyName"></p-sortIcon>
                <p-columnFilter type="text" field="companyName" display="menu" matchMode="contains"></p-columnFilter>
            </th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-company let-i="rowIndex">
        <tr (click)="handleSelection(company.id)">
            <td>{{ i + 1 }}</td>
            <td>{{ company.csn }}</td>
            <td>{{ company.companyName }}</td>
        </tr>
    </ng-template>
</p-table>
