import { Component, Input, OnInit } from '@angular/core';
import { EnqCompany } from '../model/company.model';

@Component({
  selector: 'n2-company-particulars-summary',
  templateUrl: './company-particulars-summary.component.html',
  styleUrls: ['./company-particulars-summary.component.scss']
})
export class CompanyParticularsSummaryComponent implements OnInit {

  @Input() isCompanyFound: boolean;
  @Input() company: EnqCompany;

  displayCompanyCABDialog: any = { display: false };
  displayCompanyExpandedSummaryDialog: any = { display: false };

  highlightCaution: boolean;
  highlightAlert: boolean;
  highlightBlock: boolean;

  constructor() {
    // to do
   }

   ngOnInit(): void {
    this.initialiseCabFlags();
  }

  initialiseCabFlags(): void {
    // sample scenario
    this.highlightCaution = true;
    this.highlightAlert = true;
    this.highlightBlock = false;
  }

  onOpen(dialog: any): void {
    dialog.display = true;
  }

}
