import { Component, OnInit } from '@angular/core';
import { HttpResponse } from '@angular/common/http';
import { FormBuilder, Validators } from '@angular/forms';
import { ConfirmationService } from 'primeng/api';
import { DynamicDialogConfig, DynamicDialogRef } from 'primeng/dynamicdialog';
import { NSPAY2_ROLES } from 'app/nspay2.constants';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { AccessRightRequestService } from '../../service/access-right-request.service';
import { IAccessRightRequest, IRejectRequest } from '../../model/access-right-request.model';
import { AccountStatus, RequestStatus, Salutation } from '../../model/overview-enum.model';

@Component({
    selector: 'n2-view-access-right-request',
    templateUrl: './view-access-right-request.component.html',
    styleUrls: ['./view-access-right-request.component.scss'],
    providers: [ConfirmationService]
})

export class ViewAccessRightRequestComponent implements OnInit {

    // modals
    displayRejectionReasonDialog: any = { display: false };
    displayRejectionFormDialog: any = { display: false };

    // models
    idToReason: IRejectRequest;
    selectAccessRightRequest: IAccessRightRequest;
    accountStatusEnum: { [key: string]: AccountStatus } = AccountStatus;
    requestStatusEnum: { [key: string]: RequestStatus } = RequestStatus;
    salutationEnum: { [key: string]: Salutation } = Salutation;

    // for view record
    isApproved = false;
    isRejected = false;

    // for approve/reject record
    currentAccountMainRole: any;
    isApprovingOrRejecting = false;
    rejectionForm = this.formBuilder.group({
        reason: ['', [Validators.required]],
    });

    constructor(
      private accessRightRequestService: AccessRightRequestService,
      private confirmationService: ConfirmationService,
      private dialogRef: DynamicDialogRef,
      private dialogConfig: DynamicDialogConfig,
      private formBuilder: FormBuilder,
      private toastService: CustomToastService,
    ) { }

    ngOnInit(): void {
      this.load();
      this.sortScenario();
    }
    
    load(): void {
      this.selectAccessRightRequest = this.dialogConfig.data.record;
      console.log("Selected access right request", this.selectAccessRightRequest);
      this.currentAccountMainRole = this.dialogConfig.data.currentAccountMainRole;
    }

    sortScenario(): void {
      if (this.requestStatusEnum[this.selectAccessRightRequest.requestStatus] === RequestStatus.REJECTED) {
        this.isRejected = true;
        console.log("Showing rejected scenario");
      } else if (this.requestStatusEnum[this.selectAccessRightRequest.requestStatus] === RequestStatus.APPROVED) {
        this.isApproved = true;
        console.log("Showing approved scenario");
      } else if (this.requestStatusEnum[this.selectAccessRightRequest.requestStatus] === RequestStatus.PENDING_APPROVAL &&
        this.currentAccountMainRole[NSPAY2_ROLES.MINDEF_APPROVER] !== undefined) {
        this.isApprovingOrRejecting = true;
        console.log("Showing approve or reject scenario");
      } else {
        console.log("Showing view record scenario");
      }
    }

    confirm(event: Event, approve: boolean): void {
      console.log("Choosing to proceed...");
      this.confirmationService.confirm({
        target: event.target,
        message: 'Good to go?',
        icon: 'pi pi-exclamation-triangle',
        acceptLabel: "Proceed",
        rejectLabel: "No",
        accept: () => {
          console.log("Submitting request...");
          this.submitRequestDecision(approve);
          
          // ensure delay so that able to update database
          setTimeout(() => {
            this.closeDialog(true);
          }, 1000);
        },
        reject: () => {
          this.closeDialog(false);
        }
      });
    }

    submitRequestDecision(approve: boolean): void {
      let message: string;
      if (approve === true) {
        this.isApproved = true;
        const accessRequestApproveObserver = {
          next: (x: HttpResponse<any>) => console.log(x),
          error: (err: Error) => {
            console.error(err);
            this.toastService.error(err.message);
          },
          complete: () => console.log('Access Right Request approved successfully'),
        }
        this.accessRightRequestService.approveRequest(this.dialogConfig.data.record.id).subscribe(accessRequestApproveObserver);

        message = "Access Right Request has been approved";
        this.toastService.success(message);
      } else {
        const accessRequestRejectedObserver = {
          next: (x: HttpResponse<any>) => console.log(x),
          error: (err: Error) => {
            console.error(err);
            this.toastService.error(err.message);
          },
          complete: () => console.log('Access Right Request rejected successfully'),
        }

        if (this.rejectionForm.valid) {
          this.idToReason = {
            id: this.dialogConfig.data.record.id,
            reason: this.rejectionForm.value.reason
          };
          this.accessRightRequestService.rejectRequest(this.idToReason).subscribe(accessRequestRejectedObserver);
          console.log("Reject reason is", this.idToReason);
        }

        message = "Access Right Request has been rejected";
        this.toastService.success(message);
      }
    }

    onOpen(dialog: any): void {
      dialog.display = true;
    }

    closeDialog(cond: boolean): void {
      this.dialogRef.close(cond);
    }
}
