import { Component, OnInit } from '@angular/core';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { AccountService } from 'app/core/auth/account.service';
import { HttpResponse } from '@angular/common/http';

@Component({
  selector: 'ic-user-idle-dialog',
  templateUrl: './user-idle-dialog.component.html',
})
export class UserIdleDialogComponent implements OnInit {
  idleMinites?: string;
  constructor(public activeModal: NgbActiveModal, private accountService: AccountService) {}

  ngOnInit(): void {
    this.accountService.getSessionTimeoutMin().subscribe((res: HttpResponse<any>) => {
      // eslint-disable-next-line
      const idleTime = parseInt(+res.body[1] / 60 + '', 0);
      if (idleTime === 0) {
        this.idleMinites = '1';
      } else {
        // eslint-disable-next-line @typescript-eslint/restrict-plus-operands
        this.idleMinites = idleTime + '';
      }
    });
  }

  clear(): void {
    this.activeModal.dismiss('cancel');
  }
}
<div class="modal-header">
  <h4 class="modal-title" icTranslate="idle.warningtitle">Session will be expired</h4>
  <button type="button" class="close" data-dismiss="modal" aria-hidden="true" (click)="clear()">&times;</button>
</div>
<div class="modal-body">
  <ic-alert-error></ic-alert-error>
  <p>
    Your session will be expired in <strong>{{ idleMinites }}</strong> minutes, please click ok to proceed!
  </p>
</div>
<div class="modal-footer">
  <button type="button" class="btn btn-warning" data-dismiss="modal" (click)="clear()">
    <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="idle.proceedbutton">ok</span>
  </button>
</div>
