import { Pipe, PipeTransform } from '@angular/core';

@Pipe({ name: 'status' })
export class StatusPipe implements PipeTransform {
  transform(value: string): string {

    if (value) {
      switch (value) {
        case 'PENDING_APPROVAL': {
          return 'Pending Approval';
        }
        case 'APPROVED': {
          return 'Approved';
        }
        case 'REJECTED': {
          return 'Rejected';
        }
        case 'COMPLETED': {
          return 'Completed';
        }
        default: {
          break;
        }
      }
    }
    return '';
  }
}
