import { NgModule } from '@angular/core';
import { SharedLibsModule } from './shared-libs.module';
import { FindLanguageFromKeyPipe } from './language/find-language-from-key.pipe';
import { TranslateDirective } from './language/translate.directive';
import { AlertComponent } from './alert/alert.component';
import { AlertErrorComponent } from './alert/alert-error.component';
import { HasAnyAuthorityDirective } from './auth/has-any-authority.directive';
import { HasAnyResourceDirective } from './auth/has-any-resource.directive';
import { DurationPipe } from './date/duration.pipe';
import { FormatMediumDatetimePipe } from './date/format-medium-datetime.pipe';
import { FormatMediumDatePipe } from './date/format-medium-date.pipe';
import { SortByDirective } from './sort/sort-by.directive';
import { SortDirective } from './sort/sort.directive';
import { ItemCountComponent } from './pagination/item-count.component';
import { FileUploadComponent } from './component/file-upload/file-upload.component';
import { DefaultStringPipe } from './pipe/default-string.pipe';
import { TruncateNamePipe } from './pipe/truncate-name.pipe';
import { StatusPipe } from './status/status.pipe';
import { ParameterCapPipe } from './pipe/cap.pipe';
import { EditRemarkComponent } from 'app/shared/component/edit-remark/edit-remark.component';
import { RejectionReasonComponent } from 'app/shared/component/rejection-reason/rejection-reason.component';
import { DefaultNumberPipe } from 'app/shared/pipe/default-number.pipe';
import { FileUploadWrapperComponent } from './component/file-upload/file-upload-wrapper.component';
import { FileUpload2Component } from './component/file-upload/file-upload2.component';
import { FileUpload3Component } from './component/file-upload/file-upload3.component';
import { InputFieldTypeComponent } from './component/formly-input-field/input-field-type.component';
import { FormlyModule } from '@ngx-formly/core';
import { FormlyHorizontalWrapperComponent } from './component/formly-input-field/formly-horizontal-wrapper.component';
import { FormlyCalendarMonthlyComponent } from './component/formly-input-field/formly-calendar-monthly.component';
import { FormlyDatepickerModule } from '@ngx-formly/primeng/datepicker';
import { FormlyCalendarQuarterlyComponent } from './component/formly-input-field/formly-calendar-quarterly.component';
import { FormlyCalendarAnnuallyComponent } from './component/formly-input-field/formly-calendar-annually.component';
import { endDateMoreThanStartDateMsg, passwordMatchMsg, requiredValidationMsg } from './validator/formly-validator.directive';
import { ClaimSuppDocComponent } from 'app/claim/pop-up/view-supporting-doc/claim-supp-doc/claim-supp-doc.component';
import { FormlyCurrencyComponent } from './component/formly-input-field/formly-currency.component';
import { FormlyDropdownFormulaComponent } from './component/formly-input-field/formly-dropdown-formula.component';
import { FormlyDropdownAllowanceComponent } from './component/formly-input-field/formly-dropdown-allowance.component';
import { FormlyInputDoubleComponent } from './component/formly-input-field/formly-input-double.component';
import { FormlyDropdownClaimOptionsComponent } from './component/formly-input-field/formly-dropdown-claim-options.component';
import { FormlyCurrencyTypeComponent } from './component/formly-input-field/formly-currency-type.component';
import { FormlyDropdownOverlappingCaseIdComponent } from './component/formly-input-field/formly-dropdown-overlapping-caseId.component';
import { FormlyObjectComponent } from './component/formly-input-field/formly-object.component';
import { FormlyTableComponent } from './component/formly-input-field/formly-table.component';
import { FormlyMonthPickerComponent } from './component/formly-input-field/formly-month-picker.component';
import { FormlyYearPickerComponent } from './component/formly-input-field/formly-year-picker.component';
import { FormlyDatePickerNewComponent } from './component/formly-input-field/formly-date-picker-new.component';
import { FormlyDayPickerComponent } from './component/formly-input-field/fomrly-day-picker.component';
import { FormlyTextAreaNewComponent } from './component/formly-input-field/formly-textarea-new.component';

@NgModule({
  imports: [
    SharedLibsModule,
    FormlyDatepickerModule,
    FormlyModule.forRoot(
      {
        validators: [{
          name: 'fieldMatch', validation: passwordMatchMsg
        }, {
          name: 'endDateMoreThanStartDate', validation: endDateMoreThanStartDateMsg
        }],
        validationMessages: [
          { name: 'required', message: requiredValidationMsg },
        ],
        types: [
          {
            name: 'monthlyCal', component: FormlyCalendarMonthlyComponent, extends: 'datepicker'
            // defaultOptions: {
            //   templateOptions: {
            //     datepickerOptions: {},
            //   },
            // },
          },
          {
            name: 'quarterlyCal', component: FormlyCalendarQuarterlyComponent, extends: 'datepicker',
            defaultOptions: {
              templateOptions: {
              },
            },
            wrappers: ['form-field'],

          },
          {
            name: 'annuallyCal', component: FormlyCalendarAnnuallyComponent, extends: 'datepicker'
          },
          { name: 'input2', component: InputFieldTypeComponent },
          { name: 'object', component: FormlyObjectComponent },
          { name: 'table', component: FormlyTableComponent },
          {
            name: 'currencyTypeInput', component: FormlyCurrencyTypeComponent, extends: 'input'
          },
          {
            name: 'currencyInput', component: FormlyCurrencyComponent, extends: 'input'
          },
          {
            name: 'dropdownAllowance', component: FormlyDropdownAllowanceComponent, extends: 'select'
          },
          {
            name: 'dropdownClaimOption', component: FormlyDropdownClaimOptionsComponent, extends: 'select'
          },
          {
            name: 'dropdownFormula', component: FormlyDropdownFormulaComponent, extends: 'select'
          },
          {
            name: 'dropdownOverlappingCaseId', component: FormlyDropdownOverlappingCaseIdComponent, extends: 'select'
          },
          {
            name: 'doubleInput', component: FormlyInputDoubleComponent, extends: 'input'
          },
          {
            name: 'dayPicker', component: FormlyDayPickerComponent, extends: 'datepicker'
          },
          {
            name: 'datePickerNew', component: FormlyDatePickerNewComponent, extends: 'datepicker'
          },
          {
            name: 'monthPicker', component: FormlyMonthPickerComponent, extends: 'datepicker'
          },
          {
            name: 'yearPicker', component: FormlyYearPickerComponent, extends: 'datepicker'
          },
          {
            name: 'textAreaNew', component: FormlyTextAreaNewComponent, extends: 'textarea'
          },
          { name: 'input2', component: InputFieldTypeComponent },

        ]
      }
    ),
    //   wrappers: [{ name: 'form-field', component: FormlyHorizontalWrapperComponent }],
    //   types: [
    //     { name: 'input', component: InputFieldTypeComponent },
    //   ],
    // ),
  ],
  declarations: [
    FindLanguageFromKeyPipe,
    TranslateDirective,
    AlertComponent,
    AlertErrorComponent,
    HasAnyAuthorityDirective,
    HasAnyResourceDirective,
    DurationPipe,
    FormatMediumDatetimePipe,
    FormatMediumDatePipe,
    SortByDirective,
    SortDirective,
    ItemCountComponent,
    FileUploadComponent,
    FileUploadWrapperComponent,
    FileUpload2Component,
    FileUpload3Component,
    EditRemarkComponent,
    RejectionReasonComponent,
    DefaultStringPipe,
    TruncateNamePipe,
    StatusPipe,
    ParameterCapPipe,
    DefaultNumberPipe,
    InputFieldTypeComponent,
    FormlyCalendarMonthlyComponent,
    FormlyCalendarQuarterlyComponent,
    FormlyCalendarAnnuallyComponent,
    FormlyHorizontalWrapperComponent,
    FormlyCurrencyTypeComponent,
    FormlyCurrencyComponent,
    FormlyDropdownAllowanceComponent,
    FormlyDropdownClaimOptionsComponent,
    FormlyDropdownFormulaComponent,
    FormlyDropdownOverlappingCaseIdComponent,
    FormlyInputDoubleComponent,
    FormlyDayPickerComponent,
    FormlyDatePickerNewComponent,
    FormlyMonthPickerComponent,
    FormlyYearPickerComponent,
    FormlyTextAreaNewComponent,
    ClaimSuppDocComponent,
    FormlyObjectComponent,
    FormlyTableComponent
  ],
  exports: [
    SharedLibsModule,
    FindLanguageFromKeyPipe,
    TranslateDirective,
    AlertComponent,
    AlertErrorComponent,
    HasAnyAuthorityDirective,
    HasAnyResourceDirective,
    DurationPipe,
    FormatMediumDatetimePipe,
    FormatMediumDatePipe,
    SortByDirective,
    SortDirective,
    ItemCountComponent,
    FileUploadComponent,
    FileUploadWrapperComponent,
    FileUpload2Component,
    FileUpload3Component,
    EditRemarkComponent,
    RejectionReasonComponent,
    DefaultStringPipe,
    TruncateNamePipe,
    StatusPipe,
    ParameterCapPipe,
    DefaultNumberPipe,
    FormlyModule,
    FormlyCalendarMonthlyComponent,
    FormlyCalendarQuarterlyComponent,
    FormlyCalendarAnnuallyComponent,
    FormlyCurrencyTypeComponent,
    FormlyCurrencyComponent,
    FormlyDropdownAllowanceComponent,
    FormlyDropdownClaimOptionsComponent,
    FormlyDropdownFormulaComponent,
    FormlyDropdownOverlappingCaseIdComponent,
    FormlyInputDoubleComponent,
    FormlyDayPickerComponent,
    FormlyDatePickerNewComponent,
    FormlyMonthPickerComponent,
    FormlyYearPickerComponent,
    FormlyTextAreaNewComponent,
    ClaimSuppDocComponent
  ]
})
export class SharedModule { }
