import { Component } from '@angular/core';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParamService } from './param.service';
import { ParamModel } from './param.model';

@Component({
  selector: 'ic-param-delete-dialog',
  templateUrl: './param-delete-dialog.component.html',
})
export class ParamDeleteDialogComponent {
  param?: ParamModel;

  constructor(protected paramService: ParamService, public activeModal: NgbActiveModal, protected eventManager: EventManager) {}

  cancel(): void {
    this.activeModal.dismiss();
  }

  confirmDelete(appId: string, paramKey: string): void {
    this.paramService.delete(appId, paramKey).subscribe(() => {
      this.eventManager.broadcast('paramListModification');
      this.activeModal.close();
    });
  }
}
<form name="deleteForm">
  <div class="modal-header">
    <h4 class="modal-title" icTranslate="paramAdmin.param.maintain.deleteConfirm">Confirm delete operation</h4>
    <button type="button" class="close" data-bs-dismiss="modal" aria-hidden="true" (click)="cancel()">&times;</button>
  </div>

  <div class="modal-body">
    <ic-alert-error></ic-alert-error>
    <p icTranslate="paramAdmin.param.maintain.deleteConfirmQuestion" [translateValues]="{ id: param?.paramKey }">
      Are you sure to delete the parameter?
    </p>
  </div>

  <div class="modal-footer">
    <button type="button" class="btn btn-danger btn-sm" (click)="confirmDelete(param?.appId!, param?.paramKey!)">
      <fa-icon [icon]="'times'"></fa-icon>&nbsp;<span icTranslate="paramAdmin.param.btn.delete">Delete</span>
    </button>

    <button type="button" class="btn btn-secondary btn-sm" data-bs-dismiss="modal" (click)="cancel()">
      <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="paramAdmin.param.btn.cancel">Cancel</span>
    </button>
  </div>
</form>
