import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, combineLatest } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { Subject } from './user.model';
import { UserService } from './user.service';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { AccountService } from 'app/core/auth/account.service';

@Component({
  selector: 'ic-user',
  templateUrl: './user.component.html',
})
export class UserComponent implements OnInit, OnDestroy {
  currentAccount: any;
  users?: Subject[];
  cols: any[];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;
  searchCriteria: any;
  statuses: any[];
  loginTypes: any[];

  constructor(
    private userService: UserService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private accountService: AccountService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager
  ) {
    this.searchCriteria = {
      firstName: '',
      nric: '',
      userInit: '',
      email: '',
      status: '',
      effectiveDt: '',
      expiryDt: '',
      loginMechanism: '',
      loginName: '',
    };
    this.statuses = [
      { codeId: 'A', codeDesc: 'Active' },
      { codeId: 'S', codeDesc: 'Suspended' },
      { codeId: 'I', codeDesc: 'Inactive' },
      { codeId: 'R', codeDesc: 'Revoked' },
      { codeId: 'P', codeDesc: 'Pending' },
      { codeId: 'D', codeDesc: 'Deactived' },
    ];
    this.loginTypes = [
      { codeId: 'PASSWORD', codeDesc: 'Password Login' },
      { codeId: 'SINGPASS', codeDesc: 'Singpass Login' },
      { codeId: 'SINGPASS_EASY', codeDesc: 'Singpass With Easy Login' },
      { codeId: 'NETRUST', codeDesc: 'Netrust Login' },
      { codeId: 'KERBEROS', codeDesc: 'Kerberos Login' },
      { codeId: 'CONTAINER', codeDesc: 'Container Login' },
      { codeId: 'LDAP', codeDesc: 'LDAP Login' },
    ];
    this.cols = [
      { field: 'firstName', header: 'Name' },
      { field: 'nric', header: 'NRIC' },
      { field: 'salutation', header: 'Salutation' },
      { field: 'displayName', header: 'Display Name' },
      { field: 'userInit', header: 'User Initial' },
      { field: 'email', header: 'Email Address' },
      { field: 'appt', header: 'Appointment' },
      { field: 'unit', header: 'Unit' },
      { field: 'status', header: 'status' },
      { field: 'updatedBy', header: 'Updated By' },
      { field: 'updatedDt', header: 'Updated Date' },
      // { field: 'loginMechanism', header: 'loginType' },
      // { field: 'loginName', header: 'Login ID' },
    ];
  }

  loadAll(): void {
    this.userService
      .query({
        firstName: this.searchCriteria.firstName,
        nric: this.searchCriteria.nric,
        userInit: this.searchCriteria.userInit,
        email: this.searchCriteria.email,
        status: this.searchCriteria.status,
        effectiveDt: this.searchCriteria.effectiveDt,
        expiryDt: this.searchCriteria.expiryDt,
        loginMechanism: this.searchCriteria.loginMechanism,
        loginName: this.searchCriteria.loginName,
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }

  transition(): void {
    this.router.navigate(['/admin/acm/ic-user'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  search(): void {
    this.links = {
      last: 0,
    };
    this.page = 0;
    this.predicate = 'subjectId';
    this.reverse = false;
    this.loadAll();
  }

  clear(): void {
    this.searchCriteria = {
      firstName: '',
      nric: '',
      userInit: '',
      email: '',
      status: '',
      effectiveDt: '',
      expiryDt: '',
      loginMechanism: '',
      loginName: '',
    };
    this.page = 0;
    this.router.navigate([
      '/admin/acm/ic-user',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    ]);
  }

  ngOnInit(): void {
    this.accountService.identity().subscribe(account => (this.currentAccount = account));
    this.handleNavigation();
    this.registerChangeInUsers();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: Subject): any {
    return item.subjectId;
  }

  registerChangeInUsers(): void {
    this.eventSubscriber = this.eventManager.subscribe('userListModification', () => this.loadAll());
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
    if (this.predicate !== 'subjectId') {
      result.push('subjectId');
    }
    return result;
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    data.forEach((element: { loginName: any; subjectLogins: { loginMechanism: any; loginName: any }[]; loginMechanism: any }) => {
      element.loginName = element.subjectLogins[0].loginName;
      element.loginMechanism = element.subjectLogins[0].loginMechanism;
    });
    this.users = data;
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div>
  <h2>
    <span icTranslate="acmAdmin.user.home.title">Users</span>
  </h2>
  <ic-alert></ic-alert>
  <br />
  <div class="row">
    <form name="searchForm" class="col-sm-12">
      <div class="card">
        <div class="card-header"><span icTranslate="acmAdmin.home.SearchCriteria">Search Criteria</span></div>
        <div class="card-block" style="padding: 1.5%">
          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Name</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="firstName" id="firstName" [(ngModel)]="searchCriteria.firstName" />
            </div>


            <label class="col-sm-2 form-control-label">NRIC</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="nric" id="nric" [(ngModel)]="searchCriteria.nric" />
            </div>
          </div>

          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">User Initial</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="userInit" id="userInit" [(ngModel)]="searchCriteria.userInit" />
            </div>

            <label class="col-sm-2 form-control-label">Email Address</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="email" id="email" [(ngModel)]="searchCriteria.email" />
            </div>
          </div>

          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Status</label>
            <div class="col-sm-4">
              <select class="form-control" name="status" [(ngModel)]="searchCriteria.status">
                <option [ngValue]="null"></option>
                <option
                  [ngValue]="status.codeId === searchCriteria.status ? searchCriteria.status : status.codeId"
                  *ngFor="let status of statuses"
                >
                  {{ status.codeDesc }}
                </option>
              </select>
            </div>
          </div>

          <div style="margin-top: 1.5%">
            <button id="seachUserButton" type="button" class="btn btn-warning btn-sm" (click)="search()">
              <fa-icon [icon]="'search'"></fa-icon>
              <span class="fa fa-search" icTranslate="acmAdmin.home.search"></span>
            </button>

            <button type="button" class="btn btn-secondary btn-sm" (click)="clear()">
              <fa-icon [icon]="'trash-alt'"></fa-icon>
              <span class="fa fa-trash-o" icTranslate="acmAdmin.home.reset"></span>
            </button>

            <button id="createnewuser" class="btn btn-primary btn-sm" [routerLink]="['../ic-user-new']">
              <fa-icon [icon]="'plus'"></fa-icon>
              <span icTranslate="acmAdmin.user.home.createLabel"> Create new User </span>
            </button>
          </div>
        </div>
      </div>
    </form>
  </div>
  <br />
  <div class="card">
    <div class="card-header"><span icTranslate="acmAdmin.home.SearchResult">Search Criteria</span></div>
    <div class="card-block" style="padding: 1.5% 1.5% 0 1.5%">
      <div class="table-responsive" *ngIf="users">
        <p-table [value]="users" [columns]="cols" [reorderableColumns]="true" [resizableColumns]="true">
          <ng-template pTemplate="header" let-columns>
            <tr>
              <th scope="col" style="width: 3rem"></th>
              <th scope="col" pResizableColumn>S/No.</th>
              <th scope="col" *ngFor="let col of columns" pReorderableColumn pResizableColumn pSortableColumn="{{ col.field }}">
                <p-sortIcon field="{{ col.field }}"></p-sortIcon>
                {{ col.header }}
              </th>
              <th scope="col" pResizableColumn></th>
            </tr>
          </ng-template>

          <ng-template pTemplate="body" let-rowData let-user let-columns="columns" let-index="rowIndex">
            <tr [pReorderableRow]="index">
              <td>
                <span class="pi pi-bars" pReorderableRowHandle></span>
              </td>
              <td>{{ index + 1 }}</td>
              <td *ngFor="let col of columns">
                {{ rowData[col.field] }}
              </td>
              <td class="text-end">
                <div class="btn-group flex-btn-group-container">
                  <button type="submit" [routerLink]="['../../../admin/acm/ic-user', user.subjectId]" class="btn btn-primary btn-sm">
                    <fa-icon [icon]="'pencil-alt'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.edit">Edit</span>
                  </button>
                </div>
              </td>
            </tr>
          </ng-template>
        </p-table>
      </div>
      <div *ngIf="users && users.length">
        <div class="row justify-content-center">
          <ic-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></ic-item-count>
        </div>
        <div class="row justify-content-center">
          <ngb-pagination
            [collectionSize]="totalItems"
            [pageSize]="itemsPerPage"
            [(page)]="page"
            (pageChange)="loadPage(page)"
          ></ngb-pagination>
        </div>
      </div>
    </div>
  </div>
</div>
