import { Component } from '@angular/core';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { EventManager } from 'app/core/util/event-manager.service';
import { Resource } from '../resource/resource.model';
import { FunctionService } from './function.service';

@Component({
  selector: 'ic-function-delete-dialog',
  templateUrl: './function-delete-dialog.component.html',
})
export class FunctionDeleteDialogComponent {
  func?: Resource;

  constructor(protected functionService: FunctionService, public activeModal: NgbActiveModal, protected eventManager: EventManager) {}

  cancel(): void {
    this.activeModal.dismiss();
  }

  confirmDelete(id: any): void {
    this.functionService.delete(id).subscribe(() => {
      this.eventManager.broadcast('functionListModification');
      this.activeModal.close();
    });
  }
}
<form name="deleteForm" (ngSubmit)="confirmDelete(func?.resourceId!)">
  <div class="modal-header">
    <h4 class="modal-title" icTranslate="entity.delete.title">Confirm delete operation</h4>
    <button type="button" class="close" data-bs-dismiss="modal" aria-hidden="true" (click)="cancel()">&times;</button>
  </div>

  <div class="modal-body">
    <ic-alert-error></ic-alert-error>
    <p icTranslate="acmAdmin.function.delete.question" [translateValues]="{ id: func?.resourceName }">
      Are you sure you want to delete this Function?
    </p>
  </div>

  <div class="modal-footer">
    <button id="deleteFunction" type="submit" class="btn btn-danger btn-sm">
      <fa-icon [icon]="'times'"></fa-icon>&nbsp;<span icTranslate="entity.action.delete">Delete</span></button
    >&nbsp;
    <button type="button" class="btn btn-secondary btn-sm" data-bs-dismiss="modal" (click)="cancel()">
      <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
    </button>
  </div>
</form>
