  updateFormulaOptions(claimType, claimBasis) {
    const startPayPeriod = this.createEcForm?.get('startPayPeriod').value;
    const restDayList = this.createEcForm?.get('restDay').value;
    let params = new http_1.HttpParams();
    params = params.append('claimType', claimType);
    params = params.append('claimBasisCode', claimBasis);
    if (startPayPeriod) {
      params = params.append('payPeriodStartInd', 1);
    }
    if (restDayList && restDayList.length > 0) {
      params = params.append('restDayInd', 1);
      if (restDayList.some(day => (0, moment_1.default)(day).format('ddd').toUpperCase() === 'SAT')) {
        params = params.append('isSatInd', 1);
      }
    }
    const options = [];
    // this.claimFormulaOption = [];
    this.claimService.retrieveFormulaList({
      params
    }).subscribe(res => {
      if (res.body.data) {
        const sortedData = res.body.data.sort(this.claimService.sortFormulaListByCode());
        sortedData.forEach(x => {
          options.push({
            label: String(x.formulaCode) + ' ' + String(x.formulaDesc),
            value: x.formulaCode
          });
        });
        this.claimFormulaOption = options;
      }
    });
  }
  addFormula(formulaCode) {
    const restDay = this.createEcForm.get('restDay').value;
    const startPayPeriod = this.createEcForm.get('startPayPeriod').value;
    const code = String(formulaCode);
    if (code === '16A' || code === '16B' || code === '16C') {
      if (startPayPeriod) {
        return true;
      } else {
        return false;
      }
    }
    if (code === '16D') {
      console.log('REST DAY', restDay);
      if (restDay && restDay.length > 0 && startPayPeriod) {
        return true;
      } else {
        return false;
      }
    }
    if (code === '8A ' || code === '10A' || code === '8B ' || code === '10B') {
      if (restDay && restDay.length > 0) {
        const isSaturday = restDay.some(day => (0, moment_1.default)(day).format('ddd').toUpperCase() === 'SAT');
        console.log('IS REST DAY ON SATURDAY ?', isSaturday);
        if (isSaturday) {
          return true;
        } else {
          return false;
        }
      } else {
        return false;
      }
    } else {
      return true;
    }
  }
