import { Component, OnInit, ViewChild } from '@angular/core';
import { FormGroup, FormBuilder, Validators, FormControl, AbstractControl} from '@angular/forms';
import { ECClaimService } from '../service/ec-claim.service';
import { EmpClaimRequest, SaveEmpClaimRequest } from '../model/EmpClaimRequest.model';
import { Claim, ClaimItem, ClaimSuppDoc } from "../../model/claim.model";
import { EmpClaim, EmpClaimHardcopy, EmpClaimHardcopyItem } from '../model/EmpClaim.model';
import moment from 'moment';
import { DateValidator } from 'app/shared/validator/date-validator.directive';
import { CommonValidator } from 'app/shared/validator/common-validator.directive';
import { FileUploadComponent } from 'app/shared/component/file-upload/file-upload.component';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';
import { ClaimService } from '../../service/claim.service';
import { FileUploadService } from 'app/shared/service/file-upload.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { ActivatedRoute, Router } from '@angular/router';
import { zip } from 'rxjs';
import { HttpParams, HttpResponse } from '@angular/common/http';
import { ClaimSuppDocComponent } from 'app/claim/pop-up/view-supporting-doc/claim-supp-doc/claim-supp-doc.component';
import claim from '../../../../i18n/en/claim.json';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { NSPAppConstants } from 'app/shared/constants/nspapp.constants';
import { PublicHolidayService } from 'app/administration/black-out-date/public-holiday/service/public-holiday.service';

@Component({
    selector: 'n2-edit-hardcopy-ec',
    templateUrl: './edit-hardcopy-ec.component.html',
    styleUrls: ['./edit-hardcopy-ec.component.scss'],
})
export class EditHardcopyEcComponent implements OnInit {
    // form control status validation check
    readonly INVALID_STATUS: string = 'INVALID'; 
    readonly INVALID_DATE: string = 'Invalid date';
    readonly SUBMIT_ACTION: string = 'submit';
    readonly SAVE_ACTION: string = 'save';

    // allowable extension for support document
    readonly ALLOWABLE_EXTENSION: string = ".jpg,.png,.gif,.pdf,.csv";

    // mock data (ns man info)
    readonly NS_TYPE: string = 'NSman';
    readonly PERIOD_1: string = '13-07-2021 (0830) to 15-07-2021 (1825)';
    readonly PERIOD_2: string = '04-08-2021 (0830) to 09-08-2021 (1825)';
    readonly PERIOD_3: string = '10-08-2021 (0830) to 15-08-2021 (1825)';
    readonly PERIOD_4: string = '17-08-2021 (0830) to 20-08-2021 (1825)';
    readonly PERIOD_5: string = '01-09-2021 (0830) to 13-09-2021 (1825)';

    overlappingClaims: any[] = [];
    claimBasisOption: any[] = [];
    // readonly claimBasisOption = [{label: 'Per Day', value: 'PD'}, {label: 'Per Month', value: 'PM'}, {label: 'Per ICT', value: 'ICT'}];
    // mock var
    nsType: string;
    nsInfo1: string;
    nsInfo2: string;
    nsInfo4: string;

    // rest day logic
    readonly restDayFormulaLogic = ['8a ', '8b ', '10a', '10b']; // auto populate alternate sat
    minRestDate: Date = new Date();
    maxRestDate: Date = new Date();
    minMaxRestDate: any[]= [];
    altSaturdayList: Date[]=[];

    // ngModel
    nsmanCab: string;
    isCurrentClaimOldPolicy = false;
    claimJson: any;
    nsmanDraftCount = 0;
    header: string;
    coyCSN: string;
    coyDirectInd: string;
    trgPeriodStartDay: string; // indicate day of the week
    trgPeriodEndDay: string; // indicate day of the week
    workHourStartTime = ''; // format time HH:MM AM/PM
    workHourEndTime = ''; // format time HH:MM AM/PM
    coyAddressEdited = false;
    companyAddress: string;
    indexTimes = 0;
    viewSuppDocParams: any;
    selectAllDocument = false;
    supportDocList: any[] = [];
    selectedDocument: any[] = [];
    selectedItems: string[] = [];
    selectedOtherItems: string[] = [];
    selectedCertOfficer: string;
    selectedSalutation: string;
    selectedFormula: string;
    viewAllModel = false;
    showAddressModel = false;
    showUploadDocModel = false;
    showRemarksModel = false;
    showReturnModel = false;
    halfdayInd = true;
    isDummyCoy: boolean;
    isInvalidAddress = false;
    isERegisteredCoy: string;    
    isNewCO = false;
    hardcopyDto: EmpClaimRequest;
    empClaimDto: EmpClaim;
    claimDto: Claim;
    subject: any;
    claimFormulaOption: any[] = [];
    displaySuccessResp: any = {display: false};
    routeOutMsg = "";
    routeOutCaseId: string;
    overlappingErrorMsg = '';
