    constructor(
        private formBuilder: FormBuilder,
        private ecClaimService: ECClaimService,
        private claimService: ClaimService,
        private commonClaimService: ClaimService,
        private fileUploadService: FileUploadService,
        private toastService: CustomToastService,
        private nspConfirmationService: NspConfirmationService,
        private route: ActivatedRoute,
        private router: Router,
        private publicHolidayService: PublicHolidayService,
        public dateUtilsService: DateUtilsService,
    ) {
        this.claimJson = claim;
    }

    ngOnInit(): void {
        console.log('this.route', this.route);
        const routeParams = this.route.snapshot.params;
        if (routeParams?.caseId) {
            this.loadClaimByCaseId(routeParams.caseId);
        } else if (this.hardcopyDto && this.hardcopyDto?.claimDto?.caseId) {
            this.loadClaimByCaseId(this.hardcopyDto?.claimDto?.caseId);
        } else {
            this.loadNewClaim();
        }        
    }

    loadNewClaim() {
        console.log('>>>> NEW CLAIM');
        this.hardcopyDto = this.ecClaimService.hardcopyEcDto;
        this.hardcopyDto.saveEmpClaimRequestDto = new SaveEmpClaimRequest();
        this.empClaimDto = this.hardcopyDto.empClaimDto;
        this.claimDto = this.hardcopyDto.claimDto;

        this.claimService.getUserLoginDetails().subscribe((subjectRes: any) => {
            if (subjectRes) {
                this.subject = subjectRes.body.data;
            }
        });
        this.ecClaimService.getCodes('claim_basis').subscribe((codeTypeRes: HttpResponse<any>) => {
            if (codeTypeRes) {
                this.claimBasisOption = codeTypeRes.body[0]?.map((ct: any) => {
                    return {
                        label: ct.label['en-US'],
                        value: ct.codeId
                    }
                });
            }
        });
        this.ecClaimService.getCabBlockByNric(this.claimDto.nric).subscribe((cabBlockRes: any) => {
            // returns a LIST
            if (cabBlockRes.body.data && cabBlockRes.body.data.length > 0) {
                this.nsmanCab = 'Y';
            } else {
                this.nsmanCab = 'N';
            }
        });
        
        // set default ngModel
        this.workHourStartTime = '';
        this.workHourEndTime = '';
        this.trgPeriodEndDay = this.trgPeriodStartDay = 'Day';
        this.isERegisteredCoy = this.hardcopyDto.coyType;
        this.coyDirectInd = this.hardcopyDto.coyDirectInd;
        this.coyCSN = String(this.hardcopyDto.csn1) + '-' + String(this.hardcopyDto.csn2) + '-' + String(this.hardcopyDto.csn3);
        this.isDummyCoy = this.hardcopyDto.isDummyCoy;
        this.companyAddress = (this.isDummyCoy === true) ? '-' : this.hardcopyDto.coyAddress;
        this.header = 'New EC '.concat(this.hardcopyDto?.claimDto?.nric);

        if (this.isDummyCoy === false) {
            if (this.isERegisteredCoy === 'Y') {
                const submittedBy = { label: 'New Certification Officer', value: 'NCO' };
                this.submittedByOption.push(submittedBy);
            }
            this.selectedCertOfficer = 'CO1';
            this.certOfficerOnChanges();
        }

        this.loadItemStatusDropdown();
        this.updateFormulaOptions('E', 'PM ');
        this.initialiseFormGroup();
        this.changeFormGroupValidation();
        this.initialiseAddressFormGroup();
        this.setMockData();
        this.retrieveNsmanDraftCount();
    }

    loadClaimByCaseId(caseId: string) {
        console.log('>>>> GET CLAIM BY CASE ID');
        let empClaimRequestParams = new HttpParams();
        empClaimRequestParams = empClaimRequestParams.append("caseId", caseId);
        empClaimRequestParams = empClaimRequestParams.append("role", 'role-pv');
        
        const getUserLoginDetails = this.claimService.getUserLoginDetails();
        const getHardcopyEmpClaimByCaseId = this.ecClaimService.getEmpClaimRequestByCaseId(empClaimRequestParams);
        const getCodeType = this.ecClaimService.getCodes('claim_basis');
        zip([getUserLoginDetails, 
            getHardcopyEmpClaimByCaseId,
            getCodeType]).subscribe(([subjectRes, hardcopyRes, codeTypeRes]) => {
            if (hardcopyRes) {
                this.hardcopyDto = hardcopyRes.body.data;
                this.hardcopyDto.saveEmpClaimRequestDto = new SaveEmpClaimRequest();
                this.empClaimDto = this.hardcopyDto.empClaimDto;
                this.claimDto = this.hardcopyDto.claimDto;
                this.header = this.hardcopyDto?.claimDto?.caseId;
                this.coyCSN = this.formatCoyCsn(this.empClaimDto?.csn);
                this.isDummyCoy = this.checkDummyCoy(this.empClaimDto?.csn);
                this.isERegisteredCoy = this.hardcopyDto?.coyType;
                this.nsmanCab = this.hardcopyDto?.cab;

                if (this.claimDto?.hrWorkStart){
                    const appendStartTime = this.addLeadingZero(this.claimDto?.hrWorkStart, 4);
                    this.workHourStartTime = this.formatTime(appendStartTime, 'hh:mm A');
                }
                if (this.claimDto?.hrWorkEnd){
                    const appendEndTime = this.addLeadingZero(this.claimDto?.hrWorkEnd, 4);
                    this.workHourEndTime = this.formatTime(appendEndTime, 'hh:mm A');
                }
                this.trgPeriodEndDay = moment(new Date(this.claimDto?.trgEndDate)).format('ddd');
                this.trgPeriodStartDay = moment(new Date(this.claimDto?.trgStartDate)).format('ddd');

                if (this.isERegisteredCoy === 'Y' && (!this.submittedByOption.some(option => option.value === 'NCO'))) {
                    const submittedBy = { label: 'New Certification Officer', value: 'NCO' };
                    this.submittedByOption.push(submittedBy);
                }

                const startPayPeriod = this.empClaimDto?.startPayPeriod;
                const restDayList = this.hardcopyDto?.restDay;
                let params = new HttpParams();
                params = params.append('claimType', 'E');
                params = params.append('claimBasisCode', this.hardcopyDto?.empClaimHardcopyDto?.basis);
                if (startPayPeriod) {
                    params = params.append('payPeriodStartInd', 1);
                }
                if (restDayList && restDayList.length > 0) {
                    params = params.append('restDayInd', 1);
                    if (restDayList.some((day: any) => moment(day, 'YYYY-MM-DD').format('ddd').toUpperCase() === 'SAT')) {
                        params = params.append('isSatInd', 1);
                    }
                }
                this.claimFormulaOption = [];
                this.claimService.retrieveFormulaList({params}).subscribe((res: any) => {
                    if (res.body.data) {
                        const sortedData = res.body.data.sort(this.claimService.sortFormulaListByCode());
                        sortedData.forEach((x:any)=>{
                            this.claimFormulaOption.push({
                            label: String(x.formulaCode) + ' ' + String(x.formulaDesc),
                            value: x.formulaCode
                            })
                        });
                        this.loadFormGroup(this.hardcopyDto);
                    }
                });
                
                this.setMockData();
                this.loadSupportingDocument(hardcopyRes.body.data);
                this.loadCompanyAddress();
                this.retrieveNsmanDraftCount();

                this.viewSuppDocParams = {
                    nric: this.claimDto?.nric,
                    name: this.hardcopyDto?.nsManName,
                    caseId: this.claimDto?.caseId,
                    claimId: this.claimDto?.id
                }

                if (this.hardcopyDto?.empClaimHardcopyDto?.status !== 'PCA') {
                    this.initialiseRemarksFormGroup();
                }
            }
            if (subjectRes) {
                this.subject = subjectRes.body.data;
            }
            if (codeTypeRes) {
                this.claimBasisOption = codeTypeRes.body[0]?.map((ct: any) => {
                    return {
                        label: ct.label['en-US'],
                        value: ct.codeId
                    }
                });
            }

            this.loadItemStatusDropdown();
        });
        
    }

    checkHalfDayIndicator() {
        this.halfdayInd = (this.hardcopyDto?.empClaimDto?.halfDayInd === 'Y') ? true : false;
    }

    // CSN = Unique Entity Identifier(UEN) + Payment Codes
    initialiseFormGroup() {
        this.checkHalfDayIndicator();
        this.selectedFormula = '2a ';
        this.minRestDate = moment(this.claimDto.trgStartDate, 'YYYY-MM-DD').startOf('month').toDate();
        this.maxRestDate = moment(this.claimDto.trgEndDate, 'YYYY-MM-DD').endOf('month').toDate();

        const isSameTrgDay =  moment(this.claimDto.trgStartDate, 'YYYY-MM-DD') === moment(this.claimDto.trgEndDate, 'YYYY-MM-DD') ? true : false;

        this.createEcForm = this.formBuilder.group({
            trgPeriodStartDt: [moment(this.claimDto.trgStartDate, 'YYYY-MM-DD').toDate(), [Validators.required]],
            trgPeriodEndDt: [moment(this.claimDto.trgEndDate, 'YYYY-MM-DD').toDate(), [Validators.required]],
            // trgPeriodEndDt: [moment(this.claimDto.trgEndDate, 'YYYY-MM-DD').toDate(), [Validators.required, DateValidator.isBeforeStartDate('trgPeriodStartDt', 'trgPeriodEndDt')]],
            halfdayInd: [this.halfdayInd],
            workHourStart: [ isSameTrgDay ? '0001' : null, [CommonValidator.numbersOnlyValidator()]],
            workHourEnd: [ isSameTrgDay ? '2359' : null, [CommonValidator.numbersOnlyValidator()]],
            claimBasis: ['PM ', [Validators.required]], // default
            formulaCode: ['2a ', [Validators.required]], // default
            startPayPeriod: [null, [CommonValidator.numbersOnlyValidator(), CommonValidator.numberRangeValidator(2, 31)]], // required if claim formula is type-16
            restDay: [[]], // required if formula 8a, 8b, 10a, 10b, 15a, 16d
            status: ['PCA', [Validators.required]],
            items: [''],
            otherItems: [''],
            supportingDocuments: [[], [FileUploadValidator2.allowableExtension(this.ALLOWABLE_EXTENSION)]],
            companyName: [this.hardcopyDto.coyName],
            submittedBy: [this.isDummyCoy === false ? 'CO1' : null],
            submittedBySalutation: [null],
            submittedByName: [''],
            submittedByAppt: [''],
            submittedByEmail: ['', [Validators.email, Validators.maxLength(80)]], // db accepts up to 80 characters
            submittedByPhone1: ['', [Validators.minLength(8)]],
            submittedByPhone2: ['']
        }, {validators: [
            DateValidator.dateLessThan('trgPeriodStartDt', 'trgPeriodEndDt'),
            DateValidator.dateRangeMoreThan('trgPeriodStartDt', 'trgPeriodEndDt', 60),
            CommonValidator.mustNotEqualValidator('workHourStart', 'workHourEnd')
          ]
        });

        let params = new HttpParams();
        params = params.append('nric', this.claimDto.nric);
        params = params.append('claimStartDt', this.claimDto.trgStartDate);
        params = params.append('claimEndDt', this.claimDto.trgEndDate);
        this.getOverlappingClaims(params);
        this.checkFormValueChanges();
    }

    loadFormGroup(data: any) {
        const suppDocRes = data.claimSuppDocList.map((sp:any): any => {
            return {
                name: sp.fileName
            };
        });
        const hardcopyItemList = this.hardcopyDto?.empClaimHardcopyDto?.empClaimHardcopyItemList;
        const claimItems: string[] = [];
        const otherItems: string[] = [];
        hardcopyItemList.forEach(item => {
            if (item?.itemType === 'ORD' || item?.itemType === 'ADD') {
                if (!claimItems.includes('OAW')) {
                    claimItems.push('OAW');
                }
            } else if (item?.itemType === 'ECO') {
                const otherItemName = item?.itemName.replace('OTHERS-', '');
                otherItems.push(otherItemName);
            } else if (item?.itemType === 'ONB') {
                claimItems.push(item?.itemName);
            } else {
                claimItems.push(item?.itemType);
            }
        });
        this.selectedItems = claimItems;
        // this.selectedOtherItems = otherItems;

        this.minRestDate = moment(new Date(this.claimDto?.trgStartDate)).startOf('month').toDate();
        this.maxRestDate = moment(new Date(this.claimDto?.trgEndDate)).endOf('month').toDate();
        this.selectedCertOfficer = this.empClaimDto?.certRole;
        this.selectedSalutation = this.empClaimDto?.certSalutation;
        this.selectedFormula = data?.empClaimHardcopyDto?.formulaCode;

        this.checkHalfDayIndicator();

        const restDayList = data?.restDay.map((date: any) => {return new Date(date)});
        this.createEcForm = this.formBuilder.group({
            trgPeriodStartDt: [new Date(this.claimDto?.trgStartDate), [Validators.required]],
            trgPeriodEndDt: [new Date(this.claimDto?.trgEndDate), [Validators.required]],
            // trgPeriodEndDt: [new Date(this.claimDto?.trgEndDate), [Validators.required, DateValidator.isBeforeStartDate('trgPeriodStartDt', 'trgPeriodEndDt')]],
            halfdayInd: [this.halfdayInd],
            workHourStart: [this.claimDto?.hrWorkStart, [CommonValidator.numbersOnlyValidator()]],
            workHourEnd: [this.claimDto?.hrWorkEnd, [CommonValidator.numbersOnlyValidator()]],
            claimBasis: [data?.empClaimHardcopyDto?.basis, [Validators.required]],
            formulaCode: [data?.empClaimHardcopyDto?.formulaCode, [Validators.required]],
            startPayPeriod: [this.empClaimDto?.startPayPeriod, [CommonValidator.numbersOnlyValidator(), CommonValidator.numberRangeValidator(2, 31)]], // required if claim formula is type-16
            restDay: [restDayList], // required if formula 8a, 8b, 10a, 10b, 15a, 16d
            status: [data?.empClaimHardcopyDto?.status, [Validators.required]],
            items: [claimItems],
            otherItems: [otherItems],
            supportingDocuments: [suppDocRes, [FileUploadValidator2.allowableExtension(this.ALLOWABLE_EXTENSION)]],
            companyName: [data.coyName],
            submittedBy: [this.empClaimDto?.certRole],
            submittedBySalutation: [this.empClaimDto?.certSalutation],
            submittedByName: [this.empClaimDto?.certName],
            submittedByAppt: [this.empClaimDto?.certAppt],
            submittedByEmail: [this.empClaimDto?.certEmail, [Validators.email]], // db accepts up to 80 characters
            submittedByPhone1: [this.empClaimDto?.certContactNo, [Validators.minLength(8)]],
            submittedByPhone2: [this.empClaimDto?.certContactExt]
        }, {validators: [
            DateValidator.dateLessThan('trgPeriodStartDt', 'trgPeriodEndDt'),
            DateValidator.dateRangeMoreThan('trgPeriodStartDt', 'trgPeriodEndDt', 60),
            CommonValidator.mustNotEqualValidator('workHourStart', 'workHourEnd')
          ]
        });

        if (!this.coMap.has(this.selectedCertOfficer)) {
            const coDetail = {
                certRole: this.empClaimDto?.certRole,
                certSalutation: this.empClaimDto?.certSalutation,
                certName: this.empClaimDto?.certName,
                certAppt: this.empClaimDto?.certAppt,
                certEmail: this.empClaimDto?.certEmail,
                certContactNo: this.empClaimDto?.certContactNo,
                certContactExt: this.empClaimDto?.certContactExt
            }
            this.coMap.set(this.selectedCertOfficer, coDetail);
        }

        this.populateRestDayAltSat();
        this.changeFormGroupValidation();
        this.checkFormValueChanges();
    }

    loadSupportingDocument(data: any) {
        data?.claimSuppDocList.forEach((doc: any) => {
            const claimSuppDoc = new ClaimSuppDoc();
            claimSuppDoc.fileName = doc.fileName;
            claimSuppDoc.indexDt = doc.indexDt;
            claimSuppDoc.indexBy = doc?.indexBy; 
            claimSuppDoc.indexByInit = doc?.indexByInit;
            claimSuppDoc.docId = doc.docId;
            claimSuppDoc.eclaimInd = doc?.eclaimInd;
            this.supportDocList.push(claimSuppDoc); 
        });
    }

    loadCompanyAddress() {
        if (this.empClaimDto?.blkNo || this.empClaimDto?.street || this.empClaimDto?.unitNo
            || this.empClaimDto?.building || this.empClaimDto?.postalCode) {
                let fullAddress = '';
                fullAddress += this.empClaimDto?.blkNo ? 'Blk ' + String(this.empClaimDto.blkNo) + ' ' : '';
                fullAddress += this.empClaimDto?.street ? String(this.empClaimDto.street) + ' ' : '';
                fullAddress += this.empClaimDto?.unitNo ? '#' + String(this.empClaimDto.unitNo) + ' ' : '';
                fullAddress += this.empClaimDto?.building ? String(this.empClaimDto.building) + ' ' : '';
                fullAddress += this.empClaimDto?.postalCode ? 'Singapore ' + String(this.empClaimDto.postalCode) : '';
                this.companyAddress = fullAddress;

                this.initialiseAddressFormGroup();
        } else {
            this.claimService.getCompanyInfo(this.empClaimDto?.csn).subscribe({
                next: (res) => this.csnRespHandler(res),
                error: (error) => this.errorRespHandler(error)
            });
        }
    }

    loadFormula(claimType: string, claimBasis: string, formulaCode: string) {
        this.commonClaimService.getClaimFormula(claimType, claimBasis).subscribe(res => {
            console.log('formula resp', res);
            if (res.body.data) {
                this.claimFormulaOption = [];
                const resp = res.body.data;
                resp.forEach((formula: any) => {
                    if (claimBasis === 'PD' || claimBasis === 'ICT') {
                        this.claimFormulaOption.push(formula);
                        this.createEcForm.get('formulaCode').setValue(formulaCode);
                        this.createEcForm.get('formulaCode').updateValueAndValidity();
                    } else if (claimBasis === 'PM' && this.addFormula(formula.value)) {
                        this.claimFormulaOption.push(formula);
                        this.createEcForm.get('formulaCode').setValue(formulaCode);
                        this.createEcForm.get('formulaCode').updateValueAndValidity();
                    }
                });
            }   
        });
    }

    loadItemStatusDropdown() {
        let params = new HttpParams();
        params = params.append("claimType",'E');
        params = params.append("station",'D');
        if (this.hardcopyDto?.empClaimHardcopyDto?.empClaimHardcopyItemList) {
            const hardcopyItemList = this.hardcopyDto?.empClaimHardcopyDto?.empClaimHardcopyItemList;
            if (hardcopyItemList.some(item => item.itemType === 'DIA')) {
                params = params.append("dfInd", 1);
            }
        } else if (this.createEcForm && this.createEcForm.get('items')?.value) {
            if (this.createEcForm.get('items').value.includes('DIA')) {
                params = params.append("dfInd", 1);
            }
        }
        this.claimService.retrieveDropDownItemStatus({params}).subscribe((itemStatusResp:any) => {
            const itemStatusDropdownList = itemStatusResp.body.data?.itemStatusDropdown?.map((dropdown: any) => {
                return {
                label: String(dropdown.statusDesc).toUpperCase(),
                value: dropdown.statusCode
                }
            });
            this.statusOption = itemStatusDropdownList
        });
    }

    errorRespHandler(error:any) {
        console.log("error is" + String(error?.error?.error_desc));
        this.toastService.error(error?.error?.error_desc);
    }
    
    csnRespHandler(res: any) {
        console.log('CSN RES', res);
        if (res) {
            const resData = res.body.data;
            this.isERegisteredCoy = resData.eRegistered;
            this.coyDirectInd = resData.directInd;

            let fullAddress = '';
            fullAddress += resData?.blk ? 'Blk ' + String(resData.blk) + ' ' : '';
            fullAddress += resData?.street ? String(resData.street) + ' ' : '';
            fullAddress += resData?.unit ? '#' + String(resData.unit) + ' ' : '';
            fullAddress += resData?.building ? String(resData.building) + ' ' : '';
            fullAddress += resData?.postalCode ? 'Singapore ' + String(resData.postalCode) : '';
            this.companyAddress = fullAddress;

            this.addressForm = this.formBuilder.group({
                blk: [resData?.blk],
                road: [resData?.street],
                unit: [resData?.unit],
                building: [resData?.building],
                postalCode: [resData?.postalCode, [Validators.maxLength(6)]]
            });
        }
    }

    initialiseAddressFormGroup(): FormGroup {
        if (this.isDummyCoy === true) {
            return this.addressForm = this.formBuilder.group({
                blk: [''],
                road: [''],
                unit: [''],
                building: [''],
                postalCode: ['', [Validators.maxLength(6)]]
            });
        } else {
            return this.addressForm = this.formBuilder.group({
                blk: [this.empClaimDto?.blkNo],
                road: [this.empClaimDto?.street],
                unit: [this.empClaimDto?.unitNo],
                building: [this.empClaimDto?.building],
                postalCode: [this.empClaimDto?.postalCode, [Validators.maxLength(6)]]
            });
        }
    }

    initialiseRemarksFormGroup() {
        this.remarksForm = this.formBuilder.group({
            remarks: ['', [Validators.required, Validators.maxLength(450)]]
        });
    }

    changeFormGroupValidation() {
        // IF NOT DUMMY COMPANY
        if (this.isDummyCoy === false) {

            // SUBMITTED_BY: UI show dropdown, required validation
            const submitByControl = this.createEcForm.get('submittedBy');
            submitByControl.setValidators(Validators.required);
            submitByControl.updateValueAndValidity();

            // COMPANY_NAME: default as inputText(required). If dummy coy, UI changes to label
            const companyNameControl = this.createEcForm.get('companyName');
            companyNameControl.setValidators(Validators.required);
            companyNameControl.updateValueAndValidity();

        }
        
        if (this.isDummyCoy === true || this.isERegisteredCoy === 'N' || (this.isERegisteredCoy === 'Y' && this.selectedCertOfficer === 'NCO')) {
            // SUBMITTED_BY_NAME: Default as Label, InputText(required) if company is not e-registered or is a dummy company or is an e-registered company with “New Certification Officer” selected.
            const submitNameControl = this.createEcForm.get('submittedByName');
            submitNameControl.setValidators(Validators.required);
            submitNameControl.updateValueAndValidity();

            // SUBMITTED_BY_APPT: Default as Label, InputText(required) if company is not e-registered or is a dummy company or is an e-registered company with “New Certification Officer” selected.
            const submitApptControl = this.createEcForm.get('submittedByAppt');
            submitApptControl.setValidators(Validators.required);
            submitApptControl.updateValueAndValidity();
        }

        if (moment(this.claimDto.trgStartDate, 'YYYY-MM-DD').format('DD-MM-YYYY') === moment(this.claimDto.trgEndDate, 'YYYY-MM-DD').format('DD-MM-YYYY')) {
            const workHourStartControl = this.createEcForm.get('workHourStart');
            workHourStartControl.setValidators(Validators.required);
            workHourStartControl.updateValueAndValidity();

            const workHourEndControl = this.createEcForm.get('workHourEnd');
            workHourEndControl.setValidators(Validators.required);
            workHourEndControl.updateValueAndValidity();
        }
    }

    onBlurTrgStartEndDt(): void{
        this.outsideCurYr =  this.fallOutsideCurrentYr(this.createEcForm?.get('trgPeriodStartDt')?.value)||
        this.fallOutsideCurrentYr(this.createEcForm?.get('trgPeriodEndDt')?.value);
    }
    
    fallOutsideCurrentYr(date: any): boolean{
        const curYr = new Date().getFullYear();
        const inputDate = new Date(date);
        if (inputDate.getFullYear()< curYr || inputDate.getFullYear()> curYr){
            return true;
        } else return false;
    }

    checkFormValueChanges() {

        this.createEcForm.get('trgPeriodStartDt').valueChanges.subscribe(startDt => {
            if (startDt) {  

                // check changes between old and new policy claim, then reset the items accordingly
                if (this.isClaimOldPolicy() === true && this.isCurrentClaimOldPolicy=== false) {
                    this.createEcForm.get('items').setValue(null);
                    this.createEcForm.get('items').updateValueAndValidity();
                    this.isCurrentClaimOldPolicy = true;
                } else if (this.isClaimOldPolicy() === false && this.isCurrentClaimOldPolicy=== true) {
                    this.createEcForm.get('items').setValue(null);
                    this.createEcForm.get('items').updateValueAndValidity();
                    this.isCurrentClaimOldPolicy = false;
                }

                // check for Rest Day changes
                const startMonth = moment(startDt).startOf('month');
                this.minRestDate = startMonth.toDate();
                const startPP = this.createEcForm.get('startPayPeriod').value;
                if (startPP) {
                    if (new Date(startDt).getDate() < Number(startPP)) {
                        this.minRestDate = startMonth.subtract(1, 'months').toDate();
                    }
                }

                // check for overlapping claim changes
                const endDt = this.createEcForm.get('trgPeriodEndDt').value; 
                if (endDt) {
                    let params = new HttpParams();
                    if (this.claimDto?.id) {
                        params = params.append('claimId', this.claimDto.id);
                    }
                    params = params.append('nric', this.claimDto.nric);
                    params = params.append('claimStartDt', this.formatDate(startDt, 'YYYY-MM-DD'));
                    params = params.append('claimEndDt', this.formatDate(endDt, 'YYYY-MM-DD'));
                    this.getOverlappingClaims(params);
                }

                // check for public holiday
                const year = (startDt as Date).getFullYear();
                this.validateHasHoliday(year, this.createEcForm.get('trgPeriodStartDt'));

                // check for same trg date
                // if (moment(startDt).format('YYYY-MM-DD') === moment(endDt).format('YYYY-MM-DD')) {
                if (this.isTrgSameDay()) {    
                    console.log('IS TRG SAME DAY');
                    const workHourStartControl = this.createEcForm.get('workHourStart');
                    workHourStartControl.setValidators([Validators.required]);
                    workHourStartControl.updateValueAndValidity();
                    const workHourEndControl = this.createEcForm.get('workHourEnd');
                    workHourEndControl.setValidators([Validators.required]);
                    workHourEndControl.updateValueAndValidity();
                    
                    this.createEcForm.get('halfdayInd').setValue(true);
                    this.halfdayInd = true;
                } else {
                    this.createEcForm.get('workHourStart').clearValidators();
                    this.createEcForm.get('workHourStart').setValue(null);
                    this.createEcForm.get('workHourStart').updateValueAndValidity();
                    this.createEcForm.get('workHourEnd').clearValidators();
                    this.createEcForm.get('workHourEnd').setValue(null);
                    this.createEcForm.get('workHourEnd').updateValueAndValidity();

                    this.createEcForm.get('halfdayInd').setValue(false);
                    this.halfdayInd = false;
                }

                this.onBlurTrgStartEndDt();
            } 
        });
    
        this.createEcForm.get('trgPeriodEndDt').valueChanges.subscribe(endDt => {
            if (endDt) {
                
                // check for Rest Day changes
                const endMonth = moment(endDt).endOf('month');
                this.maxRestDate = endMonth.toDate();
                const startPP = this.createEcForm.get('startPayPeriod').value;
                if (startPP) {
                    if (new Date(endDt).getDate() > Number(startPP-1)) {
                        this.maxRestDate = endMonth.add(1, 'months').toDate();
                    }
                }
                this.populateRestDayAltSat();

                // check for overlapping claim changes
                const startDt = this.createEcForm.get('trgPeriodStartDt').value; 
                if (startDt) {
                    let params = new HttpParams();
                    if (this.claimDto?.id) {
                        params = params.append('claimId', this.claimDto.id);
                    }
                    params = params.append('nric', this.claimDto.nric);
                    params = params.append('claimStartDt', this.formatDate(startDt, 'YYYY-MM-DD'));
                    params = params.append('claimEndDt', this.formatDate(endDt, 'YYYY-MM-DD'));
                    this.getOverlappingClaims(params);
