    getOverlappingClaims(params: HttpParams) {
        this.ecClaimService.getOverlappingEmpClaim(params).subscribe(res => {
            if (res.body.data && res.body.data.length>0) {
                this.overlappingClaims = res.body.data;
                this.overlappingClaims.forEach(x=>{
                    let errMsg = "";
                    let panelMsg ="";
                    panelMsg = errMsg = x.caseId;
                    if (x.claimOption) {
                        panelMsg = panelMsg.concat("(").concat(x.claimOption).concat(")");
                    } 
                    if (x.draftByInit) {
                      errMsg = errMsg.concat("(").concat("Draft by ").concat(x.draftByInit).concat(")");
                    } 
                    x.errorMsg = errMsg;
                    x.panelMsg = panelMsg;
                });
                if (!this.createEcForm.hasError('overlapping')){
                    this.createEcForm.setErrors({...this.createEcForm.errors || {}, 'overlapping': true});
                }
            } else {
                this.overlappingClaims = [];
                if (this.createEcForm?.errors && this.createEcForm?.errors['overlapping']) {
                  delete this.createEcForm.errors['overlapping'];
                }
            } 
        });
    }

    isClaimOldPolicy(): boolean {
        if (moment(this.createEcForm.get('trgPeriodStartDt').value).isBefore(moment(NSPAppConstants.CLAIM_NEW_POLICY_CUT_OFF_DATE, 'DD-MM-YYYY'))) {
            return true;
        } else return false;
    }

    isTrgSameDay(): boolean {
       return moment(this.createEcForm?.get('trgPeriodStartDt').value).format('YYYY-MM-DD') === moment(this.createEcForm?.get('trgPeriodEndDt').value).format('YYYY-MM-DD') ? true : false;
    }

    // REST DAY LOGIC FOR SPECIFIC FORMULA
    populateRestDayAltSat() {
        this.restDayAltSatRevert();
        const restDayFc = this.createEcForm?.get('restDay');
        const formulaCode = this.createEcForm?.get('formulaCode').value;
        if (restDayFc.value && formulaCode) {
            const restDayValue = [...restDayFc.value];
            // identify if any item formula has 8a, 8b, 10a, 10b
            if (this.restDayFormulaLogic.includes(formulaCode)) {
                let firstSaturday: Date = null;
                restDayFc.value.forEach((restDay: Date) => {
                    if (moment(restDay).format('ddd').toUpperCase() === 'SAT') {
                        if (firstSaturday === null) {
                            firstSaturday = restDay;
                        } else if (moment(firstSaturday).isAfter(restDay)) {
                            firstSaturday = restDay;
                        }
                    }
                });
                
                if (firstSaturday !== null) {
                    this.altSaturdayList = [];
                    // find difference in weeks
                    const weekDiff = moment(this.createEcForm.get('trgPeriodEndDt').value).diff(moment(firstSaturday), 'weeks');
                    console.log('Difference in weeks', weekDiff);
                    if (weekDiff > 0) {
                        const listOfAltSaturdays = []; // subsequent saturdays (not including the selected sat)
                        
                        for (let i = 0; i < weekDiff; i++) {
                            const alternateSat = moment(firstSaturday).add((14*(i+1)), 'days');
                            if (alternateSat.isSameOrBefore(moment(this.createEcForm.get('trgPeriodEndDt').value)) && !(restDayValue.some(value => moment(value).format('YYYY-MM-DD') === alternateSat.format('YYYY-MM-DD')))) {
                                listOfAltSaturdays.push(alternateSat.toDate());
                            }
                        }
                        console.log('listOfAltSaturdays', listOfAltSaturdays);
                        this.altSaturdayList = [...listOfAltSaturdays];
                        restDayValue.push(...listOfAltSaturdays); // concat new list into the current list
                        restDayFc.setValue(restDayValue, { emitEvent: false });
                    } else {
                        this.restDayAltSatRevert();
                    }
                }
            } else {
                this.restDayAltSatRevert();
            }
        //         } else {
        //             this.updateFormulaOptions('E', this.createEcForm.get('claimBasis').value);    
        //         }
        //     } else if (restDayFc.value.some((restDay: Date) => moment(restDay).format('ddd').toUpperCase() === 'SAT')) {
        //         this.updateFormulaOptions('E', this.createEcForm.get('claimBasis').value);        
        //     }
        // } else {
        //     this.updateFormulaOptions('E', this.createEcForm.get('claimBasis').value);          
        }
    }

    // only show 1 saturday when showing calendar (remove alternate saturday)
    restDayAltSatRevert() {
        if (this.altSaturdayList.length > 0) {
        const restDayFc = this.createEcForm.get('restDay');
        const restDayFcValue = restDayFc.value.filter((value: Date) => {
            return !this.altSaturdayList.includes( value )
        });
        restDayFc.setValue(restDayFcValue, { emitEvent: false });
        }
    }

    restDayCustomWidth(): string {
        if (this.createEcForm) {
            const maxWidth = 25;
            let width = 14; // default
            const eachValueWidth = 7; 
            
            const restDayControl = this.createEcForm.get('restDay');
            if (restDayControl.value && restDayControl.value.length > 2) {
                for (let i=0; i<restDayControl.value.length-2; i++) {
                    width += eachValueWidth;
                }
                if (width > maxWidth) {
                    return String(maxWidth)+'rem';
                } else {
                    return String(width)+'rem';
                }
            } else {
                return '14rem';
            }
        } else {
            return '14rem'; 
        }
    }

    // ----------- FORMULA FUNCTION ---------------
    updateFormulaOptions(claimType: string, claimBasis: string) {
        const startPayPeriod = this.createEcForm?.get('startPayPeriod').value;
        const restDayList = this.createEcForm?.get('restDay').value;
        let params = new HttpParams();
        params = params.append('claimType', claimType);
        params = params.append('claimBasisCode', claimBasis);
        if (startPayPeriod) {
            params = params.append('payPeriodStartInd', 1);
        }
        if (restDayList && restDayList.length > 0) {
            params = params.append('restDayInd', 1);
            if (restDayList.some((day: Date) => moment(day).format('ddd').toUpperCase() === 'SAT')) {
            params = params.append('isSatInd', 1);
            }
        }
        const options: any[] = [];
        // this.claimFormulaOption = [];
        this.claimService.retrieveFormulaList({params}).subscribe((res: any) => {
            if (res.body.data) {
                const sortedData = res.body.data.sort(this.claimService.sortFormulaListByCode());
                sortedData.forEach((x:any)=>{
                    options.push({
                    label: String(x.formulaCode) + ' ' + String(x.formulaDesc),
                    value: x.formulaCode
                    })
                });
                this.claimFormulaOption = options;
            }
        });
    }

    addFormula(formulaCode: any): boolean {

        const restDay = this.createEcForm.get('restDay').value;
        const startPayPeriod = this.createEcForm.get('startPayPeriod').value;
        const code = String(formulaCode);
        if (code === '16A' || code === '16B' || code === '16C') {
            if (startPayPeriod) {
                return true;
            } else { 
                return false; 
            }
        }
        if (code === '16D') {
            console.log('REST DAY', restDay);
            if ((restDay && restDay.length > 0) && startPayPeriod) {
                return true;
            } else {
                return false;
            }
        }
        if (code === '8A ' || code === '10A' || code === '8B ' || code === '10B') {
            if (restDay && restDay.length > 0) {
                const isSaturday = restDay.some((day: any) => moment(day).format('ddd').toUpperCase() === 'SAT');
                console.log('IS REST DAY ON SATURDAY ?', isSaturday);
                if (isSaturday) {
                    return true;
                } else {
                    return false;
                }
            } else {
                return false;
            }
        } else {
            return true;
        }   
    }

