    formatStartPayPeriod() {
        const ppStartDtControl = this.createEcForm.get('startPayPeriod');
        if (ppStartDtControl.value && ppStartDtControl.value.length === 1) {
            const formatPayPeriodDt = this.addLeadingZero(ppStartDtControl.value, 2);
            ppStartDtControl.setValue(formatPayPeriodDt);
            // ppStartDtControl.updateValueAndValidity();
        }
    }

    addLeadingZero(str: string, maxLength: number): string {
        const diff = maxLength - str.length;
        let zeroes = '';
        for (let i=0; i<diff; i++) {
            zeroes += '0';
        }
        return String(zeroes) + String(str);
    }

    formatTime(time: any, formatType: string): string {
        return moment(time, ['HH:mm']).format(formatType);
    }

    formatDate(date: any, formatType: string): string {
        return moment(date).format(formatType);
    }

    formatCoyCsn(csn: string): string {
        const strLength = csn.length;
        const csn1 = csn.substring(0, strLength-5);
        const csn2 = csn.substring(strLength-5, strLength-2);
        const csn3 = csn.substring(strLength-2, strLength);
        this.hardcopyDto.csn1 = csn1;
        this.hardcopyDto.csn2 = csn2;
        this.hardcopyDto.csn3 = csn3;
        return csn1 + '-'+ csn2 + '-' + csn3;
    }

    onAddOtherItemsConvertToUpperCase() {
        const otherItemValue = this.createEcForm.value.otherItems;
        if (otherItemValue) {
            const upperCaseList: string[] = [];
            otherItemValue.forEach((value: string) => upperCaseList.push(value.toUpperCase()));
            this.createEcForm.get('otherItems').setValue(upperCaseList);
        }
    }

    checkDummyCoy(csn: string): boolean {
        const strLength = csn.length;
        const dummyCsn = csn.substring(strLength-5);
        return dummyCsn === String('99999');
    }

