        this.claimDto.hrWorkStart = this.createEcForm.get('workHourStart').value;
        this.claimDto.hrWorkEnd = this.createEcForm.get('workHourEnd').value;
        this.claimDto.trgStartDate = moment(this.createEcForm.get('trgPeriodStartDt').value).format('YYYY-MM-DD');
        this.claimDto.trgEndDate = moment(this.createEcForm.get('trgPeriodEndDt').value).format('YYYY-MM-DD');
        this.hardcopyDto.claimDto = this.claimDto;

        if (this.coyAddressEdited === true) {
            this.empClaimDto.blkNo = this.addressForm.get('blk').value;
            this.empClaimDto.building = this.addressForm.get('building').value;
            this.empClaimDto.street = this.addressForm.get('road').value;
            this.empClaimDto.unitNo = this.addressForm.get('unit').value;
            this.empClaimDto.postalCode = this.addressForm.get('postalCode').value;
        }

        this.empClaimDto.invalidAddressIndicator = this.isInvalidAddress === true ? 'Y' : 'N';
        this.empClaimDto.halfDayInd = this.halfdayInd === true ? 'Y' : 'N';
        this.empClaimDto.startPayPeriod = this.createEcForm.get('startPayPeriod').value;
        this.empClaimDto.certRole = this.selectedCertOfficer;
        this.empClaimDto.certSalutation = this.createEcForm.get('submittedBySalutation').value;
        this.empClaimDto.certName = this.createEcForm.get('submittedByName').value;
        this.empClaimDto.certAppt = this.createEcForm.get('submittedByAppt').value;
        this.empClaimDto.certEmail = this.createEcForm.get('submittedByEmail').value;
        this.empClaimDto.certContactNo = this.createEcForm.get('submittedByPhone1').value;
        this.empClaimDto.certContactExt = this.createEcForm.get('submittedByPhone2').value;
        
        this.hardcopyDto.empClaimDto = this.empClaimDto;

        if (this.isERegisteredCoy === 'N' && this.coyAddressEdited === true) {
            this.hardcopyDto.saveEmpClaimRequestDto.saveCompanyAddressInd = 'Y';
