    }

    prepareHardcopyItems(): EmpClaimHardcopyItem[] {
        const hardcopyItemList: EmpClaimHardcopyItem[] = [];
        const itemList = this.createEcForm.get('items').value;
        const existingHardcopyItemList = this.hardcopyDto?.empClaimHardcopyDto?.empClaimHardcopyItemList; // check if there are existing item (for update)
        console.log('existingHardcopyItemList', existingHardcopyItemList);
        if (itemList.length > 0) {
            itemList.forEach((itemCode: any) => {
                if (itemCode === 'OAW') {
                    const owItem = new EmpClaimHardcopyItem();
                    if (existingHardcopyItemList) {
                        const itemObj = existingHardcopyItemList.find(item => item.itemType === 'ORD');
                        owItem.id = (itemObj) ? itemObj.id : null;
                    }   
                    owItem.itemName = 'OW';
                    owItem.itemType = 'ORD';
                    hardcopyItemList.push(owItem);
                    
                    const awItem = new EmpClaimHardcopyItem();
                    if (existingHardcopyItemList) {
                        const itemObj = existingHardcopyItemList.find(item => item.itemType === 'ADD');
                        awItem.id = (itemObj) ? itemObj.id : null;
                    }
                    awItem.itemName = 'AW';
                    awItem.itemType = 'ADD';
                    hardcopyItemList.push(awItem);
                    
                } else if(this.isClaimOldPolicy()) {
                    const claimItem = new EmpClaimHardcopyItem();
                    
                    if (existingHardcopyItemList) {
                        const itemObj = existingHardcopyItemList.find(item => {
                            if (item.itemType === 'ONB' && item.itemName.trim().toUpperCase() === itemCode.trim().toUpperCase()) {
                            return true;
                            } else if (item.itemType === 'OBS' && item.itemType === itemCode) {
                                return true;
                            } else {
                                return false;
                            }
                        });
                        claimItem.id = (itemObj) ? itemObj.id : null;
                    }
                    claimItem.itemName = itemCode === 'OBS' ? 'BASIC SALARY' : itemCode;
                    claimItem.itemType = itemCode === 'OBS' ? 'OBS' : 'ONB';
                    hardcopyItemList.push(claimItem);
                } else {
                    const claimItem = new EmpClaimHardcopyItem();
                    const itemOption = this.itemsOption.find(i => i.value === itemCode);
                    if (itemOption) {
                        if (existingHardcopyItemList) {
                            const itemObj = existingHardcopyItemList.find(item => item.itemType === itemCode);
                            claimItem.id = (itemObj) ? itemObj.id : null;
                        }
                        claimItem.itemName = itemOption.label;
                        claimItem.itemType = itemCode;
                        hardcopyItemList.push(claimItem);
                    }
                }   
            });
        }
        const otherItemList = this.createEcForm.get('otherItems').value;
        if (otherItemList.length > 0) {
            otherItemList.forEach((itemName: any) => {
                const claimItem = new EmpClaimHardcopyItem();
                if (existingHardcopyItemList) {
                    const itemObj = existingHardcopyItemList.find(item => item.itemName.trim().toUpperCase() === itemName.trim().toUpperCase() 
                        && item.itemType === 'ECO');
                    claimItem.id = (itemObj) ? itemObj.id : null;
                }
                claimItem.itemName = this.appendOtherItemName(itemName);
                claimItem.itemType = 'ECO';
                hardcopyItemList.push(claimItem);
            });
        }
        
        return hardcopyItemList;
    }

