    downloadDocument() {
        console.log('selectedDocument for download', this.selectedDocument);

        if (this.selectedDocument.length === 1) {
            const id = this.selectedDocument[0].docId;
            console.log('file id', id);
            this.fileUploadService.downloadTxnId(id).subscribe((res:any) => {
                if (res) {
                    console.log('download res', res);
                    const data = res;
                    const contentDispositionHeader: string = data.headers.get('content-disposition');
                    const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
                    const blob = new Blob([data.body], { type: data.headers.get('content-type') });
                    const anchor = document.createElement("a");
                    anchor.href = URL.createObjectURL(blob);
                    anchor.download = fileName;
                    anchor.click();
                }
            });
        } else if (this.selectedDocument.length > 1) {
            const ids = this.selectedDocument.map(doc => {return doc.docId as string});
            console.log('file ids', ids);
            this.fileUploadService.downloadZip(ids).subscribe((res:any) => {
                if (res) {
                    console.log('download zip res', res);
                    const data = res;
                    const contentDispositionHeader: string = data.headers.get('content-disposition');
                    const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
                    const blob = new Blob([data.body], { type: data.headers.get('content-type') });
                    const anchor = document.createElement("a");
                    anchor.href = URL.createObjectURL(blob);
                    anchor.download = fileName;
                    anchor.click();
                }
            });
        }        
    }
