    // ----------- Submitted By (Certification Officer) ---------------
    certOfficerOnChanges() {
        if (this.selectedCertOfficer === 'NCO') {
            this.isNewCO = true;
            this.selectedSalutation = null;
            this.createEcForm.get('submittedBySalutation').setValue(null);
            this.createEcForm.get('submittedByName').setValue(null);
            this.createEcForm.get('submittedByAppt').setValue(null);
            this.createEcForm.get('submittedByEmail').setValue(null);
            this.createEcForm.get('submittedByPhone1').setValue(null);
            this.createEcForm.get('submittedByPhone2').setValue(null);
            this.changeFormGroupValidation();
        } else if (this.empClaimDto?.certRole && this.selectedCertOfficer === this.empClaimDto?.certRole) {
            this.isNewCO = false;
            this.selectedSalutation = this.empClaimDto?.certSalutation;
            this.createEcForm.get('submittedBy').setValue(this.empClaimDto?.certRole);
            this.createEcForm.get('submittedBySalutation').setValue(this.empClaimDto?.certSalutation);
            this.createEcForm.get('submittedByName').setValue(this.empClaimDto?.certName);
            this.createEcForm.get('submittedByAppt').setValue(this.empClaimDto?.certAppt);
            this.createEcForm.get('submittedByEmail').setValue(this.empClaimDto?.certEmail);
            this.createEcForm.get('submittedByPhone1').setValue(this.empClaimDto?.certContactNo);
            this.createEcForm.get('submittedByPhone2').setValue(this.empClaimDto?.certContactExt);

            if (!this.coMap.has(this.selectedCertOfficer)) {
                const coDetail = {
                    certRole: this.empClaimDto?.certRole,
                    certSalutation: this.empClaimDto?.certSalutation,
                    certName: this.empClaimDto?.certName,
                    certAppt: this.empClaimDto?.certAppt,
                    certEmail: this.empClaimDto?.certEmail,
                    certContactNo: this.empClaimDto?.certContactNo,
                    certContactExt: this.empClaimDto?.certContactExt
                }
                this.coMap.set(this.selectedCertOfficer, coDetail);
            }
        } else {
            this.isNewCO = false;
            let params = new HttpParams;
            params = params.append('coRole', this.selectedCertOfficer);
            params = params.append('csn', this.empClaimDto.csn);
            this.claimService.getCertificationOfficer({params}).subscribe((res: any) => {
                if (res.body.data) {
                    const response = res.body.data;
                    this.selectedSalutation = response?.salutation;
                    this.createEcForm.get('submittedBy').setValue(response?.officerRole);
                    this.createEcForm.get('submittedBySalutation').setValue(response?.salutation);
                    this.createEcForm.get('submittedByName').setValue(response?.name);
                    this.createEcForm.get('submittedByAppt').setValue(response?.appointment);
                    this.createEcForm.get('submittedByEmail').setValue(response?.email);
                    this.createEcForm.get('submittedByPhone1').setValue(response?.contactNo);
                    this.createEcForm.get('submittedByPhone2').setValue(response?.contactExt);

                    if (!this.coMap.has(this.selectedCertOfficer)) {
                        const coDetail = {
                            certRole: response?.officerRole,
                            certSalutation: response?.salutation,
                            certName: response?.name,
                            certAppt: response?.appointment,
                            certEmail: response?.email,
                            certContactNo: response?.contactNo,
                            certContactExt: response?.contactExt
                        }
                        this.coMap.set(this.selectedCertOfficer, coDetail);
                    }
                }
            });
        }
    }

    editedCertOfficer(): boolean {
        if (this.coMap.has(this.selectedCertOfficer)) {
            const coDetail = this.coMap.get(this.selectedCertOfficer);

            if (this.createEcForm.get('submittedByName').value && coDetail.certName && this.createEcForm.get('submittedByName').value !== coDetail.certName) {
                return true;
            } else if (this.createEcForm.get('submittedByAppt').value && coDetail.certAppt && this.createEcForm.get('submittedByAppt').value !== coDetail.certAppt) {
                return true;
            } else if (this.createEcForm.get('submittedByEmail').value && coDetail.certEmail && this.createEcForm.get('submittedByEmail').value !== coDetail.certEmail) {
                return true;
            } else if (this.createEcForm.get('submittedByPhone1').value && coDetail.certContactNo && this.createEcForm.get('submittedByPhone1').value !== coDetail.certContactNo) {
                return true;
            } else {
                return false;
            }
        } else {
            return false;
        }
    }
