    isLateClaim(): boolean{
        if (this.createEcForm.get('trgPeriodStartDt').value) {
          const claimStart = this.createEcForm.get('trgPeriodStartDt')?.value;
          const dateNow  = new Date();
          const threshold = moment(dateNow).subtract(1, 'years').subtract(1, 'days').toDate();
        //   const threshold = new Date(dateNow.getFullYear()-1, dateNow.getMonth(),dateNow.getDay()); 
          return claimStart<threshold;
        } else return false;
    }

    isLongPeriod(): boolean {
        if (this.createEcForm.get('trgPeriodStartDt').value && this.createEcForm.get('trgPeriodEndDt').value){
          const diffDays = this.dateUtilsService.getDaysBetween((this.createEcForm.get('trgPeriodStartDt').value as Date),(this.createEcForm.get('trgPeriodEndDt').value as Date));
          return diffDays>30 && diffDays<60;
        } else return false;
    }

    workHourStartMoreThanEnd() {
        if (this.createEcForm.get('workHourStart').value && this.createEcForm.get('workHourEnd').value){
            return this.createEcForm.get('workHourStart').value > this.createEcForm.get('workHourEnd').value;
        } else return false;
    }

