import { ICreatePayrollRecord } from "../model/create-payroll.model";
import { Injectable } from '@angular/core';
import { HttpClient, HttpResponse } from '@angular/common/http';
import { ApplicationConfigService } from 'app/core/config/application-config.service';
import { Observable } from "rxjs";
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';

export type EntityResponseType = HttpResponse<ICreatePayrollRecord>;
export type EntityArrayResponseType = HttpResponse<ICreatePayrollRecord[]>;

@Injectable({
    providedIn: 'root'
})
export class CreatePayrollService{
    public resourceUrl = this.applicationConfigService.getEndpointFor('/api/payroll/payroll');
    constructor(
        private applicationConfigService: ApplicationConfigService,
        private ns2Http: Ns2HttpService,
        private http: HttpClient
    ){}

    public getPersonalParticularsByNric(nric: string): Observable<any>{
        const url = `${this.resourceUrl}/nsman/${nric}`;
        return this.ns2Http.get(url);
    }

    public postCreatePayrollRequest(body: any): Observable<any> {
        const url = `${this.resourceUrl}/create`;
        return this.ns2Http.post(url, body);
    }
}
