import { ValidatorFn, AbstractControl } from '@angular/forms';

/* Validate working cap 1-999999 or Unlimited or NA */
export function workingCapValidator(): ValidatorFn {
  return (control: AbstractControl) : { [key: string]: boolean } | null => {
    if (!control.value) {
      return null
    }

    if (isNaN(control.value)) {
      if (control.value.toUpperCase() !== 'UNLIMITED' && control.value.toUpperCase() !== 'NA') {
        return { 'violateLimit' : true };
      }
    } else {
      if (parseInt(control.value, 10) < 1 || parseInt(control.value, 10) > 999999) {
        return { 'violateLimit' : true };
      }
    }

    return null
  }
}
