import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, combineLatest } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { Resource } from '../resource/resource.model';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { ResourceService } from './resource.service';
import { Application } from '../application/application.model';

@Component({
  selector: 'ic-resource',
  templateUrl: './resource.component.html',
})
export class ResourceComponent implements OnInit, OnDestroy {
  resources: Resource[] = [];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;
  searchCriteria: any;
  applications: Application[] = [];
  resourceTypes: any[];

  constructor(
    private resourceService: ResourceService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager
  ) {
    this.searchCriteria = {
      applicationId: '',
      resourceName: '',
      resourceType: '',
      resourcePath: '',
    };
    this.resourceTypes = [
      { codeId: 'URI', codeDesc: 'URI' },
      { codeId: 'WEBURI', codeDesc: 'WEBURI' },
      { codeId: 'PAGE_RES', codeDesc: 'PAGE_RES' },
    ];
  }

  loadAll(): void {
    this.resourceService
      .query({
        applicationId: this.searchCriteria.applicationId,
        resourceName: this.searchCriteria.resourceName,
        resourceType: this.searchCriteria.resourceType,
        resourcePath: this.searchCriteria.resourcePath,
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }

  transition(): void {
    this.router.navigate(['/admin/acm/ic-resource'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  search(): void {
    this.links = {
      last: 0,
    };
    this.page = 0;
    this.predicate = 'resourceId';
    this.reverse = false;
    this.loadAll();
  }

  clear(): void {
    this.searchCriteria = {
      applicationId: '',
      resourceName: '',
      resourceType: '',
      resourcePath: '',
    };
    this.page = 0;
    this.router.navigate([
      '/admin/acm/ic-resource',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    ]);
  }

  ngOnInit(): void {
    this.handleNavigation();
    this.resourceService.findAllApplication().subscribe(
      (res: HttpResponse<any>) => {
        this.applications = res.body;
      },
      (res: HttpResponse<any>) => this.onError(res.body)
    );

    this.registerChangeInResources();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: Resource): string {
    return item.id as string;
  }

  trackApplicationById(index: number, item: Application): any {
    return item.id;
  }

  registerChangeInResources(): void {
    this.eventSubscriber = this.eventManager.subscribe('resourceListModification', () => this.loadAll());
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
    if (this.predicate !== 'resourceId') {
      result.push('resourceId');
    }
    return result;
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    this.resources = data;
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div>
  <h2>
    <span icTranslate="acmAdmin.resource.home.title">Resources</span>
  </h2>
  <ic-alert></ic-alert>
  <br />
  <div class="row">
    <form name="searchForm" class="col-sm-12">
      <div class="card">
        <div class="card-header"><span icTranslate="acmAdmin.home.SearchCriteria">Search Criteria</span></div>
        <div class="card-block" style="padding: 1.5%">
          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Application</label>
            <div class="col-sm-4">
              <select class="form-control" name="applicationId" [(ngModel)]="searchCriteria.applicationId">
                <option [ngValue]="null"></option>
                <option
                  [ngValue]="
                    applicationOption.appId === searchCriteria.applicationId ? searchCriteria.applicationId : applicationOption.appId
                  "
                  *ngFor="let applicationOption of applications; trackBy: trackApplicationById"
                >
                  {{ applicationOption.appName }}
                </option>
              </select>
            </div>

            <label class="col-sm-2 form-control-label">Resource Type</label>
            <div class="col-sm-4">
              <select class="form-control" name="resourceType" [(ngModel)]="searchCriteria.resourceType">
                <option [ngValue]="null"></option>
                <option
                  [ngValue]="
                    resourceType!['codeId'] === searchCriteria.resourceType ? searchCriteria.resourceType : resourceType!['codeId']
                  "
                  *ngFor="let resourceType of resourceTypes"
                >
                  {{ resourceType['codeDesc'] }}
                </option>
              </select>
            </div>
          </div>

          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Resource Name</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="resourceName" id="resourceName" [(ngModel)]="searchCriteria.resourceName" />
            </div>

            <label class="col-sm-2 form-control-label">Resource Path</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="resourcePath" id="resourcePath" [(ngModel)]="searchCriteria.resourcePath" />
            </div>
          </div>

          <div style="margin-top: 1.5%">
            <button id="searchResource" type="button" class="btn btn-warning btn-sm" (click)="search()">
              <fa-icon [icon]="'search'"></fa-icon>
              <span class="fa fa-search" icTranslate="acmAdmin.home.search"></span>
            </button>

            <button type="button" class="btn btn-secondary btn-sm" (click)="clear()">
              <fa-icon [icon]="'trash-alt'"></fa-icon>
              <span class="fa fa-trash-o" icTranslate="acmAdmin.home.reset"></span>
            </button>

            <button id="createNewResource" class="btn btn-primary btn-sm" [routerLink]="['../ic-resource-new']">
              <fa-icon [icon]="'plus'"></fa-icon>
              <span icTranslate="acmAdmin.resource.home.createLabel"> Create new Resource </span>
            </button>
          </div>
        </div>
      </div>
    </form>
  </div>
  <br />
  <div class="card">
    <div class="card-header"><span icTranslate="acmAdmin.home.SearchResult">Search Criteria</span></div>
    <div class="card-block" style="padding: 1.5% 1.5% 0 1.5%">
      <div class="table-responsive" *ngIf="resources">
        <table class="table table-striped">
          <thead>
            <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
              <th scope="col"><span icTranslate="acmAdmin.home.sn">S/No.</span></th>
              <th scope="col" icSortBy="application.appName">
                <span icTranslate="acmAdmin.resource.application">Application</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="resourceName">
                <span icTranslate="acmAdmin.resource.resourceName">Resource Name</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="resourceName">
                <span icTranslate="acmAdmin.resource.resourceType">Resource Type</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="resourceName">
                <span icTranslate="acmAdmin.resource.resourcePath">Resource Path</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>

              <th scope="col" icSortBy="updatedBy">
                <span icTranslate="acmAdmin.resource.updatedBy">Updated By</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="updatedDt">
                <span icTranslate="acmAdmin.resource.updatedDt">Updated Date</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" style="text-align: center">
                <span icTranslate="acmAdmin.home.operation">Operation</span>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let resource of resources; let idx = index; trackBy: trackId">
              <td>{{ idx + 1 }}</td>
              <td>
                <div *ngIf="resource.application">{{ resource.application?.appName }}</div>
              </td>
              <td>{{ resource.resourceName }}</td>
              <td>{{ resource.resourceType }}</td>
              <td>{{ resource.resourcePath }}</td>
              <td>{{ resource.updatedBy }}</td>
              <td>{{ resource.updatedDt | date: 'yyyy-MM-dd' }}</td>
              <td class="text-end">
                <div class="btn-group flex-btn-group-container">
                  <button
                    type="submit"
                    [routerLink]="['../../../admin/acm/ic-resource', resource.resourceId]"
                    class="btn btn-primary btn-sm"
                  >
                    <fa-icon [icon]="'pencil-alt'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.edit">Edit</span></button
                  >&nbsp;
                  <button
                    [id]="resource.resourceName"
                    type="submit"
                    [routerLink]="['../ic-resource/' + resource.resourceId + '/delete']"
                    [replaceUrl]="true"
                    class="btn btn-danger btn-sm"
                  >
                    <fa-icon [icon]="'times'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.delete">Delete</span>
                  </button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div *ngIf="resources && resources.length">
        <div class="row justify-content-center">
          <ic-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></ic-item-count>
        </div>
        <div class="row justify-content-center">
          <ngb-pagination
            [collectionSize]="totalItems"
            [pageSize]="itemsPerPage"
            [(page)]="page"
            (pageChange)="loadPage(page)"
          ></ngb-pagination>
        </div>
      </div>
    </div>
  </div>
</div>
