import { Injectable } from '@angular/core';
import { HttpClient, HttpParams, HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';
import { SERVER_API_URL } from 'app/app.constants';
import { createRequestOption } from 'app/core/request/request-util';

@Injectable({ providedIn: 'root' })
export class AcmReportService {
  public resourceUrl = SERVER_API_URL + 'api/acmReport';

  constructor(protected http: HttpClient) {}

  exportReport(req?: any): Observable<HttpResponse<ArrayBuffer>> {
    const params: HttpParams = createRequestOption(req);
    return this.http.get(this.resourceUrl, { params, observe: 'response', responseType: 'arraybuffer' });
  }
}
