import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParamService } from './param.service';
import { ParamModel } from './param.model';
import { ParamPopupService } from './param-popup.service';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'ic-param-dialog',
  templateUrl: './param-dialog.component.html',
  styles: [
    `
      .errMsgs {
        margin-left: auto;
      }
    `,
  ],
})
export class ParamDialogComponent implements OnInit {
  appList: any[] = [];
  routeData: any;
  param: ParamModel = new ParamModel();
  paramTypes: any;
  effectiveDateDp: any;
  expireDateDp: any;
  errorBody: any;

  constructor(
    private paramService: ParamService,
    private alertService: AlertService,
    private eventManager: EventManager,
    public activeModal: NgbActiveModal,
    private router: Router
  ) {}

  ngOnInit(): any {
    this.initParam();
    this.getAppList();
  }

  getAppList(): any {
    this.paramService.getAppList().subscribe((data: any[]) => {
      this.appList = data;
    });
  }

  initParam(): any {
    this.paramTypes = this.paramService.getParamTypes();

    if (!(this.param as any).paramList) {
      (this.param as any).paramList = [{ value: '' }];
    }

    if (!(this.param as any).paramMap) {
      (this.param as any).paramMap = [{ id: this.paramService.newGuid() }];
    }
  }

  save(): any {
    if ((this.param as any).id) {
      this.subscribeToSaveResponse(this.paramService.update(this.param as any));
    } else {
      this.subscribeToSaveResponse(this.paramService.create(this.param as any));
    }
  }

  clear(): any {
    this.activeModal.dismiss('cancel');
    this.backToPrevious();
  }

  addNewListItem(): any {
    const listItem = { value: '' };
    this.param.paramList.push(listItem);
  }

  removeListItem(i: any): any {
    this.param.paramList.splice(i, 1);
  }

  addMapItem(): any {
    const mapItem = { id: this.paramService.newGuid() };
    this.param.paramMap.push(mapItem);
  }

  removeMapItem(i: any): any {
    this.param.paramMap.splice(i, 1);
  }

  private subscribeToSaveResponse(result: Observable<ParamModel>): any {
    result.subscribe(
      (res: ParamModel) => this.onSaveSuccess(res),
      (res: HttpResponse<any>) => this.onSaveError(res)
    );
  }

  private onSaveSuccess(result: ParamModel): void {
    this.eventManager.broadcast({ name: 'paramListModification', content: 'OK' });
    this.activeModal.dismiss(result);
    this.backToPrevious();
  }

  private onSaveError(error: any): void {
    try {
      this.errorBody = error.body;
    } catch (exception) {
      error.message = error.text();
    }
    this.onError(error);
  }

  private onError(error: any): any {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }

  private backToPrevious(): any {
    setTimeout(() => {
      this.router.navigate(['admin/ic-param']);
    }, 0);
  }
}

@Component({
  selector: 'ic-param-popup',
  template: '',
})
export class ParamPopupComponent implements OnInit, OnDestroy {
  routeSub: any;

  constructor(private route: ActivatedRoute, private paramPopupService: ParamPopupService) {}

  ngOnInit(): any {
    this.routeSub = this.route.params.subscribe(params => {
      if (params['appId'] && params['paramKey']) {
        this.paramPopupService.open(ParamDialogComponent as Component, params['appId'], params['paramKey']);
      } else {
        this.paramPopupService.open(ParamDialogComponent as Component);
      }
    });
  }

  ngOnDestroy(): any {
    this.routeSub.unsubscribe();
  }
}
<form id="maintainParamForm" name="maintainParamForm" role="form"  #maintainParamForm="ngForm">
  <div class="modal-header">
    <h2 *ngIf="param.id">
      <span icTranslate="paramAdmin.param.updateParam">Update Parameter</span>
    </h2>
    <h2 *ngIf="!param.id">
      <span icTranslate="paramAdmin.param.createParam">Create new Parameter</span>
    </h2>
    <div class="errMsgs">
      <ic-alert-error></ic-alert-error>
    </div>
    <button type="button" class="close" data-dismiss="modal" aria-hidden="true" (click)="clear()">&times;</button>
  </div>
  <div class="modal-body">
    <div class="card">
      <div class="card-block" style="padding: 1.5%">
        <div class="rounded-pill row">
          <label class="col-md-3 form-control-label">
            <span icTranslate="paramAdmin.param.application">Application</span>
            <label style="color: red">*</label>
          </label>
          <div class="col-md-4" *ngIf="!param.id">
            <select class="form-control" [(ngModel)]="param.appId" id="appId" name="appId" required>
              <option [ngValue]="null"></option>
              <option *ngFor="let app of appList" [value]="app.appId">{{ app.appName }}</option>
            </select>
            <div [hidden]="!(maintainParamForm.controls.appId?.dirty && maintainParamForm.controls.appId?.invalid)">
              <small
                class="form-text text-danger"
                [hidden]="!maintainParamForm.controls.appId?.errors?.required"
                icTranslate="paramAdmin.param.maintain.required"
              >
                This field is required.
              </small>
            </div>
          </div>
          <div class="col-md-4" *ngIf="param.id">
            {{ param.appId }}
          </div>
        </div>

        <div class="rounded-pill row">
          <label class="col-md-3 form-control-label">
            <span icTranslate="paramAdmin.param.paramKey">Parameter Key</span>
            <label style="color: red">*</label>
          </label>
          <div class="col-md-4" *ngIf="!param.id">
            <input type="text" class="form-control" [(ngModel)]="param.paramKey" id="paramKey" name="paramKey" maxlength="255" required />
            <div [hidden]="!(maintainParamForm.controls.paramKey?.dirty && maintainParamForm.controls.paramKey?.invalid)">
              <small
                class="form-text text-danger"
                [hidden]="!maintainParamForm.controls.paramKey?.errors?.required"
                icTranslate="paramAdmin.param.maintain.required"
              >
                This field is required.
              </small>
            </div>
          </div>
          <div class="col-md-4" *ngIf="param.id">
            <input type="text" class="form-control" id="paramKey" name="paramKey" [(ngModel)]="param.paramKey" readonly />
          </div>
        </div>

        <div class="rounded-pill row">
          <label class="col-md-3 form-control-label">
            <span icTranslate="paramAdmin.param.paramType">Parameter Type</span>
            <label style="color: red">*</label>
          </label>
          <div class="col-md-4" *ngIf="!param.id">
            <select class="form-control" [(ngModel)]="param.paramType" id="paramType" name="paramType" required>
              <option [ngValue]="null"></option>
              <option *ngFor="let paramType of paramTypes" [value]="paramType.codeId">{{ paramType.label }}</option>
            </select>
            <div [hidden]="!(maintainParamForm.controls.paramType?.dirty && maintainParamForm.controls.paramType?.invalid)">
              <small
                class="form-text text-danger"
                [hidden]="!maintainParamForm.controls.paramType?.errors?.required"
                icTranslate="paramAdmin.param.maintain.required"
              >
                This field is required.
              </small>
            </div>
          </div>
          <div class="col-md-4" *ngIf="param.id">
            {{ param.paramTypeDesc }}
          </div>
        </div>

        <div class="rounded-pill row">
          <label class="col-md-3 form-control-label">
            <span icTranslate="paramAdmin.param.paramValue">Parameter Value</span>
            <label style="color: red">*</label>
          </label>
          <div class="col-md-4" *ngIf="param.paramType != 'L' && param.paramType != 'M'">
            <input
              type="text"
              class="form-control"
              [(ngModel)]="param.paramValue"
              id="paramValue"
              name="paramValue"
              maxlength="255"
              required
            />
            <div [hidden]="!(maintainParamForm.controls.paramValue?.dirty && maintainParamForm.controls.paramValue?.invalid)">
              <small
                class="form-text text-danger"
                [hidden]="!maintainParamForm.controls.paramValue?.errors?.required"
                icTranslate="paramAdmin.param.maintain.required"
              >
                This field is required.
              </small>
            </div>
          </div>

          <div class="col-md-9" *ngIf="param.paramType == 'L'">
            <div class="table-responsive">
              <table class="table table-striped table-bordered table-hover dataTable" style="margin-bottom: 0 !important">
                <thead>
                  <tr>
                    <th scope="col" class="text-center">
                      <span icTranslate="paramAdmin.param.sn">S/No.</span>
                    </th>
                    <th scope="col" class="text-center">
                      <span icTranslate="paramAdmin.param.maintain.listItemValue">Value</span>
                    </th>
                    <th scope="col" class="text-center">
                      <span icTranslate="acmAdmin.home.operation">Operation</span>
                    </th>
                  </tr>
                </thead>
                <tbody>
                  <tr *ngFor="let listItem of param.paramList; index as i">
                    <td class="text-center">{{ i + 1 }}</td>
                    <td>
                      <div class="col-md-4 col-lg-12">
                        <input type="text" class="form-control" [name]="'listItem' + i" [(ngModel)]="param.paramList[i].value" required />
                      </div>
                    </td>
                    <td>
                      <button class="btn btn-danger btn-xs" (click)="removeListItem(i)">
                        <fa-icon icon="times"></fa-icon>
                        <span icTranslate="paramAdmin.param.btn.remove">Remove</span>
                      </button>
                    </td>
                  </tr>
                </tbody>
                <tfoot>
                  <tr>
                    <td colspan="3">
                      <button class="btn btn-primary btn-xs" (click)="addNewListItem()">
                        <fa-icon icon="plus"></fa-icon>
                        <span icTranslate="paramAdmin.param.btn.add">Add</span>
                      </button>
                    </td>
                  </tr>
                </tfoot>
              </table>
            </div>
          </div>

          <div class="col-md-9" *ngIf="param.paramType == 'M'">
            <div class="table-responsive">
              <table class="table table-striped table-bordered table-hover dataTable" style="margin-bottom: 0 !important">
                <thead>
                  <tr>
                    <th scope="col" class="text-center">
                      <span icTranslate="paramAdmin.param.maintain.mapItemKey">Key</span>
                    </th>
                    <th scope="col" class="text-center">
                      <span icTranslate="paramAdmin.param.maintain.mapItemValue">Value</span>
                    </th>
                    <th scope="col" class="text-center">
                      <span icTranslate="acmAdmin.home.operation">Operation</span>
                    </th>
                  </tr>
                </thead>
                <tbody>
                  <tr *ngFor="let mapItem of param.paramMap; index as i">
                    <td>
                      <div class="col-md-4 col-lg-12">
                        <input
                          [id]="'mapItem' + i"
                          [name]="'mapItem' + i"
                          type="text"
                          class="form-control"
                          [(ngModel)]="param.paramMap[i].mapKey"
                          required
                        />
                      </div>
                    </td>
                    <td>
                      <div class="col-md-4 col-lg-12">
                        <input type="text" [name]="'mapValue' + i" class="form-control" [(ngModel)]="param.paramMap[i].mapValue" required />
                      </div>
                    </td>
                    <td>
                      <button class="btn btn-danger btn-xs" (click)="removeMapItem(i)">
                        <fa-icon icon="times"></fa-icon>
                        <span icTranslate="paramAdmin.param.btn.remove">Remove</span>
                      </button>
                    </td>
                  </tr>
                </tbody>
                <tfoot>
                  <tr>
                    <td colspan="3">
                      <button class="btn btn-primary btn-xs" (click)="addMapItem()">
                        <fa-icon icon="plus"></fa-icon>
                        <span icTranslate="paramAdmin.param.btn.add">Add</span>
                      </button>
                    </td>
                  </tr>
                </tfoot>
              </table>
            </div>
          </div>
        </div>

        <div class="rounded-pill row">
          <label class="col-md-3 form-control-label">
            <span icTranslate="paramAdmin.param.paramDesc">Parameter Description</span>
          </label>
          <div class="col-md-4">
            <input type="text" class="form-control" [(ngModel)]="param.paramDesc" id="paramDesc" name="paramDesc" maxlength="255" r />
          </div>
        </div>

        <div class="rounded-pill row">
          <label class="col-md-3 form-control-label">
            <span icTranslate="paramAdmin.param.effectiveDate">Effective Date</span>
          </label>
          <div class="col-md-4">
            <div class="input-group">
              <input
                type="date"
                class="form-control"
                placeholder="yyyy-mm-dd"
                name="effectiveDate"
                id="effectiveDate"
                [(ngModel)]="param.effectiveDate"
              />
            </div>
          </div>
        </div>

        <div class="rounded-pill row">
          <label class="col-md-3 form-control-label">
            <span icTranslate="paramAdmin.param.expireDate">Expire Date</span>
          </label>
          <div class="col-md-4">
            <div class="input-group">
              <input
                type="date"
                class="form-control"
                placeholder="yyyy-mm-dd"
                name="expireDate"
                id="expireDate"
                [(ngModel)]="param.expireDate"
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>

  <div class="modal-footer">
    <div style="margin-top: 1.5%">
      <button class="btn btn-primary btn-sm" [disabled]="maintainParamForm.form.invalid" (click)="save()">
        <fa-icon [icon]="'save'"></fa-icon>&nbsp;
        <span icTranslate="paramAdmin.param.btn.save">Save</span>
      </button>
      <button type="button" class="btn btn-secondary btn-sm" data-dismiss="modal" (click)="clear()">
        <fa-icon [icon]="'ban'"></fa-icon>&nbsp;
        <span icTranslate="paramAdmin.param.btn.cancel">Cancel</span>
      </button>
    </div>
  </div>
</form>
