import { HttpParams } from '@angular/common/http';
import { Component, EventEmitter, Input, OnInit, Output, SimpleChanges } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Claim } from 'app/claim/model/claim.model';
import { EmpClaim } from 'app/claim/ec-claim/model/EmpClaim.model';
import { EmpClaimRequest } from 'app/claim/ec-claim/model/EmpClaimRequest.model';
import { ECClaimService } from 'app/claim/ec-claim/service/ec-claim.service';
import { AdditionalEmpEClaimInfo } from 'app/claim/ec-claim/model/additional-emp-eclaim-info.model';
import { CustomToastService } from 'app/layouts/service/customtoast.service';

@Component({
    selector: 'n2-view-eclaim-additional-info-section',
    templateUrl: './view-eclaim-additional-info-section.component.html',
    styleUrls: ['./view-eclaim-additional-info-section.component.scss']
  })
export class ViewEClaimAdditionalInfoSectionComponent implements OnInit {
    @Input() eClaim: EmpClaimRequest;

    isHardcopyClaim: boolean;

    empClaimDto: EmpClaim;
    claimDto: Claim;
    additionalInfo: AdditionalEmpEClaimInfo;

    constructor(
        private ecClaimService: ECClaimService,
        private toastService: CustomToastService,
        private formBuilder: FormBuilder
    ) { }

    ngOnInit(): void {
        console.log('Company Child component checked');
        this.empClaimDto = this.eClaim?.empClaimDto;
        this.claimDto = this.eClaim?.claimDto;
        this.ecClaimService.getEClaimAdditionalInfoByEmpClaimId(this.empClaimDto.id).subscribe({
            next: (res) => {
                if (res.body.data) {
                    this.additionalInfo = res.body.data;
                }
            },
            error: (error) => this.errorRespHandler(error)
        });    
    }
    // ------------------------------------------

    errorRespHandler(error:any) {
        console.log("error is" + String(error?.error?.error_desc));
        this.toastService.error('Fail to load : ' + String(error?.error?.error_desc));
    }

    consoleLog(){
        console.log('additionalInfo ', this.additionalInfo);
    }
}
