import { Component, OnInit, ViewChild } from '@angular/core';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { IStopPayment } from 'app/payment/bankfile-stop-payment/stop-payment/model/stop-payment.model';
import {
  StopPaymentFileUploadComponent
} from 'app/payment/bankfile-stop-payment/stop-payment/stop-payment-file-upload/stop-payment-file-upload.component';
import { IKahRnsaData } from 'app/payment/deduction/model/kah-rnsa-data';
import { StopPaymentService } from 'app/payment/bankfile-stop-payment/stop-payment/service/stop-payment.service';
import { HttpErrorResponse, HttpResponse, HttpStatusCode } from '@angular/common/http';
import { EditRemarkComponent } from 'app/shared/component/edit-remark/edit-remark.component';
import {
  KahRnsaReleaseComponent
} from 'app/payment/bankfile-stop-payment/stop-payment/kah-rnsa-release/kah-rnsa-release.component';
import { LazyLoadEvent } from 'primeng/api';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { StopPaymentManualInsertComponent } from './stop-payment-manual-insert/stop-payment-manual-insert.component';
import { stringify } from 'querystring';
import { KahRnsaService } from './service/kah-rnsa.service';
import { IKahRnsaSummary } from './model/kah-rnsa-release-record.model';
import { Table } from 'primeng/table';
import { Validators } from '@angular/forms';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';

@Component({
  selector: 'n2-stop-payment',
  templateUrl: './stop-payment.component.html',
  styleUrls: ['./stop-payment.component.scss']
})
export class StopPaymentComponent implements OnInit {
  stopPaymentArray: IStopPayment[];
  displayStopPaymentFileUploadDialog: any = { display: false };
  displayKahRnsaReleaseHistoryDialog: any = { display: false };
  displayKahRnsaReleaseDialog: any = { display: false };
  displayStopPaymentEditRemarkDialog: any = { display: false };
  displayStopPaymentManualInsertDialog: any = { display: false };
  kahRnsaData: IKahRnsaSummary;
  searchNricInput: string;
  //Edit remarks
  selectedStopPaymentRecord: IStopPayment;

  //Deletion
  selectedRecordsToDelete: IStopPayment[] = [];
  selectAll = false;

  //Stop Payment
  loading = false;
  totalRecords = 0;
  pageData: { currentPage: number } = {
    currentPage: 0
  }
  pageSize = 10;
  lazyLoadEvent : LazyLoadEvent;
  
  //
  @ViewChild('stopPaymentManualInsert', { static: true })
  public manualInsertComponent!: StopPaymentManualInsertComponent;

  @ViewChild(KahRnsaReleaseComponent)
  private kahRnsaRelease!: KahRnsaReleaseComponent;

  @ViewChild('editStopPaymentRemark', { static: true })
  private editStopPaymentRemark!: EditRemarkComponent;

  @ViewChild('stopPaymentFileUpload', { static: true })
  private fileUploadComponent!: StopPaymentFileUploadComponent;

  @ViewChild('stopPaymentTable', { static: true })
  private stopPaymentTable!: Table;

  constructor(
    private toastService: CustomToastService,
    private nspConfirmService: NspConfirmationService,
    private stopPaymentService: StopPaymentService,
    private n2httpService: Ns2HttpService,
    private kahRnsaService: KahRnsaService
  ) {
  }

  ngOnInit(): void {
    this.loadKahRnsaSummary();
  }

  loadStopPayments(event: LazyLoadEvent): void {
    this.loading = true;
    this.lazyLoadEvent = event;

    setTimeout(() => {
      const options = this.n2httpService.convertLazyEvent(event, this.pageData, this.pageSize);
      
      this.stopPaymentService.getAllStopPayment(options).subscribe(
        (res: HttpResponse<any>) => {
          // Pipe response to an array of Interface model
          this.stopPaymentArray = res.body?.data?.content;
          console.log ("stopPaymentArray", this.stopPaymentArray)
          this.loading = false;
          this.totalRecords = res.body.data.totalElements;
        },
        (error: HttpErrorResponse) => {
          console.log(error.error?.error_desc);
          if (error.status === HttpStatusCode.NotFound) {
            this.toastService.error("No Stop Payment records");
          }
          this.loading = false;
        }
      );
    }, 1000);
  }

  onPage(event: any): void {
    this.pageSize = event.rows;
    this.pageData.currentPage = event.first / event.rows;

    //clear selection for delete all
    this.selectedRecordsToDelete = [];
    this.selectAll = false;
  }
  
  search(): void {
    this.stopPaymentTable.filter(this.searchNricInput, 'nric', 'contains');
  }

  exportCSV(): void {
    // TODO: Implement export CSV function or call it from a utility class
  }

  onOpen(dialog: any): void {
    dialog.display = true;
  }

  onOpenEditRemark(dialog: any, stopPaymentRecord: IStopPayment): void {
    dialog.display = true;
    this.selectedStopPaymentRecord = stopPaymentRecord;
    this.editStopPaymentRemark.remarksValue = stopPaymentRecord.remarks;
  }

  onSubmit(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog);
  }

  onEditRemarkSubmit(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      // const fields = new Map<string, any>();
      // fields.set("remarks", this.editStopPaymentRemark.remarksValue);
      // console.log("fields", fields);
      const fields = {"remarks": this.editStopPaymentRemark.remarksValue};
      console.log("selected edit", this.selectedStopPaymentRecord);
      this.stopPaymentService.patch(this.selectedStopPaymentRecord.stopPaymentId, fields).subscribe(
        (res: HttpResponse<any>) => {
          this.toastService.success(res.body?.success_message);
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error?.error_desc);
        }
      );
    });
  }

  onManualInsertSubmit(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      this.stopPaymentService.create(Array.from(this.manualInsertComponent.stopPaymentManualInsertMap.values())).subscribe(
        (res: HttpResponse<any>) => {
          this.toastService.success("Records created successfully");
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error?.error_desc);
        }
      );
    });
  }

  onDeleteAllSubmit(event: Event, key: string): void {
    const options = this.n2httpService.convertLazyEvent(this.lazyLoadEvent, this.pageData, this.pageSize);
    
    console.log("options", options);  
    this.nspConfirmService.onConfirmPopup(event, key, null, () => {
      this.stopPaymentService.deleteAll(options).subscribe(
        (res: HttpResponse<any>) => {
          this.toastService.success(res.body?.success_message);
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error?.error_desc);
        }
      );
    });
  }

  onDeleteSubmit(event: Event, key: string): void {
    if(this.selectedRecordsToDelete.length === 0) {
      this.toastService.error("No records selected");
    } else {
      const ids: string[] = this.selectedRecordsToDelete.map(record => String(record.stopPaymentId));

      this.nspConfirmService.onConfirmPopup(event, key, null, () => {
        this.stopPaymentService.delete(ids).subscribe(
          (res: HttpResponse<any>) => {
            this.toastService.success(res.body?.success_message);
            this.loadStopPayments(this.lazyLoadEvent);
            this.selectedRecordsToDelete = [];
          },
          (error: HttpErrorResponse) => {
            this.toastService.error(error.error?.error_desc);
          }
        );
      });
    }
  }

  //Delete selection
  onSelectionChange(value: any[] = []): void {
    this.selectAll = value.length === this.stopPaymentArray.length;
    this.selectedRecordsToDelete = value;
  }

  onSelectAllChange(event: any): void {
    const checked = event.checked;

    if (checked) {
      this.selectAll = true;
      this.selectedRecordsToDelete = this.stopPaymentArray;
    } else {
      this.selectAll = false;
      this.selectedRecordsToDelete = [];
    }
  }

  loadKahRnsaSummary():void {
    this.kahRnsaService.getSummary().subscribe(
      (res: HttpResponse<any>) => {
        console.log("res", res.body);
        this.kahRnsaData = res.body?.data;
      },
      (error: HttpErrorResponse) => {
        this.toastService.error(error.error?.error_desc);
      }
    );
  }

  //Release
  onReleaseSubmit(event: Event, key: string): void {
    const param = this.kahRnsaRelease.getRequestValue();
    this.nspConfirmService.onConfirmPopup(event, key, null, () => {
      this.kahRnsaService.release(param).subscribe(
        (res: HttpResponse<any>) => {
          this.toastService.success(res.body?.success_message);
          this.displayKahRnsaReleaseDialog.display = false;
          this.kahRnsaRelease.reset();
        },
        (error: HttpErrorResponse) => {
          this.toastService.error(error.error?.error_desc);
        }
      );
    });
  }

  //Batch file upload
  onBatchFileUploadSubmit(event: Event, key: string, dialog: any): void {
    console.log("the verified", this.fileUploadComponent.getVerifiedFiles());
    const request = {
      remarks: this.fileUploadComponent.batchFileForm.get('remarks').value,
      fileId: this.fileUploadComponent.getVerifiedFiles().map(x=>
        String(x.meta_data.id)
      )
    }

    console.log("request", request);
    
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      this.stopPaymentService.upload(request).subscribe(
        resp => {
          this.toastService.success("Stop Payment record added successfully");
          this.loadStopPayments(this.lazyLoadEvent);
          this.fileUploadComponent.batchFileForm.reset({ file:[], remarks:"" })
          this.fileUploadComponent.clearAndReset();
        }, 
        error => {
          this.toastService.error(error.error?.error_desc);
        }
      )
    });
  }

  isFileUploadFormValid(): boolean {
    // const test = this.fileUploadComponent.batchFileForm.get('file')?.value;
    return this.fileUploadComponent.batchFileForm.valid;
  }

  onCancel(dialog: any): void {
    dialog.display = false
    this.fileUploadComponent.cancel();
    this.fileUploadComponent.batchFileForm.reset({ file:[], remarks:"" });
  }
}
<div class='flex align-items-stretch justify-content-between gap-3' style='margin-bottom: 1rem'>
  <p-panel class='flex-1' header='Search'>
    <div class='flex align-items-center'>
      <div class='search-label'>NRIC</div>
      <div class='search-input-text'>
        <input type='text' pInputText [(ngModel)]="searchNricInput"/>
      </div>

      <button pButton label='Search' (click)='search()'></button>
    </div>
  </p-panel>

  <p-panel class='flex-1'>
    <ng-template pTemplate="header">
      <span class="p-panel-title">KAH/RNSA Details (As at {{kahRnsaData?.currentDt | date:'dd-MM-yyyy'}})</span>
    </ng-template>
    <div class='flex align-items-center justify-content-between'>
      <div>
        Total Amount: {{kahRnsaData?.totalAmount | currency:'SGD':'symbol-narrow' | defaultString}}
      </div>
      <div>
        No. of Record: {{kahRnsaData?.totalRecords | defaultString}} records
      </div>
      <div>
        <button pButton label='Released History' (click)='onOpen(displayKahRnsaReleaseHistoryDialog)'></button>
        <button pButton label='Release KAH/RNSA' (click)='onOpen(displayKahRnsaReleaseDialog)'></button>
      </div>
    </div>
  </p-panel>
</div>

<p-table #stopPaymentTable
        [value]='stopPaymentArray'
        styleClass='p-datatable-gridlines'
        [paginator]='true' [rows]='5'
        [rowsPerPageOptions]='[10,25,50]'
        [showCurrentPageReport]='true'
        currentPageReportTemplate='Showing {first} to {last} of {totalRecords} entries'
        [lazy]='true'
        [totalRecords]='totalRecords'
        [loading]='loading'
        (onLazyLoad)='loadStopPayments($event)'
        (onPage) = "onPage($event)"
        [(selection)]="selectedRecordsToDelete"
        (selectionChange)="onSelectionChange($event)"
        [selectAll]="selectAll"
        (selectAllChange)="onSelectAllChange($event)">
  <ng-template pTemplate='caption'>
    <div class='grid'>
      <div class='col-6 nsp-align-left flex justify-content-start align-items-center'>
        <button type='button' pButton pRipple icon='pi pi-file-o' (click)='exportCSV()' pTooltip='Export as CSV'
                tooltipPosition='bottom'></button>
        <p-confirmPopup key='deleteAllStopPayment'></p-confirmPopup>
        <button pButton label='Delete All' (click)='onDeleteAllSubmit($event, "deleteAllStopPayment")'
                [style]="{'margin-left': '.5em'}">
        </button>
        <p-confirmPopup key='deleteSelectedStopPayment'></p-confirmPopup>
        <button pButton label='Delete Selection' (click)='onDeleteSubmit($event, "deleteSelectedStopPayment")'
                [disabled]="this.selectedRecordsToDelete.length === 0" [style]="{'margin-left': '.5em'}">
        </button>
      </div>
      <div class='col-6 nsp-align-right'>
        <button pButton label='Insert by NRIC' (click)='onOpen(displayStopPaymentManualInsertDialog)'
                [style]="{'margin-left': '.5em'}">
        </button>
        <button pButton label='Insert by Batch File' (click)='onOpen(displayStopPaymentFileUploadDialog)'
                [style]="{'margin-left': '.5em'}">
        </button>
      </div>
    </div>
  </ng-template>
  <ng-template pTemplate='header'>
    <tr>
      <th scope="col" class='checkbox-header'>
        <p-tableHeaderCheckbox></p-tableHeaderCheckbox>
      </th>
      <th scope="col" class='sn-header'>SN</th>
      <th scope="col" class='exclusion-by-header' pSortableColumn='exclusionDt'>Exclusion Date<p-sortIcon field='exclusionDt'></p-sortIcon>
        <p-columnFilter type="date" field="exclusionDt" display="menu" matchMode="contains"></p-columnFilter>
      </th>
      <th scope="col" class='name-header' pSortableColumn='name'>Name<p-sortIcon field='name'></p-sortIcon>
        <p-columnFilter type="text" field="name" display="menu" matchMode="contains"></p-columnFilter>
      </th>
      <th scope="col" class='unit-code-header' pSortableColumn='unitCode'>Unit Code<p-sortIcon field='unitCode'></p-sortIcon>
        <p-columnFilter type="text" field="unitCode" display="menu" matchMode="contains"></p-columnFilter>
      </th>
      <th scope="col" class='remark-header' pSortableColumn='remarks'>Remark<p-sortIcon field='remarks'></p-sortIcon>
        <p-columnFilter type="text" field="remarks" display="menu" matchMode="contains"></p-columnFilter>
      </th>
    </tr>
  </ng-template>

  <ng-template pTemplate='body' let-stopPaymentRecord let-i='rowIndex'>
    <tr>
      <td>
        <p-tableCheckbox [value]='stopPaymentRecord'></p-tableCheckbox>
      </td>
      <td>{{i + 1}}</td>
      <td>{{stopPaymentRecord?.exclusionDt | date:'dd-MM-yyyy \'(\'HHmm\')\'' |defaultString}}<br
        *ngIf='stopPaymentRecord?.exclusionDt'>{{stopPaymentRecord?.exclusionBy}}</td>
      <td>{{stopPaymentRecord?.nric | defaultString}}<br *ngIf='stopPaymentRecord?.nric'>{{stopPaymentRecord?.name}}
      </td>
      <td>{{stopPaymentRecord?.unitCode | defaultString}}</td>
      <td>
        <div class="grid">
          <div class="col-11 remark-column">
              {{stopPaymentRecord?.remarkBy}} {{stopPaymentRecord?.remarkByDt}}
              <br *ngIf='stopPaymentRecord?.remarkBy'>
              {{stopPaymentRecord?.remarks}}
          </div>
          <div class='col'>
            <button pButton pRipple (click)='onOpenEditRemark(displayStopPaymentEditRemarkDialog, stopPaymentRecord)'
              pTooltip='Edit remark'
              tooltipPosition='bottom'
              icon='pi pi-pencil'>
            </button>
         </div>
        </div>
      </td>
    </tr>
  </ng-template>

  <ng-template pTemplate='emptymessage'>
    <tr>
      <td colspan='6'>No Stop Payment records found.</td>
    </tr>
  </ng-template>
</p-table>

<p-dialog header='KAH/RNSA Release History' [(visible)]='displayKahRnsaReleaseHistoryDialog.display'
          [breakpoints]="{'960px': '75vw'}"
          [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true'>

  <n2-kah-rnsa-release-history #kahRnsaReleaseHistory></n2-kah-rnsa-release-history>
  <ng-template pTemplate='footer'>
  </ng-template>
</p-dialog>

<p-dialog header='Release KAH/RNSA' [(visible)]='displayKahRnsaReleaseDialog.display'
          [breakpoints]="{'960px': '75vw'}"
          [style]="{width: '40vw'}" [draggable]='false' [resizable]='false' [modal]='true'>

  <n2-kah-rnsa-release #kahRnsaRelease></n2-kah-rnsa-release>
  <ng-template pTemplate='footer'>
    <button pButton class='p-button-danger' label='Cancel' (click)='displayKahRnsaReleaseDialog.display=false'
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
    <p-confirmPopup key='confirmReleaseKahRnsa'></p-confirmPopup>
    <button pButton label='Submit' (click)="onReleaseSubmit($event, 'confirmReleaseKahRnsa')"
      [disabled]="!kahRnsaRelease.isFormValid()"
      [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
  </ng-template>
</p-dialog>

<p-dialog header='Upload Stop Payment File' [(visible)]='displayStopPaymentFileUploadDialog.display'
          [breakpoints]="{'960px': '75vw'}"
          [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true'>

  <n2-stop-payment-file-upload #stopPaymentFileUpload></n2-stop-payment-file-upload>

  <ng-template pTemplate='footer'>
    <button pButton class='p-button-danger' label='Cancel'
      (click)='onCancel(displayStopPaymentFileUploadDialog)'
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
    <p-confirmPopup key='stopPaymentFileUpload'></p-confirmPopup>
    <button pButton label='Submit' (click)="onBatchFileUploadSubmit($event, 'stopPaymentFileUpload', displayStopPaymentFileUploadDialog)"
      [disabled]="!isFileUploadFormValid()"
      [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
  </ng-template>
</p-dialog>

<p-dialog header='Add new Stop Payment' [(visible)]='displayStopPaymentManualInsertDialog.display'
          [breakpoints]="{'960px': '75vw'}"
          [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true'>

  <n2-stop-payment-manual-insert #stopPaymentManualInsert></n2-stop-payment-manual-insert>

  <ng-template pTemplate='footer'>
    <button pButton class='p-button-danger' label='Cancel' (click)='displayStopPaymentManualInsertDialog.display=false'
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
    <p-confirmPopup key = "confirmStopPaymentManualInsert"></p-confirmPopup>
    <button pButton label='Submit'
            (click)="onManualInsertSubmit($event, 'confirmStopPaymentManualInsert', displayStopPaymentManualInsertDialog)"
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"
            [disabled]="manualInsertComponent.stopPaymentManualInsertMap?.size <= 0"></button>
  </ng-template>
</p-dialog>

<p-dialog header='Edit Remark' [(visible)]='displayStopPaymentEditRemarkDialog.display'
          [breakpoints]="{'960px': '75vw'}"
          [style]="{width: '50vw'}" [draggable]='false' [resizable]='false' [modal]='true'>

  <n2-edit-remark [maxRemarksChar]=200 #editStopPaymentRemark></n2-edit-remark>

  <ng-template pTemplate='footer'>
    <button pButton class='p-button-danger' label='Cancel' (click)='displayStopPaymentEditRemarkDialog.display=false'
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
    <p-confirmPopup key='confirmEditRemark'></p-confirmPopup>
    <button pButton label='Submit' (click)="onEditRemarkSubmit($event, 'confirmEditRemark', displayStopPaymentEditRemarkDialog)"
            [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
  </ng-template>
</p-dialog>
