import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, combineLatest } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { ParamService } from './param.service';
import { SearchParamModel, ParamModel } from './param.model';
import dayjs from 'dayjs';
import { DATE_FORMAT } from 'app/config/input.constants';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { ParamDeleteDialogComponent } from './param-delete-dialog.component';

@Component({
  selector: 'ic-param',
  templateUrl: './param.component.html',
  styles: [],
})
export class ParamComponent implements OnInit, OnDestroy {
  searchParamCriteria = new SearchParamModel();
  appList: any[] = [];
  params: any[] = [];
  codes: any[] = [];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;
  effectiveDateAsStrDp: any;
  expireDateAsStrDp: any;

  constructor(
    private paramService: ParamService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager,
    protected modalService: NgbModal
  ) {}

  ngOnInit(): void {
    this.handleNavigation();
    this.getAppList();
    this.registerChangeInParams();
  }

  getAppList(): void {
    this.paramService.getAppList().subscribe((data: any[]) => {
      this.appList = data;
    });
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: ParamModel): any {
    return item.id;
  }

  registerChangeInParams(): void {
    this.eventSubscriber = this.eventManager.subscribe('paramListModification', () => this.load());
  }

  load(): void {
    if (!this.searchParamCriteria.appId) {
      this.searchParamCriteria.appId = '';
    }
    if (!this.searchParamCriteria.paramKey) {
      this.searchParamCriteria.paramKey = '';
    }
    if (!this.searchParamCriteria.paramDesc) {
      this.searchParamCriteria.paramDesc = '';
    }

    this.paramService
      .query({
        effectiveDateAsStr: this.convertDate(this.searchParamCriteria.effectiveDateAsStr),
        expireDateAsStr: this.convertDate(this.searchParamCriteria.expireDateAsStr),
        appId: this.searchParamCriteria.appId,
        paramKey: this.searchParamCriteria.paramKey,
        paramDesc: this.searchParamCriteria.paramDesc,
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }

  transition(): void {
    this.router.navigate(['admin/ic-param'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  search(): void {
    this.load();
  }

  sort(): any {
    return [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
  }

  reset(): void {
    this.searchParamCriteria = {};
    this.page = 0;
    this.load();
  }

  delete(param: ParamModel): void {
    const modalRef = this.modalService.open(ParamDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.param = param;
  }

  private convertDate(searchParam: string): string {
    if (searchParam) {
      return dayjs(searchParam).format(DATE_FORMAT);
    }
    return '';
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.load();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    data.forEach((param: any) => {
      this.paramService.getParamTypes().forEach((paramType: any) => {
        if (param.paramType === paramType.codeId) {
          param.paramTypeDesc = paramType.label;
        }
      });
    });
    this.params = data;
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div class="row">
  <div class="col-lg-12">
    <span class="h3" icTranslate="paramAdmin.param.searchParameter">Search Parameter</span>
  </div>
</div>
<hr />
<div class="card" style="margin-bottom: 15px">
  <div class="card-header">
    <span icTranslate="paramAdmin.param.search"> Search Criteria </span>
  </div>
  <div class="card-body" style="padding: 1.25rem">
    <div class="rounded-pill row">
      <label class="col-sm-2 form-control-label" icTranslate="paramAdmin.param.application">Application</label>
      <div class="col-sm-3">
        <select class="form-control" [(ngModel)]="searchParamCriteria.appId">
          <option [ngValue]="null"></option>
          <option *ngFor="let app of appList" [value]="app.appId">{{ app.appName }}</option>
        </select>
      </div>
    </div>

    <div class="rounded-pill row">
      <label class="col-sm-2 form-control-label" for="paramKey" icTranslate="paramAdmin.param.paramKey">Parameter Key:</label>
      <div class="col-sm-3">
        <input class="form-control" name="paramKey" id="paramKey" [(ngModel)]="searchParamCriteria.paramKey" />
      </div>
    </div>

    <div class="rounded-pill row">
      <label class="col-sm-2 form-control-label" for="paramDesc" icTranslate="paramAdmin.param.paramDesc">Parameter Description:</label>
      <div class="col-sm-3">
        <input type="text" class="form-control" name="paramDesc" id="paramDesc" [(ngModel)]="searchParamCriteria.paramDesc" />
      </div>
    </div>

    <div class="rounded-pill row">
      <label class="col-md-2 form-control-label">
        <span icTranslate="paramAdmin.param.effectiveDate">Effective Date</span>
      </label>
      <div class="col-md-3">
        <div class="input-group">
          <input
            type="text"
            class="form-control"
            placeholder="yyyy-mm-dd"
            name="effectiveDateAsStr"
            id="effectiveDateAsStr"
            ngbDatepicker
            #effectiveDateAsStrDp="ngbDatepicker"
            [(ngModel)]="searchParamCriteria.effectiveDateAsStr"
          />
          <span class="input-group-btn">
            <button type="button" class="btn btn-default" (click)="effectiveDateAsStrDp.toggle()">
              <fa-icon [icon]="'calendar-alt'"></fa-icon>
            </button>
          </span>
        </div>
      </div>
    </div>

    <div class="rounded-pill row">
      <label class="col-md-2 form-control-label">
        <span icTranslate="paramAdmin.param.expireDate">Expire Date</span>
      </label>
      <div class="col-md-3">
        <div class="input-group">
          <input
            type="text"
            class="form-control"
            placeholder="yyyy-mm-dd"
            name="expireDateAsStr"
            id="expireDateAsStr"
            ngbDatepicker
            #expireDateAsStrDp="ngbDatepicker"
            [(ngModel)]="searchParamCriteria.expireDateAsStr"
          />
          <span class="input-group-btn">
            <button type="button" class="btn btn-default" (click)="expireDateAsStrDp.toggle()">
              <fa-icon [icon]="'calendar-alt'"></fa-icon>
            </button>
          </span>
        </div>
      </div>
    </div>

    <div style="margin-top: 1.5%">
      <button class="btn btn-warning btn-sm" (click)="search()">
        <fa-icon [icon]="'search'"></fa-icon>
        <span class="fa fa-search" icTranslate="paramAdmin.param.btn.search">Search</span>
      </button>
      <button class="btn btn-secondary btn-sm" (click)="reset()">
        <fa-icon [icon]="'trash-alt'"></fa-icon>
        <span class="fa fa-trash-o" icTranslate="acmAdmin.home.reset">Reset</span>
      </button>
      <button class="btn btn-primary btn-sm" [routerLink]="['./param-new']">
        <fa-icon [icon]="'plus'"></fa-icon>&nbsp;
        <span icTranslate="paramAdmin.param.btn.create">Create</span>
      </button>
    </div>
  </div>
</div>
<ic-alert></ic-alert>
<div class="card" style="margin-bottom: 15px">
  <div class="card-header">
    <span icTranslate="paramAdmin.param.searchResult"> Search Result </span>
  </div>
  <div class="card-body" style="padding: 1.25rem">
    <div class="table-responsive">
      <table class="table table-striped">
        <thead>
          <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
            <th scope="col">
              <span icTranslate="paramAdmin.param.sn">S/No.</span>
            </th>
            <th scope="col" icSortBy="id.appId">
              <span icTranslate="paramAdmin.param.application">Application</span>
              <fa-icon [icon]="'sort'"></fa-icon>
            </th>
            <th scope="col" icSortBy="id.paramKey">
              <span icTranslate="paramAdmin.param.paramKey">Parameter Key</span>
              <fa-icon [icon]="'sort'"></fa-icon>
            </th>
            <th scope="col" icSortBy="paramType">
              <span icTranslate="paramAdmin.param.paramType">Parameter Type</span>
              <fa-icon [icon]="'sort'"></fa-icon>
            </th>
            <th scope="col" icSortBy="paramValue">
              <span icTranslate="paramAdmin.param.paramValue">Parameter Value</span>
              <fa-icon [icon]="'sort'"></fa-icon>
            </th>
            <th scope="col" icSortBy="paramDesc">
              <span icTranslate="paramAdmin.param.paramDesc">Parameter Description</span>
              <fa-icon [icon]="'sort'"></fa-icon>
            </th>
            <th scope="col" icSortBy="effectiveDate">
              <span>Effective Date</span>
              <fa-icon [icon]="'sort'"></fa-icon>
            </th>
            <th scope="col" icSortBy="expireDate">
              <span>Expire Date</span>
              <fa-icon [icon]="'sort'"></fa-icon>
            </th>
            <th scope="col" style="text-align: center">
              <span icTranslate="acmAdmin.home.operation">Operation</span>
            </th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let param of params; trackBy: trackId; index as i">
            <td>{{ itemsPerPage * (page - 1) + i + 1 }}</td>
            <td>{{ param.appId }}</td>
            <td>{{ param.paramKey }}</td>
            <td>{{ param.paramTypeDesc }}</td>
            <td>{{ param.paramValue }}</td>
            <td>{{ param.paramDesc }}</td>
            <td>{{ param.effectiveDate }}</td>
            <td>{{ param.expireDate }}</td>
            <td class="text-end">
              <div class="btn-group flex-btn-group-container">
                <button type="submit" [routerLink]="['/admin/ic-param/', param.appId, param.paramKey]" class="btn btn-info btn-sm">
                  <fa-icon [icon]="'eye'"></fa-icon>
                  <span class="hidden-md-down" icTranslate="paramAdmin.param.btn.view">View</span></button
                >&nbsp;
                <button
                  type="submit"
                  [routerLink]="['/admin/ic-param/' + param.appId + '/' + param.paramKey + '/edit']"
                  [replaceUrl]="true"
                  class="btn btn-primary btn-sm"
                >
                  <fa-icon [icon]="'pencil-alt'"></fa-icon>
                  <span class="hidden-md-down" icTranslate="paramAdmin.param.btn.edit">Edit</span></button
                >&nbsp;

                <button type="submit" (click)="delete(param)" class="btn btn-danger btn-sm">
                  <fa-icon icon="times"></fa-icon>
                  <span class="hidden-md-down" icTranslate="paramAdmin.param.btn.delete">Delete</span>
                </button>
              </div>
            </td>
          </tr>
          <tr *ngIf="params?.length! <= 0">
            <span icTranslate="paramAdmin.param.noMatchingRecords">No Matching Records Found.</span>
          </tr>
        </tbody>
      </table>
    </div>
    <div *ngIf="params && params?.length!">
      <div class="row justify-content-center">
        <ic-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></ic-item-count>
      </div>
      <div class="row justify-content-center">
        <ngb-pagination
          [collectionSize]="totalItems"
          [pageSize]="itemsPerPage"
          [(page)]="page"
          (pageChange)="loadPage(page)"
        ></ngb-pagination>
      </div>
    </div>
  </div>
</div>
