        this.toastService.error(String(error?.error?.error_desc));
      });
    } else if (this.currSelectedOption.changes && !this.currSelectedOption.newWD) {
      this.solePropForm = this.workingDataWithChanges.get(this.currSelectedOption?.obj?.workingDataId).formGroup;
      this.yrmthList = this.solePropForm.get('yrmthList').value;
