  confirmSwitch(e, type) {
    var _this = this;
    return _asyncToGenerator(function* () {
      const value = e.target.value;
      console.log("incoming value", value);
      if (value !== _this.solePropForm.get('captionGroup.radioOption').value) {
        e.preventDefault();
        const confirm = yield _this.showConfirm(e, type);
        if (!confirm) {
          return;
        } else {
