sourcesContent:
import { formatDate } from '@angular/common';
import { HttpParams } from '@angular/common/http';
import { Component, EventEmitter, Inject, Input, LOCALE_ID, OnChanges, OnInit, Output, SimpleChanges, ViewChild } from '@angular/core';
import { AbstractControl, FormArray, FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { AnnualIncomeWD } from 'app/claim/se-claim/model/annual-income-model';
import { SaveSEClaimRequest } from 'app/claim/se-claim/model/save-se-claim-request.model';
import { SolePropSubWD } from 'app/claim/se-claim/model/sole-prop-sub-data.model';
import { SolePropWD } from 'app/claim/se-claim/model/sole-prop.model ';
import { SEClaimService } from 'app/claim/se-claim/service/se-claim.service';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import moment from 'moment';
import { ConfirmationService } from 'primeng/api';
import { Table } from 'primeng/table';
import { Subscription } from 'rxjs';
import { AccountService } from 'app/core/auth/account.service';
import { NSPAY2_ROLES, NSPAY2_STATION } from 'app/nspay2.constants';

@Component({
  selector: 'n2-sole-prop',
  templateUrl: './sole-prop.component.html',
  styleUrls: ['./sole-prop.component.scss']
})
export class SolePropComponent implements OnInit {

  radioOptions: any[] = [{name: 'Monthly', key: 'MTH'}, {name: 'Consolidated', key: 'CON'}];
  sourceYrOptions: any[] = [];
  loading = true;

  currHardCopyDetails: any = {};
  currSelectedOption: any;
  @Input() seHardCopyDetails:any = {};
  @Input() selectedWDOption:any = {};
  @Input() trgStartDte: string;
  @Input() workingDataWithChanges: Map<string, any>;
  @Input() newUnsavedWorkingData: Map<string, any>;
  @Input() saveSEClaimRequest: SaveSEClaimRequest;
  //meant for routing from enquiry
  @Input() readOnly: boolean;
  @Input() station: string;

  subscr:Subscription = new Subscription();

  maxMths = 6;
  cols: any[];
  dateFormat = "DD-MM-YYYY";

  solePropForm: FormGroup  = this.formBuilder.group({
    id: new FormControl(null, null),
    workingDataId: new FormControl('',null),
    consolidated: new FormControl(false, null),
    divisor: new FormControl(6, null),
    annualDivisor: new FormControl(12, null),
    dataFormArray: this.formBuilder.group({}),
    consolDataFormArray: this.formBuilder.group({}),
    headerGroup: this.formBuilder.group({
      yearInput: new FormControl(0, null),
    }),
    captionGroup: this.formBuilder.group({
      radioOption: new FormControl(this.radioOptions[0].key, null)
    }),
    yrmthList:new FormControl([], null),
    startYrmth: new FormControl('', null),
    endYrmth: new FormControl('', null),
    yearInputOptions: new FormControl([], null),
    annualIncomeList: new FormArray([]),

  });

  periodStartDate = "";
  periodEndDate = "";
  yrmthList: string[] = [];

  colMthRange:any[] = [];
  dataSet: any = {
    dataList: [],
    annualList:[]
  }
  yearInputOptions: any[]= [];

  sysProposedAmt = 0;
  nipDifference = 0;
  netIncomeDifference = 0;

  annualNIAvg = 0;
  consolNIAvg = 0;

  annualNIP = 0;
  consolNIP = 0;

  @Input() itemDetailsSection: any = {};
  readonly PEND_VERIFY_STATUS = "PVE";
  readonly PEND_ACTION_STATUS = "PAC";

  @Output()  totalAmtPayEvent: EventEmitter<any> = new EventEmitter<any>();
  @ViewChild('mthlyTable', {static: false})
  private mthlyTable!: Table;
  @ViewChild('consolTable', {static: false})
  private consolTable!: Table;

  constructor(
    private formBuilder: FormBuilder,
    private ns2Http: Ns2HttpService,
    public dateUtilsService: DateUtilsService,
    private seClaimService: SEClaimService,
    private toastService: CustomToastService,
    private confirmationService: ConfirmationService,
    private exportUtilsService: ExportUtilsService,
    public accountService: AccountService,
    @Inject(LOCALE_ID) protected localeId: string

  ) {
    console.log("tests")
   }

   static createNewSolePropForm(startYrmth: string, endYrmth: string, workingDataOption: any,
    dateUtilsService: DateUtilsService, formBuilder: FormBuilder, trgStartDte: any): FormGroup<any> {
    const solePropFormGroup: FormGroup = formBuilder.group({
      id: new FormControl(null, null),
      workingDataId: new FormControl(null,null),
      consolidated: new FormControl(false, null),
      divisor: new FormControl(6, null),
      annualDivisor: new FormControl(12, null),
      headerGroup: formBuilder.group({
        yearInput: new FormControl(0, null),
      }),
      dataFormArray: formBuilder.group({}),
      consolDataFormArray: formBuilder.group({}),
      captionGroup: formBuilder.group({
        radioOption: new FormControl("MTH", null)
      }),
      yrmthList:new FormControl([], null),
      startYrmth: new FormControl(startYrmth, null),
      endYrmth: new FormControl(endYrmth, null),
      yearInputOptions: new FormControl([], null),
      annualIncomeList: new FormArray([])
    });

    const claimStartYrMth:Date =  dateUtilsService.convertStrToDate(solePropFormGroup.get('startYrmth').value, "DD-MM-YYYY");
    const claimEndYrMth:Date =  dateUtilsService.convertStrToDate(solePropFormGroup.get('endYrmth').value, "DD-MM-YYYY");
    const claimStartDt = moment(new Date(claimStartYrMth.getFullYear(), claimStartYrMth.getMonth(),1));
    const claimEndDt = moment(new Date(claimEndYrMth.getFullYear(), +claimEndYrMth.getMonth()+ 1, 0));
    const colStartDt = claimEndDt.clone().subtract(+solePropFormGroup?.get('divisor').value - 1, 'months');
    const colEndDt = claimEndDt.clone();

    const colMthRange = [];
    for(let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')){
      colMthRange.push(mth.format("DD-MM-YYYY"));
    }
    const yrmthList = colMthRange.map(x=>  moment(dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
    solePropFormGroup.get('yrmthList').setValue(yrmthList);
    console.log("the alpha yr nmt", yrmthList);
    //create empty dataFormArray
    //add Annual
    (solePropFormGroup.get('dataFormArray') as FormGroup).addControl('annual', formBuilder.group({
      yrmthReCon: new FormControl(null, null),
      yrmth: new FormControl(null, null),
      revenue: new FormControl(null, null),
      netProfit: new FormControl(null, null),
      type: new FormControl('1', null),
    }));
    yrmthList.forEach(x=>{
      (solePropFormGroup.get('dataFormArray') as FormGroup).addControl(x, formBuilder.group({
        yrmthReCon: new FormControl(x, null),
        yrmth: new FormControl(x, null),
        revenue: new FormControl(null, null),
        netProfit: new FormControl(null, null),
        type: new FormControl('3', null),
      }));
    });

    //create empty consolDataFormArray
    //add Annual
    (solePropFormGroup.get('consolDataFormArray') as FormGroup).addControl('annual', formBuilder.group({
      yrmthReCon: new FormControl(null, null),
      yrmth: new FormControl(null, null),
      revenue: new FormControl(null, null),
      netProfit: new FormControl(null, null),
      type: new FormControl('1', null),
      year: new FormControl(2014, null),
    }));
    //add consolidated
    (solePropFormGroup.get('consolDataFormArray') as FormGroup).addControl('consolidated', formBuilder.group({
      yrmthReCon: new FormControl(null, null),
      yrmth: new FormControl(null, null),
      revenue: new FormControl(null, null),
      netProfit: new FormControl(null, null),
      type: new FormControl('2', null),
    }));


    const date1 = +dateUtilsService.convertMomentToStr(moment(dateUtilsService.convertStrToDate(trgStartDte, "DD-MM-YYYY")).subtract(1,'years'), "YYYY");
    const date2 = +dateUtilsService.convertMomentToStr(moment(dateUtilsService.convertStrToDate(trgStartDte, "DD-MM-YYYY")), "YYYY");
    const yearInputOptions = [
      {name:  date1, key: date1},
      {name:  date2, key: date2},
    ]
    //if no value from api or the claim section start date has been changed, default to first option
    solePropFormGroup.get('headerGroup.yearInput')?.setValue(yearInputOptions[1].key);
    solePropFormGroup.get('yearInputOptions')?.setValue(yearInputOptions);

    //add default annualIncomeList
    const annualIncomeFormGroup: FormGroup = formBuilder.group({
      source: new FormControl(null, null),
      assessment: new FormControl(null, null),
      acctgPeriodStrt: new FormControl(null, null),
      acctgPeriodEnd: new FormControl(null, null),
      occupationNature: new FormControl(null, null),
      revenue: new FormControl(null, null),
      adjProfit: new FormControl(null, null),
    });
    (solePropFormGroup.get('annualIncomeList') as FormArray).push(annualIncomeFormGroup);
    return solePropFormGroup;
  }

  static mapSolePropWDToFormGroup(solePropWD:SolePropWD, dateUtilsService: DateUtilsService, formBuilder: FormBuilder): FormGroup {
    const solePropFormGroup = formBuilder.group({
      workingDataId: new FormControl(solePropWD.workingDataId),
      id: new FormControl(solePropWD.id, null),
      consolidated: new FormControl(solePropWD.consolidated, null),
      divisor: new FormControl(solePropWD.divisor, null),
      annualDivisor: new FormControl(solePropWD.annualDivisor, null),
      headerGroup: formBuilder.group({
        yearInput: new FormControl(0, null),
      }),
      dataFormArray: formBuilder.group({}),
      consolDataFormArray: formBuilder.group({}),
      captionGroup: formBuilder.group({
        radioOption: new FormControl(solePropWD.consolidated? "CON": "MTH", null)
      }),
      yrmthList:new FormControl([], null),
      startYrmth: new FormControl(solePropWD.startYrmth, null),
      endYrmth: new FormControl(solePropWD.endYrmth, null),
      yearInputOptions: new FormControl([], null),
      annualIncomeList: new FormArray([]),

    });
    const annualPartnerSubWD = solePropWD?.dataList?.filter(x=> x.type === '1')[0];
    solePropFormGroup.get('headerGroup.yearInput').setValue(annualPartnerSubWD?.year);

    const claimEndYrMth:Date =  dateUtilsService.convertStrToDate(solePropFormGroup.get('endYrmth').value, "DD-MM-YYYY");
    const claimEndDt = moment(new Date(claimEndYrMth.getFullYear(), +claimEndYrMth.getMonth()+ 1, 0));
    const colStartDt = claimEndDt.clone().subtract(+solePropFormGroup?.get('divisor').value - 1, 'months');
    const colEndDt = claimEndDt.clone();

    const colMthRange = [];
    for(let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')){
      colMthRange.push(mth.format("DD-MM-YYYY"));
    }
    const yrmthList = colMthRange.map(x=>  moment(dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
    solePropFormGroup.get('yrmthList').setValue(yrmthList);

    let dataRecon:any[] = [];
    const newDataList:any[] = [];
    if(solePropWD.consolidated){
       //expect 1
       if(solePropWD.dataList){
        solePropFormGroup?.get('captionGroup.radioOption').setValue('CON')
        dataRecon = SolePropComponent.staticConstructConsolidatedHeaderData(solePropWD.dataList, true, false, dateUtilsService, yrmthList);
        SolePropComponent.constructForm(dataRecon, solePropFormGroup.get('consolDataFormArray') as FormGroup, false, formBuilder, solePropFormGroup);
        // construct empty for mthly view
        dataRecon = SolePropComponent.staticConstructConsolidatedHeaderData(newDataList, false, true, dateUtilsService, yrmthList);
        SolePropComponent.constructForm(newDataList, solePropFormGroup.get('dataFormArray') as FormGroup, true, formBuilder, solePropFormGroup);

       }
    }else {
      if(solePropWD.dataList){
        solePropFormGroup?.get('captionGroup.radioOption').setValue('MTH')
        //list of mthly records

        dataRecon = SolePropComponent.staticConstructConsolidatedHeaderData(solePropWD.dataList, false, false, dateUtilsService, yrmthList);
        SolePropComponent.constructForm(dataRecon, solePropFormGroup.get('dataFormArray') as FormGroup, false, formBuilder, solePropFormGroup);

        //construct empty for consolidated view
        dataRecon = SolePropComponent.staticConstructConsolidatedHeaderData(newDataList, true, true, dateUtilsService, yrmthList);
        SolePropComponent.constructForm(newDataList, solePropFormGroup.get('consolDataFormArray') as FormGroup, true, formBuilder, solePropFormGroup);

      }
    }

    //set eligible mth divisor and prdn bonus divisor
    solePropFormGroup.get('divisor').setValue(solePropWD.divisor);
    solePropFormGroup.get('annualDivisor').setValue(solePropWD.annualDivisor);
    solePropFormGroup.get('id').setValue(solePropWD.id);
    solePropFormGroup.get('workingDataId').setValue(solePropWD.workingDataId);
    solePropFormGroup.get('startYrmth').setValue(solePropWD.startYrmth);
    solePropFormGroup.get('endYrmth').setValue(solePropWD.endYrmth);
    solePropFormGroup.get('consolidated').setValue(solePropWD.consolidated);

    solePropWD.annualIncomeList?.forEach((annualIncomeWD: AnnualIncomeWD) => {
      const annualIncomeFormGroup: FormGroup = formBuilder.group({
        source: new FormControl(annualIncomeWD.source, null),
        assessment: new FormControl(annualIncomeWD.assessment === null? null :dateUtilsService.convertStrToDate(annualIncomeWD.assessment, "DD-MM-YYYY"), null),
        acctgPeriodStrt: new FormControl(annualIncomeWD.acctgStartDate === null? null :dateUtilsService.convertStrToDate(annualIncomeWD.acctgStartDate, "DD-MM-YYYY"), null),
        acctgPeriodEnd: new FormControl(annualIncomeWD.acctgEndDate === null? null :dateUtilsService.convertStrToDate(annualIncomeWD.acctgEndDate, "DD-MM-YYYY"), null),
        occupationNature: new FormControl(annualIncomeWD.occupationNature, null),
        revenue: new FormControl(annualIncomeWD.revenue, null),
        adjProfit: new FormControl(annualIncomeWD.adjProfit, null),
      });
      (solePropFormGroup.get('annualIncomeList') as FormArray).push(annualIncomeFormGroup);
    });

    return solePropFormGroup;
  }

   static constructForm(dataList: any[], arrformGroup: FormGroup, empty: boolean, formBuilder: FormBuilder, solePropForm: FormGroup): void {
    SolePropComponent.staticConstructInitialAnnualOptions(dataList, empty, solePropForm);
    let yearInput;

    if(arrformGroup === solePropForm.get('consolDataFormArray')){
      dataList.forEach(x=>{
        //if annual type
        if(x.type === '1'){
          console.log("migata", x.year)
          arrformGroup.addControl('annual',SolePropComponent.addRow(x, formBuilder));
          yearInput = x.year;
        }else if(x.type === '2'){
          //if consolidated type
          arrformGroup.addControl('consolidated',SolePropComponent.addRow(x,formBuilder));
        }
      })
      console.log("the consol form", arrformGroup);

    }else{
      dataList.forEach(x=>{
        //if annual type
        if(x.type === '1'){
          arrformGroup.addControl('annual',SolePropComponent.addRow(x, formBuilder));
          yearInput = x.year;
        }else if(x.type === '3'){
          //if mthly type
          arrformGroup.addControl(x?.yrmthReCon,SolePropComponent.addRow(x, formBuilder));
        }
      })
      console.log("the mthly form", arrformGroup);

    }
    if(yearInput){
      solePropForm?.get('headerGroup').get('yearInput').setValue(yearInput);
    }
  }

  static staticConstructInitialAnnualOptions(dataList: any[],  empty: boolean, solePropForm: FormGroup): void{
    const annualData = dataList?.filter(x=> x.type==='1')[0];
    if(!empty){
      console.log("annual is ", annualData);
      if(annualData.year!==undefined && !(solePropForm.get('yearInputOptions').value as any[]).find(y=>y.name === annualData.year)){
        const yearInputOptions = solePropForm.get('yearInputOptions').value;
        yearInputOptions.push({key:annualData.year, name:annualData.year});
        solePropForm.get('yearInputOptions').setValue(yearInputOptions);
      }
    }
  }

  static staticConstructConsolidatedHeaderData(dataList: any[], consolidated: boolean, empty: boolean, dateUtilsService: DateUtilsService, yrmthList: any[]): any[]{
    //initialize with 1 column annual income only
    let sortedData;
    const annualData = dataList.filter(x=> x.type==='1');
    if(annualData.length === 0){
      dataList.push({
        type: '1'
      })
    }

    let sanitizedDataList:any[] =[];

    if(consolidated || empty ){
      yrmthList.forEach((mth:any, colIndex:number)=>{
        dataList.push({
          type: '2'
        })
        dataList.push(
          {yrmthReCon: mth,
          type: '3'
          });
        });
      sortedData = dataList;
    }else{
      sanitizedDataList = dataList.filter(x=> (yrmthList.includes(x.yrmth) && x.type === '3') || x.type==='1');
      yrmthList.forEach(yrmth =>{
        const index = sanitizedDataList.findIndex(x=> x.yrmth ===  yrmth && x.type!=='1');
        if(index<0){
          sanitizedDataList.push({yrmthReCon: yrmth, type: '3'});
        }else{
          sanitizedDataList[index].yrmthReCon = yrmth;
        }
      })
      console.log("after data", sanitizedDataList);
      sortedData = sanitizedDataList;

    }
    return sortedData;

   }


