  static mapSolePropFormGroupToWD(fg: FormGroup, obj: any,  dateUtilsService: DateUtilsService):SolePropWD {
    const annualIncomeDtoList: AnnualIncomeWD[] = [];
    const dataList: SolePropSubWD[] = [];
    const solePropWD: SolePropWD = {
      id: fg.get('id')?.value,
      workingDataId: obj.workingDataId,
      year: fg?.get('year')?.value || obj.year,
      consolidated: fg?.get('consolidated')?.value,
      archiveDt: obj.archiveDt,
      dataList,
      divisor: fg.get('divisor').value,
      annualDivisor: fg.get('annualDivisor').value,
      workingDataTypeCode: obj.workingDataTypeCode,
      startYrmth: fg.get('startYrmth').value,
      endYrmth: fg.get('endYrmth').value,
      annualIncomeList: annualIncomeDtoList
    };
    const yrmthList =  fg.get('yrmthList').value as Array<string>;
    const subYrMthList = yrmthList.slice(6- fg.get('divisor').value);
    console.log('sub yr mmth',subYrMthList);
    if(fg!==undefined && fg!==null){
      let yrmthFG;
      let subWD: SolePropSubWD;
      if(fg.get('consolidated').value) {
        yrmthFG = ((fg.get('consolDataFormArray') as FormGroup)).get('annual');
        subWD = {
          revenue: yrmthFG.get("revenue").value,
          netProfit: yrmthFG.get("netProfit").value,
          type: "1",
          year:  fg.get("headerGroup.yearInput").value
        }
        dataList.push(subWD);
        yrmthFG = ((fg.get('consolDataFormArray') as FormGroup)).get('consolidated');
        subWD = {
          revenue: yrmthFG.get("revenue").value,
          netProfit: yrmthFG.get("netProfit").value,
          type: "2",
        }
        dataList.push(subWD);

      }else {
        //mthly
        yrmthFG = ((fg.get('dataFormArray') as FormGroup)).get('annual');
        subWD = {
          revenue: yrmthFG.get("revenue").value,
          netProfit: yrmthFG.get("netProfit").value,
          type: "1",
          year:  fg.get("headerGroup.yearInput").value
        }
        dataList.push(subWD);

        subYrMthList.forEach(key=>{
          yrmthFG = fg.get('dataFormArray').get(key);
          subWD = {
            revenue: yrmthFG.get("revenue").value,
            netProfit: yrmthFG.get("netProfit").value,
            type: "3",
            yrmth: yrmthFG.get("yrmthReCon").value,
          }
          dataList.push(subWD);
          }
        );
      }
    }

    const annualIncomeListFA = (fg.get('annualIncomeList') as FormArray);
    annualIncomeListFA.controls.forEach((control: AbstractControl)=>{
      const annualIncomeDto: AnnualIncomeWD = {
        workingDataId: obj.workingDataId,
        seq: annualIncomeDtoList.length + 1,
        source: control.get('source').value,
        assessment: dateUtilsService.isValid(control.get('assessment').value)? dateUtilsService.convertDateToStr(control.get('assessment').value, "DD-MM-YYYY"): null,
        acctgStartDate: dateUtilsService.isValid(control.get('acctgPeriodStrt').value)? dateUtilsService.convertDateToStr(control.get('acctgPeriodStrt').value, "DD-MM-YYYY"): null,
        acctgEndDate: dateUtilsService.isValid(control.get('acctgPeriodEnd').value)? dateUtilsService.convertDateToStr(control.get('acctgPeriodEnd').value, "DD-MM-YYYY"): null,
        occupationNature: control.get('occupationNature').value,
        revenue: control.get('revenue').value,
        adjProfit: control.get('adjProfit').value,
      }
      annualIncomeDtoList.push(annualIncomeDto);
    })


    console.log("whole saved is", solePropWD)
    return solePropWD;
  }

