  constructPeriodStartEndDate():void{
    const claimStartDt = this.dateUtilsService.convertStrToDate(this.currHardCopyDetails.trgStartDte, "DD-MM-YYYY");
    this.periodEndDate = this.dateUtilsService.convertMomentToStr(moment(claimStartDt).subtract(1,'months'), "MM-YYYY");
    this.periodStartDate = this.dateUtilsService.convertMomentToStr(moment(claimStartDt).subtract(this.solePropForm.get("divisor").value,'months'), "MM-YYYY");
