  generateYrMth(dataSet: any): void{
    //base range is 6 mths from backend, other wise is 6 mths from claim start mth - 6 to claim start mth -1
    let claimStartDt;
    let  claimEndDt;
    if(dataSet){
      console.log("dataSet", dataSet)
      const claimStartYrMth:Date =  this.dateUtilsService.convertStrToDate(dataSet?.startYrmth, this.dateFormat);
      const claimEndYrMth:Date =  this.dateUtilsService.convertStrToDate(dataSet.endYrmth, this.dateFormat);
      claimStartDt = moment(new Date(claimStartYrMth.getFullYear(), claimStartYrMth.getMonth(),1));
      claimEndDt = moment(new Date(claimEndYrMth.getFullYear(), +claimEndYrMth.getMonth()+ 1, 0));
      const colStartDt = claimEndDt.clone().subtract(+this.solePropForm?.get('divisor').value - 1, 'months');
      const colEndDt = claimEndDt.clone();

      this.colMthRange = [];
      for(let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')){
        this.colMthRange.push(mth.format("DD-MM-YYYY"));
      }
      this.yrmthList = this.colMthRange.map(x=>  moment(this.dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
      console.log("yr mth list", this.yrmthList)
    }

    //adds more form control if needed
    if(this.trgStartDte!==undefined && this.trgStartDte !== null){
      console.log("incomign trg date", this.trgStartDte)
      claimStartDt = this.dateUtilsService.convertStrToMoment(this.trgStartDte, this.dateFormat).subtract(6, 'months');
      claimEndDt =     this.dateUtilsService.convertStrToMoment(this.trgStartDte, this.dateFormat).subtract(1, 'months');

      const colStartDt = claimStartDt.clone();
      const colEndDt = claimEndDt.clone();

      this.colMthRange = [];
      for(let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')){
        this.colMthRange.push(mth.format("DD-MM-YYYY"));
      }
      this.yrmthList = this.colMthRange.map(x=>  moment(this.dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
      console.log("yr mth list", this.yrmthList)

      this.adjustFormArray();


    }
    const cloneYrmthList = JSON.parse(JSON.stringify(this.yrmthList))
    console.log("clone is", cloneYrmthList)
    this.solePropForm.get('yrmthList').setValue(cloneYrmthList);
    this.solePropForm.get('startYrmth').setValue("01-".concat(cloneYrmthList[0]));
    this.solePropForm.get('endYrmth').setValue("01-".concat(cloneYrmthList[cloneYrmthList.length -1]));

  }


  adjustFormArray():void{
    const existingYrMthControls =  Object.keys(this.dataFormArray.controls);
    const diffColHeaders: string[] = this.yrmthList.filter( x => !existingYrMthControls.includes(x));
    if(this.dataFormArray.controls){

      let formObj;
      diffColHeaders.forEach(yrmth=>{
        formObj = {
          yrmth,
          yrmthReCon: new FormControl(yrmth, null),
          revenue: new FormControl(null, null),
          netProfit: new FormControl(null, null),
          type: new FormControl('3', null)
        }
        this.dataFormArray.addControl(yrmth, this.formBuilder.group(formObj));
      });
    }
  }

  retrieveSolePropWD():void{

    if((!this.currSelectedOption.changes && !this.currSelectedOption.newWD)) {

    this.solePropForm = this.formBuilder.group({
      id: new FormControl(null, null),
      workingDataId: new FormControl('',null),
      consolidated: new FormControl(false, null),
      divisor: new FormControl(6, null),
      annualDivisor: new FormControl(12, null),
      dataFormArray: this.formBuilder.group({}),
      consolDataFormArray: this.formBuilder.group({}),
      headerGroup: this.formBuilder.group({
        yearInput: new FormControl('', null),
      }),
      captionGroup: this.formBuilder.group({
        radioOption: new FormControl(this.radioOptions[0].key, null)
      }),
      yrmthList:new FormControl([], null),
      startYrmth: new FormControl('', null),
      endYrmth: new FormControl('', null),
      yearInputOptions: new FormControl([], null),
      annualIncomeList: new FormArray([]),
    });

    let params = new HttpParams();
    params = params.append("workingDataId", this.currSelectedOption?.obj.workingDataId);
    this.seClaimService.retrieveSolePropPartnerWD({params}).subscribe((resp:any) => {

      if(resp){
          if(resp.status === 200 && resp.body.data){
            this.generateYrMth(resp.body.data);
            this.populateSolePropForm(resp.body.data, false);
            this.valueChangeSubscribe();
          }
        }
      },
      (error: any) => {
        console.log("error is" + String(error?.error?.error_desc));
        this.toastService.error(String(error?.error?.error_desc));
      });

    }else if(this.currSelectedOption.changes && !this.currSelectedOption.newWD) {
      this.solePropForm = this.workingDataWithChanges.get(this.currSelectedOption?.obj?.workingDataId).formGroup as FormGroup;
      this.yrmthList = this.solePropForm.get('yrmthList').value;
      console.log("calling from exist", this.workingDataWithChanges.get(this.currSelectedOption?.obj?.workingDataId).formGroup)

      this.setRadioOption(this.solePropForm.get('consolidated').value);
      console.log("get from changes WD Map", this.solePropForm)
      this.loading = false;
    }else if(this.currSelectedOption.newWD) {
      console.log("selected key",this.currSelectedOption?.key.toString())
      this.solePropForm = this.newUnsavedWorkingData.get(this.currSelectedOption?.key.toString()).formGroup as FormGroup;
      console.log("get from new WD Map", this.newUnsavedWorkingData.get(this.currSelectedOption?.key.toString()).formGroup);

      if(this.solePropForm.get('consolidated')){
        this.setRadioOption(this.solePropForm.get('consolidated').value);
        //defaulted to monthly
      }else this.setRadioOption(false);

      this.loading = false;

    }
  }

  populateSolePropForm(dataSet: any, newForm: boolean): FormGroup{
    this.dataSet = dataSet;
    const newDataList: any[] = [];
    let dataRecon:any[] = [];
    if(this.dataSet.consolidated === true){
      //expect 1
      if(this.dataSet.dataList){
        this.solePropForm.get('captionGroup.radioOption').setValue('CON');
        dataRecon = SolePropComponent.staticConstructConsolidatedHeaderData(this.dataSet.dataList, true, false, this.dateUtilsService, this.solePropForm.get('yrmthList').value);
        SolePropComponent.constructForm(dataRecon, this.consolDataFormArray, false, this.formBuilder, this.solePropForm);

        // construct empty for mthly view
        SolePropComponent.staticConstructConsolidatedHeaderData(newDataList, false, true, this.dateUtilsService, this.solePropForm.get('yrmthList').value);
        SolePropComponent.constructForm(newDataList, this.dataFormArray, true, this.formBuilder, this.solePropForm);

      }
    }else{
      if(this.dataSet.dataList){
        console.log("imhere")
        this.solePropForm.get('captionGroup.radioOption').setValue('MTH');
        //list of mthly records
        dataRecon = SolePropComponent.staticConstructConsolidatedHeaderData(this.dataSet.dataList, false, false, this.dateUtilsService, this.solePropForm.get('yrmthList').value);
        SolePropComponent.constructForm(dataRecon, this.dataFormArray, false, this.formBuilder, this.solePropForm);

        //construct empty for consolidated view
        SolePropComponent.staticConstructConsolidatedHeaderData(newDataList, true, true, this.dateUtilsService, this.solePropForm.get('yrmthList').value);
        SolePropComponent.constructForm(newDataList, this.consolDataFormArray, true, this.formBuilder, this.solePropForm);
      }
    }
    //set eligible mth divisor and prdn bonus divisor
    this.solePropForm.get('divisor').setValue(dataSet.divisor);
    this.solePropForm.get('annualDivisor').setValue(dataSet.annualDivisor);
    this.solePropForm.get('id').setValue(dataSet.id);
    this.solePropForm.get('workingDataId').setValue(dataSet.workingDataId);
    this.solePropForm.get('startYrmth').setValue(dataSet.startYrmth);
    this.solePropForm.get('endYrmth').setValue(dataSet.endYrmth);
    this.solePropForm.get('consolidated').setValue(this.dataSet.consolidated);
    SolePropComponent.populateAnnualIncomeList(dataSet, this.formBuilder,this.dateUtilsService, this.solePropForm);
    this.loading = false;
    return this.solePropForm;
  }




