  getSumOfCurrRow(formCtrlName: string): number
  {
    let sum = 0;
    let fieldCtrl;
    this.getYrmthList().slice(this.maxMths - this.solePropForm?.get('divisor').value, this.maxMths).forEach(yrmth => {//for each mth
      fieldCtrl = this.dataFormArray.get(yrmth).get(formCtrlName).value|| 0;  // fieldCtrl is field level, i.e. revenue
      sum+=fieldCtrl;
    });
    return sum;
  }

