  async confirmSwitch(e: Event,  type: string) {
    const value = (e.target as HTMLInputElement).value;
    console.log("incoming value",value)
    if (value !== this.solePropForm.get('captionGroup.radioOption').value) {
      e.preventDefault();
      const confirm = await this.showConfirm(e,type);
      if (!confirm) {
        return;
      }else{
        this.solePropForm.get('captionGroup.radioOption').setValue(value);
        this.solePropForm.get('consolidated').setValue(value === 'CON')
        this.clearForm(value);
      }
    }
  }

  showConfirm(e: Event, type: string) {
    return new Promise((resolve) => {
      this.confirmationService.confirm({
        target: e.target,
        message: 'All Working Data fields will be cleared',
        header: 'Confirmation',
        key: type,
        icon: 'pi pi-exclamation-triangle',
        acceptLabel: 'Proceed',
        rejectLabel: 'Cancel',
        accept() {
          return resolve(true)
        },
      });
    });
  }

  clearForm(radioKey: string): void{
    let yrmthFG:AbstractControl;
    if(radioKey === "MTH"){
      Object.keys(this.consolDataFormArray.controls).forEach(key=>{
        yrmthFG = this.consolDataFormArray.get(key);
        yrmthFG.get('revenue').setValue(null);
        yrmthFG.get('netProfit').setValue(null);
      })

    }else{
      Object.keys(this.dataFormArray.controls).forEach(key=>{
        console.log("key is", key)
        yrmthFG = this.dataFormArray.get(key);
        yrmthFG.get('revenue').setValue(null);
        yrmthFG.get('netProfit').setValue(null);
      })
    }
  }

  refreshOtherClaim(): void{
    let params = new HttpParams();
    params = params.append("nric", this.currHardCopyDetails?.headerDto.nsManNric);
    params = params.append("yrmthList", JSON.stringify(this.getYrmthList()));
    params = params.append("caseId", this.currHardCopyDetails?.headerDto.caseId);
    params = params.append("workingDataTypeCode", this.selectedWDOption?.obj.workingDataTypeCode);



    this.seClaimService.retrieveRefreshOtherSPPClaims({params}).subscribe((resp:any) => {
      if(resp){
        if(resp.status === 200 && resp.body.data){
          const refreshedArr:string[] = [];
          resp.body.data.forEach((y:any)=> {
            const ctrl = this.dataFormArray.get(y.yrmth);
            this.refreshOtherClaimSetValue(ctrl, y);
            refreshedArr.push(y.yrmth);
          });
          const existingYrMthControls =  Object.keys(this.dataFormArray.controls);
          const unrefreshed: string[] = refreshedArr.filter( x => !existingYrMthControls.includes(x));
          unrefreshed.forEach(unrefreshMths=> {
            this.refreshOtherClaimSetValue(this.dataFormArray.get(unrefreshMths), null);
          })
        }
      }
    });
  }

  refreshOtherClaimSetValue(ctrl: AbstractControl, data: any): void{
    if(ctrl){
      ctrl.get('revenue').setValue(data.revenue || null);
      ctrl.get('netProfit').setValue(data.netProfit|| null);
    }
  }

  enableRefreshOClaim():boolean{
    return this.captionGroup.get('radioOption').value==='MTH';
  }

  sortByAnnualandYrmthRecon(){
    return function (a: any, b: any) {
      if(a.type < b.type){
        return -1;
      }if(a.type > b.type){
        return 1;
      }else {
        //else 3
        return moment(a.yrmthReCon,"MM-YYYY") < moment(b.yrmthReCon,"MM-YYYY") ? -1 : 1;
      }
    }
  }

  copyToItem(event: any):void{
    this.totalAmtPayEvent.emit(this.sysProposedAmt);
  }

  enableCopytoItem(): boolean{
    const claimItemList = this.itemDetailsSection.itemForm?.get('otherSEItemArr') as FormArray;
    let itxItem = null;
    if(claimItemList.length>0){
      itxItem = claimItemList?.controls.filter(x=> x.get('itemType').value === 'AVE')[0];
      return itxItem?.get('latestStatus').value === this.PEND_VERIFY_STATUS || itxItem?.get('latestStatus').value === this.PEND_ACTION_STATUS;
    }else return false;
  }

  getSysProposedAmt(): number{
    this.sysProposedAmt =  this.solePropForm?.get('captionGroup.radioOption').value==='MTH'? (this.getSumOfCurrRow('netProfit'))/this.solePropForm?.get('divisor').value :
    +this.consolDataFormArray.get('consolidated')?.get('netProfit').value/this.solePropForm.get('divisor').value;
    return this.sysProposedAmt;
  }


  roundVal (value: number) {
    return Math.round(value);
  }

  getFormControlValue(fg: FormGroup, formCtrlName: string): any{
    return fg.get(formCtrlName).value;
  }

  getFormArray(fg: FormGroup,formCtrlName: string): FormArray{
    return fg.get(formCtrlName) as FormArray;
  }

  getYrmthList(): string[]
  {
    return this.solePropForm.get('yrmthList').value as string[];
  }


  getNIPDiff(): number{
    let diff = 0;
    let annualNIP = 0;
    let consolNIP = 0;
    if(this.solePropForm?.get('captionGroup.radioOption').value==='CON'){
      annualNIP = isFinite((this.consolDataFormArray.get('annual')?.get('netProfit').value || 0)/ (this.consolDataFormArray.get('annual')?.get('revenue').value) * 100 || 0)?
      (this.consolDataFormArray.get('annual')?.get('netProfit').value || 0)/ (this.consolDataFormArray.get('annual')?.get('revenue').value) * 100 || 0 :  0;
      consolNIP = isFinite((this.dataFormArray.get('consolidated')?.get('netProfit').value || 0)/ (this.dataFormArray.get('consolidated')?.get('revenue').value) * 100 || 0)?
      (this.consolDataFormArray.get('consolidated')?.get('netProfit').value || 0)/ (this.consolDataFormArray.get('consolidated')?.get('revenue').value) * 100 || 0 :  0;
    }else{
      annualNIP = isFinite((this.dataFormArray.get('annual')?.get('netProfit').value || 0)/ (this.dataFormArray.get('annual')?.get('revenue').value) * 100 || 0)?
      (this.dataFormArray.get('annual')?.get('netProfit').value || 0)/ (this.dataFormArray.get('annual')?.get('revenue').value) * 100 || 0 :  0;
      consolNIP = isFinite((this.getSumOfCurrRow('netProfit') /  this.getSumOfCurrRow('revenue') * 100))? (this.getSumOfCurrRow('netProfit') /  this.getSumOfCurrRow('revenue') * 100):  0;
    }
    this.consolNIP = this.roundVal(consolNIP);
    this.annualNIP  =this.roundVal(annualNIP);
    diff = this.getAbsoluteDiff(this.annualNIP, this.consolNIP);
    return diff;
  }


  getNetIncomeDiff(): number{
    let diff = 0;
    let annualTotalNI = 0;
    let consolTotalNI = 0;
    if(this.solePropForm.get('captionGroup.radioOption').value==='CON'){

      annualTotalNI =  this.consolDataFormArray.get('annual')?.get('netProfit').value/this.solePropForm.get('annualDivisor').value;
      consolTotalNI =  this.consolDataFormArray.get('consolidated')?.get('netProfit').value/this.solePropForm.get('divisor').value;
    }else{
      annualTotalNI = this.dataFormArray.get('annual')?.get('netProfit').value/this.solePropForm.get('annualDivisor').value;
      consolTotalNI = (this.getSumOfCurrRow('netProfit'))/ this.solePropForm.get('divisor').value;
    }
    this.annualNIAvg = annualTotalNI;
    this.consolNIAvg = consolTotalNI;
    diff = this.roundVal(this.getAbsoluteDiff(annualTotalNI, consolTotalNI));
    return diff;
  }


  getAbsoluteDiff(x:number, y: number): number{
    return Math.abs(x-y);
  }
  isValidNumber(obj: any): boolean{
    return !isNaN(obj);
  }

  exportToCSV(dataTable: Table): void{
    console.log("export to csv");
    const columns =
    [{field: 'rowHeader', header: "", index: 0, customExportHeader: ""},
    {field: 'annualAccount', header: 'Annual  Account'.concat(this.solePropForm.get("headerGroup.yearInput").value), index: 1},
    {field: 'aveAnnualAccount', header: 'Ave Monthly', index: 2},
    {field: 'mthAccount', header: String(this.solePropForm.get('divisor').value).concat('-mth Account'), index: 3},
    {field: 'aveMthAccount', header: 'Ave Monthly', index: 4}]
    const subsetYrmth = this.getYrmthList().slice( (this.maxMths - this.solePropForm?.get('divisor').value));
    for(let i = 0; i< subsetYrmth.length; i++ ){
      columns.push({field: String(subsetYrmth[i]), header:String(subsetYrmth[i]), index: i})
    }

    const dtoList:any[] = [
      {rowHeader: 'Revenue'},
      {rowHeader: 'Net Profit'},
      {rowHeader: 'Entity NIP (Percentage %)'}
    ];
    const dataList: SolePropSubWD[] = [];
    let yrmthFG;
    let subWD: SolePropSubWD;

    if(this.solePropForm?.get('captionGroup.radioOption').value==='CON'){
      this.exportAddConsol(yrmthFG, dataList, subWD, dtoList);
    }else{
      this.exportAddMthly(yrmthFG, dataList, subWD, subsetYrmth, dtoList);
    }
    this.exportUtilsService.exportCSV(dataTable, dtoList,10, columns,
      "sole_prop_data_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
  }

  exportAddConsol(yrmthFG: any, dataList: SolePropSubWD[], subWD: SolePropSubWD, dtoList: any[]){
    yrmthFG = ((this.solePropForm.get('consolDataFormArray') as FormGroup)).get('annual');
    subWD = {
      revenue: yrmthFG.get("revenue").value,
      netProfit: yrmthFG.get("netProfit").value,
      type: "1",
      year:  this.solePropForm.get("headerGroup.yearInput").value
    }
    dataList.push(subWD);
    yrmthFG = ((this.solePropForm.get('consolDataFormArray') as FormGroup)).get('consolidated');
    subWD = {
      revenue: yrmthFG.get("revenue").value,
      netProfit: yrmthFG.get("netProfit").value,
      type: "2",
    }
    dataList.push(subWD);

     //add annual
     dataList.forEach(x=>{
      if(x.type === "1"){
        dtoList[0]['annualAccount'] = x.revenue;
        dtoList[1]['annualAccount'] = x.netProfit;
        dtoList[2]['annualAccount'] = String(+x.netProfit/ +x.revenue * 100).concat("%") ;//Entity NIP

        dtoList[0]['aveAnnualAccount'] = x.revenue / this.solePropForm?.get('annualDivisor').value;
        dtoList[1]['aveAnnualAccount'] = x.netProfit/ this.solePropForm?.get('annualDivisor').value;
        dtoList[2]['aveAnnualAccount'] = '';
      }else if(x.type === "2"){
        //add consol
        dtoList[0]['mthAccount'] = x.revenue;
        dtoList[1]['mthAccount'] = x.netProfit;
        dtoList[2]['mthAccount'] = String(+x.netProfit/ +x.revenue * 100).concat("%") ;//Entity NIP

        dtoList[0]['aveMthAccount'] = x.revenue / this.solePropForm?.get('divisor').value;
        dtoList[1]['aveMthAccount'] = x.netProfit/ this.solePropForm?.get('divisor').value;
        dtoList[2]['aveMthAccount'] = '';
      }
    });
  }

  exportAddMthly(yrmthFG: any, dataList: SolePropSubWD[], subWD: SolePropSubWD, subsetYrmth: any[], dtoList: any[]){
    //mthly
    yrmthFG = ((this.solePropForm.get('dataFormArray') as FormGroup)).get('annual');
    subWD = {
      revenue: yrmthFG.get("revenue").value,
      netProfit: yrmthFG.get("netProfit").value,
      type: "1",
      year:  this.solePropForm.get("headerGroup.yearInput").value
    }
    dataList.push(subWD);

    subsetYrmth.forEach(key=>{
      yrmthFG = this.solePropForm.get('dataFormArray').get(key);
      subWD = {
        revenue: yrmthFG.get("revenue").value,
        netProfit: yrmthFG.get("netProfit").value,
        type: "3",
        yrmth: yrmthFG.get("yrmthReCon").value,
      }
      dataList.push(subWD);
      }
    );

     //add annual
     dataList.forEach(x=>{
      if(x.type === "1"){
        dtoList[0]['annualAccount'] = x.revenue;
        dtoList[1]['annualAccount'] = x.netProfit;
        dtoList[2]['annualAccount'] = String(+x.netProfit/ +x.revenue * 100).concat("%") ;//Entity NIP

        dtoList[0]['aveAnnualAccount'] = x.revenue / this.solePropForm?.get('annualDivisor').value;
        dtoList[1]['aveAnnualAccount'] = x.netProfit/ this.solePropForm?.get('annualDivisor').value;
        dtoList[2]['aveAnnualAccount'] = '';
      }
      else if(x.type === "3"){
        dtoList[0][x.yrmth] = x.revenue;
        dtoList[1][x.yrmth] = x.netProfit;
        dtoList[2][x.yrmth] = '';
      }
    });
    const mthlyDataList = dataList.filter(x=> x.type === "3");

      dtoList[0]['mthAccount'] = this.getSumByField(mthlyDataList.map(x=> {return x.revenue}));
      dtoList[1]['mthAccount'] = this.getSumByField(mthlyDataList.map(x=> {return x.netProfit}));
      dtoList[2]['mthAccount'] =  String(+dtoList[1]['mthAccount']/ +dtoList[0]['mthAccount'] * 100).concat("%");

      dtoList[0]['aveMthAccount'] = dtoList[0]['mthAccount'] / this.solePropForm?.get('divisor').value;
      dtoList[1]['aveMthAccount'] = dtoList[1]['mthAccount'] / this.solePropForm?.get('divisor').value;
      dtoList[2]['aveMthAccount'] = "";
  }

  getSumByField(fieldArray: number[]): number{
    const initValue = 0;
    const sum = fieldArray.reduce((accumulator, currentValue) => accumulator + currentValue, initValue);
    return sum;
  }

  //allows input on need basis
  hasValidAuthorityAndStation(): boolean{
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_VERIFIER) && 
      this.station === NSPAY2_STATION.STATION_VERIFICATION){
        return true;
      }
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) && 
    this.station === NSPAY2_STATION.STATION_CERTIFICATION) {
      return false;
    }
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) && 
    this.station === NSPAY2_STATION.STATION_APPROVAL) {
      return false;
    }
    return false;
  }
   


}
