import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, combineLatest } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { Resource } from '../resource/resource.model';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { FunctionService } from './function.service';
import { Application } from '../application/application.model';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { FunctionDeleteDialogComponent } from '../function/function-delete-dialog.component';

@Component({
  selector: 'ic-function',
  templateUrl: './function.component.html',
})
export class FunctionComponent implements OnInit, OnDestroy {
  functions: Resource[] = [];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;
  searchCriteria: any;
  applications: Application[] = [];

  constructor(
    private functionService: FunctionService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager,
    protected modalService: NgbModal
  ) {
    this.searchCriteria = {
      applicationId: '',
      functionName: '',
    };
  }

  loadAll(): void {
    this.functionService
      .query({
        applicationId: this.searchCriteria.applicationId,
        functionName: this.searchCriteria.functionName,
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }

  transition(): void {
    this.router.navigate(['/admin/acm/ic-function'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  search(): void {
    this.links = {
      last: 0,
    };
    this.page = 0;
    this.predicate = 'resourceId';
    this.reverse = false;
    this.loadAll();
  }

  clear(): void {
    this.searchCriteria = {
      applicationId: '',
      functionName: '',
    };
    this.page = 0;
    this.router.navigate([
      '/admin/acm/ic-function',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    ]);
  }

  ngOnInit(): void {
    this.handleNavigation();

    this.functionService.findAllApplication().subscribe(
      (res: HttpResponse<any>) => {
        this.applications = res.body;
      },
      (res: HttpResponse<any>) => this.onError(res.body)
    );

    this.registerChangeInFunctions();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: Resource): string {
    return item.id as string;
  }

  trackApplicationById(index: number, item: Application): any {
    return item.id;
  }

  registerChangeInFunctions(): void {
    this.eventSubscriber = this.eventManager.subscribe('functionListModification', () => this.loadAll());
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
    if (this.predicate !== 'resourceId') {
      result.push('resourceId');
    }
    return result;
  }

  delete(func: Resource): void {
    const modalRef = this.modalService.open(FunctionDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.func = func;
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    this.functions = data;
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div>
  <h2>
    <span icTranslate="acmAdmin.function.home.title">Functions</span>
  </h2>
  <ic-alert></ic-alert>
  <br />
  <div class="row">
    <form name="searchForm" class="col-sm-12">
      <div class="card">
        <div class="card-header"><span icTranslate="acmAdmin.home.SearchCriteria">Search Criteria</span></div>
        <div class="card-block" style="padding: 1.5%">
          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Application</label>
            <div class="col-sm-4">
              <select class="form-control" name="applicationId" [(ngModel)]="searchCriteria.applicationId">
                <option [ngValue]="null"></option>
                <option
                  [ngValue]="
                    applicationOption.appId === searchCriteria.applicationId ? searchCriteria.applicationId : applicationOption.appId
                  "
                  *ngFor="let applicationOption of applications; trackBy: trackApplicationById"
                >
                  {{ applicationOption.appName }}
                </option>
              </select>
            </div>
          </div>

          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Function Name</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="functionName" id="functionName" [(ngModel)]="searchCriteria.functionName" />
            </div>
          </div>

          <div style="margin-top: 1.5%">
            <button id="searchFunction" type="button" class="btn btn-warning btn-sm" (click)="search()">
              <fa-icon [icon]="'search'"></fa-icon>
              <span class="fa fa-search" icTranslate="acmAdmin.home.search"></span>
            </button>

            <button type="button" class="btn btn-secondary btn-sm" (click)="clear()">
              <fa-icon [icon]="'trash-alt'"></fa-icon>
              <span class="fa fa-trash-o" icTranslate="acmAdmin.home.reset"></span>
            </button>

            <button id="createNewFunction" class="btn btn-primary btn-sm" [routerLink]="['../ic-function-new']">
              <fa-icon [icon]="'plus'"></fa-icon>
              <span icTranslate="acmAdmin.function.home.createLabel"> Create new Function </span>
            </button>
          </div>
        </div>
      </div>
    </form>
  </div>
  <br />
  <div class="card">
    <div class="card-header"><span icTranslate="acmAdmin.home.SearchResult">Search Criteria</span></div>
    <div class="card-block" style="padding: 1.5% 1.5% 0 1.5%">
      <div class="table-responsive" *ngIf="functions">
        <table class="table table-striped">
          <thead>
            <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
              <th scope="col"><span icTranslate="acmAdmin.home.sn">S/No.</span></th>
              <th scope="col" icSortBy="application.appName">
                <span icTranslate="acmAdmin.function.application">Application</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="resourceName">
                <span icTranslate="acmAdmin.function.resourceName">Resource Name</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="updatedBy">
                <span icTranslate="acmAdmin.function.updatedBy">Updated By</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="updatedDt">
                <span icTranslate="acmAdmin.function.updatedDt">Updated Dt</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" style="text-align: center">
                <span icTranslate="acmAdmin.home.operation">Operation</span>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let func of functions; let idx = index; trackBy: trackId">
              <td>{{ idx + 1 }}</td>
              <td>
                <div *ngIf="func.application">{{ func.application?.appName }}</div>
              </td>
              <td>{{ func.resourceName }}</td>
              <td>{{ func.updatedBy }}</td>
              <td>{{ func.updatedDt | date: 'yyyy-MM-dd' }}</td>
              <td class="text-center">
                <div class="btn-group flex-btn-group-container">
                  <button type="submit" [routerLink]="['../../../admin/acm/ic-function', func.resourceId]" class="btn btn-primary btn-sm">
                    <fa-icon [icon]="'pencil-alt'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.edit">Edit</span></button
                  >&nbsp;

                  <button type="submit" (click)="delete(func)" [id]="func.resourceName" class="btn btn-danger btn-sm">
                    <fa-icon icon="times"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.delete">Delete</span>
                  </button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div *ngIf="functions && functions.length">
        <div class="row justify-content-center">
          <ic-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></ic-item-count>
        </div>
        <div class="row justify-content-center">
          <ngb-pagination
            [collectionSize]="totalItems"
            [pageSize]="itemsPerPage"
            [(page)]="page"
            (pageChange)="loadPage(page)"
          ></ngb-pagination>
        </div>
      </div>
    </div>
  </div>
</div>
