import { Component } from '@angular/core';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { EventManager } from 'app/core/util/event-manager.service';
import { Group } from './group.model';
import { GroupService } from './group.service';

@Component({
  selector: 'ic-group-delete-dialog',
  templateUrl: './group-delete-dialog.component.html',
})
export class GroupDeleteDialogComponent {
  group?: Group;

  constructor(protected groupService: GroupService, public activeModal: NgbActiveModal, protected eventManager: EventManager) {}

  cancel(): void {
    this.activeModal.dismiss();
  }

  confirmDelete(id: any): void {
    this.groupService.delete(id).subscribe(() => {
      this.eventManager.broadcast('groupListModification');
      this.activeModal.close();
    });
  }
}
<form name="deleteForm" (ngSubmit)="confirmDelete(group?.groupId!)">
  <div class="modal-header">
    <h4 class="modal-title" icTranslate="entity.delete.title">Confirm delete operation</h4>
    <button type="button" class="close" data-bs-dismiss="modal" aria-hidden="true" (click)="cancel()">&times;</button>
  </div>
  <div class="modal-body">
    <ic-alert-error></ic-alert-error>
    <p icTranslate="acmAdmin.group.delete.question" [translateValues]="{ id: group?.groupName! }">
      Are you sure you want to delete this Group?
    </p>
  </div>
  <div class="modal-footer">
    <button id="confirmDeleteGroup" type="submit" class="btn btn-danger btn-sm">
      <fa-icon [icon]="'times'"></fa-icon>&nbsp;<span icTranslate="entity.action.delete">Delete</span>
    </button>
    <button type="button" class="btn btn-secondary btn-sm" data-bs-dismiss="modal" (click)="cancel()">
      <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
    </button>
  </div>
</form>
