    } else {
      this.employerProfileService.getByCSN(csn).subscribe({
        next: res => {
          if (res.body.data == null) {
            this.toastService.error("No employers with " + csn + " exist");
          } else {
            this.disableForm = false;
            this.ep = res.body.data;
            this.patchFormValues();
            this.getUserRoles();
          }
        },
        error(error) {
          console.log(error.message);
          this.toastService.error("No employers with " + csn + " exist");
