import {Component, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from '@angular/router';

import {HttpErrorResponse, HttpResponse, HttpStatusCode} from '@angular/common/http';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import {FormArray, FormBuilder, FormControl, FormGroup, Validators} from '@angular/forms';
import {EmployerProfileService} from '../service/employer-profile.service';
import {NRICValidator} from 'app/shared/validator/nric.directive';
import {CustomToastService} from 'app/layouts/service/customtoast.service';
import {ICompany, ICompanyOfficer} from '../employer-profile.model';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import {UserService} from 'app/admin/access-control';
import {NSPAY2_ROLES} from 'app/nspay2.constants';
import { Table } from 'primeng/table';

@Component({
  selector: 'n2-search-employer-profile',
  templateUrl: './search-employer.component.html',
  styleUrls: ['./search-employer.component.css'],
  providers: []
})
export class SearchEmployerProfileComponent implements OnInit {
  csnInput1 = "";
  csnInput2 = "";
  csnInput3 = "";

  ep: ICompany = {};

  representativeList = ['CO1', 'CO2', 'EO1', 'EO2', 'SUP'];

  salutationOptions = [
    {label: "Ms", value: "MS"},
    {label: "Mr", value: "MR"},
    {label: "Mrs", value: "MRS"},
    {label: "Dr", value: "DR"}
  ]

  createdByUserRoleList: any[] = [];

  isCreatedByCV = false;

  companyForm: FormGroup = this.fb.group({
    companyInfo: this.fb.group({
      csn1: new FormControl(''),
      csn2: new FormControl(''),
      csn3: new FormControl(''),
      name: new FormControl(''),
      blk: new FormControl(''),
      street: new FormControl(''),
      unit: new FormControl(''),
      building: new FormControl(''),
      postal: new FormControl('')
    }),
    officers: this.fb.array([])
  });

  directSchemeForm: FormGroup = this.fb.group({
    directSchemeHistory: this.fb.array([])
  });

  disableForm = true;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private fb: FormBuilder,
    private employerProfileService: EmployerProfileService,
    private toastService: CustomToastService,
    private nspConfirmService: NspConfirmationService,
    private userService: UserService,
    private exportUtilsService: ExportUtilsService
  ) {
    this.constructForm();
    this.companyForm.disable();
    this.directSchemeForm.disable();
  }

  get officersFormArray(): FormArray {
    return this.companyForm.get('officers') as FormArray;
  }

  get directSchemeFormArray(): FormArray {
    return this.directSchemeForm.get('directSchemeHistory') as FormArray;
  }

  ngOnInit(): void {
    //
  }

  search(): void {
    const csn = this.csnInput1 + this.csnInput2 + this.csnInput3;

    if (csn.length === 0) {
      this.toastService.error("Mandatory fields cannot be empty");
    } else {
      this.employerProfileService.getByCSN(csn)
        .subscribe(
          {
            next: (res: HttpResponse<any>) => {
              if(res.body.data == null) {
                this.toastService.error("No employers with " + csn + " exist");
              } else {
                this.disableForm = false;
                this.ep = res.body.data;
                this.patchFormValues();
                this.getUserRoles();
              }
            },
            error(error: HttpErrorResponse) {
              console.log(error.message);
              this.toastService.error("No employers with " + csn + " exist");
            }
          }
        );
    }
  }

  constructForm(): void {
    const officers = this.companyForm.get('officers') as FormArray;

    this.representativeList.forEach((role: string) => {
        officers.push(
          this.fb.group({
            id: new FormControl(),
            officerRole: role,
            nric: new FormControl('', [NRICValidator(), Validators.required]),
            salutation: new FormControl(null, Validators.required),
            name: new FormControl('', [Validators.maxLength(50), Validators.required]),
            appointment: new FormControl('', Validators.maxLength(30)),
            email: new FormControl('', [Validators.email, Validators.required]),
            contactNo: new FormControl('', Validators.required)
          })
        );
      }
    );
  }

  patchFormValues(): void {
    this.companyForm.patchValue({
      companyInfo: {
        csn1: this.ep.csn.substring(0, 10),
        csn2: this.ep.csn.substring(10, 13),
        csn3: this.ep.csn.substring(13, 15),
        name: this.ep.companyName,
        blk: this.ep.blk,
        street: this.ep.street,
        unit: this.ep.unit,
        building: this.ep.building,
        postal: this.ep.postalCode
      },
    })

    const tempOfficers = this.companyForm.get('officers') as FormArray;

    this.ep.companyOfficers.forEach(employerRep => {
      const index = this.getIndexFromTitle(tempOfficers, employerRep.officerRole);
      console.log(employerRep);
      if (index > -1) {
        tempOfficers.at(index).patchValue({
          id: employerRep.id,
          nric: employerRep.nric,
          salutation: employerRep.salutation,
          name: employerRep.name,
          appointment: employerRep.appointment,
          email: employerRep.email,
          contactNo: employerRep.contactNo
        })
      }
    })

    console.log(this.companyForm);
  }

  getIndexFromTitle(tempOfficers: FormArray, title: string): number {
    let resIndex = -1;

    tempOfficers.controls.forEach((c, index) => {
      if (c.get('officerRole').value === title) {
        resIndex = index;
      }
    });

    return resIndex;
  }

  disableFormDecision(): void {
    const isCreatedByCV: boolean = this.isRoleInList();

    if (this.ep.eRegistered && this.ep.scheme === "DIRECT") {
      this.directSchemeForm.enable();
    } else if (this.ep.eRegistered && this.ep.scheme === "NON-DIRECT") {
      // disable all
    } else if (isCreatedByCV) {
      this.companyForm.enable();
    } else if (!this.ep.eRegistered) {
      this.companyForm.enable();
      this.directSchemeForm.enable();
    }
  }

  getUserRoles(): void {
    this.userService.findAllAssignedRoles(this.ep.createdBy).subscribe(
      (res: HttpResponse<any>) => {
        this.createdByUserRoleList = res.body;
        this.disableFormDecision();
      },
      (error: HttpErrorResponse) => {
        console.log(error);
      }
    );
  }

  isRoleInList(): boolean {
    console.log("-====-");
    console.log(this.createdByUserRoleList);

    const found: any = this.createdByUserRoleList.find(e => e.resourceId === NSPAY2_ROLES.CLAIMS_VERIFIER);

    if (found) {
      return true;
    }

    return false;
  }

  exportOfficerCSV(dataTable: Table): void {
    const officers = this.companyForm.get('officers') as FormArray;
    const data = officers.value;
    const columns = [
      { field: 'role', header: ' ', index: 0 },
      { field: 'nric', header: 'NRIC', index: 1 },
      { field: 'salutation', header: 'Salutation', index: 2 },
      { field: 'name', header: 'Name', index: 3 },
      { field: 'appointment', header: 'Appointment', index: 4 },
      { field: 'email', header: 'Email', index: 5 },
      { field: 'contactNo', header: 'Contact No.', index: 6 },
    ];

    const dtoList: any[] = [];
    data.forEach((o: any) => {
      dtoList.push({
        role: o.officerRole,
        nric: o.nric,
        salutation: o.salutation,
        name: o.name,
        appointment: o.appointment,
        email: o.email,
        contactNo: o.contactNo
      });
    });

    this.exportUtilsService.exportCSV(dataTable, dtoList, data.length, columns, "Company Representative");
  }

  exportDSCSV(dataTable: Table): void {
    const directScheme = this.directSchemeForm.get('directSchemeHistory') as FormArray;
    const data = directScheme.value;
    const columns = [
      { field: 'sn', header: 'S/N', index: 0 },
      { field: 'joinDt', header: 'Date Joined', index: 1 },
      { field: 'submittedBy', header: 'Submitted By', index: 2 },
      { field: 'submittedOn', header: 'Submitted On', index: 3 },
      { field: 'dateLeft', header: 'Date Left', index: 4 },
      { field: 'ceasedBy', header: 'Ceased By', index: 5 },
      { field: 'ceasedOn', header: 'Ceased On', index: 6 },
      { field: 'bankAccount', header: 'Bank Account', index: 7 }
    ];

    const dtoList: any[] = [];
    data.forEach((o: any, index:number) => {
      dtoList.push({
        sn: index + 1,
        joinDt: o.joinDt,
        submittedBy: o.submittedBy,
        submittedOn: o.submittedOn,
        dateLeft: o.dateLeft,
        ceasedBy: o.ceasedBy,
        ceasedDt: o.ceasedDt,
        bankAccount: o.bankAccount
      });
    });

    this.exportUtilsService.exportCSV(dataTable, dtoList, data.length, columns, "DIRECT Scheme History");
  }

  confirm(event: Event, key: string, dialog: any): void {
    if (this.disableForm) {
      this.toastService.error("Mandatory fields cannot be empty");
    } else {
      this.nspConfirmService.onConfirmPopup(event, key, null, () => {
        this.prepareRequest()
      });
    }
  }

  prepareRequest(): void {
    const formValue = this.companyForm.value;

    this.ep.csn = "${formValue.companyInfo.csn1}"
      + "${formValue.companyInfo.csn2}"
      + "${formValue.companyInfo.csn3}";

    this.ep.companyName = formValue.companyInfo.name;
    this.ep.blk = formValue.companyInfo.blk;
    this.ep.street = formValue.companyInfo.street;
    this.ep.building = formValue.companyInfo.building;
    this.ep.unit = formValue.companyInfo.unit;
    this.ep.postalCode = formValue.companyInfo.postal;

    const companyOfficerArr: ICompanyOfficer[] = [];

    formValue.officers.forEach((o: any) => {
      if (o.name !== "" && o.nric !== "" && o.email !== "" && o.salutation !== "") {

        const officer: ICompanyOfficer = {};

        officer.id = o.id;
        officer.name = o.name;
        officer.nric = o.nric;
        officer.salutation = o.salutation;
        officer.email = o.email;
        officer.officerRole = o.officerRole;
        officer.appointment = o.appointment;
        officer.contactNo = o.contactNo;

        companyOfficerArr.push(officer);
      }
    });

    this.ep.companyOfficers = companyOfficerArr;

    console.log(this.ep);
    this.submitRequest(this.ep);
  }

  submitRequest(company: ICompany): void {
    this.employerProfileService.update(company).subscribe(
      (res: HttpResponse<any>) => {
        if (res.ok) {
          this.toastService.add({
            severity: 'success',
            summary: 'Success',
            detail: "Employer details has been updated"
          });
        }
      },
      (error: HttpErrorResponse) => {
        let message = "";

        switch (error.error.errorKey) {
          case "requireCO":
            message = "Require at least 1 CO";
            break;
          default:
            message = error.error.title;
            break;
        }

        this.toastService.add({
          severity: 'error',
          summary: 'Error',
          detail: message
        });
      })
  }

  reset(): void {
    //
  }
}
<div class="grid">
  <div class="col-12">
    <p-card styleClass="search-panel-bg">
      <div class="formgroup-inline">
        <div class="field">
          <input id="csnInput1" type="text" pInputText maxlength="10" [(ngModel)]="csnInput1">
        </div>

        <div class="field">
          <label for="csnInput1">-</label>
          <input id="csnInput2" type="text" pInputText maxlength="3" [(ngModel)]="csnInput2">
        </div>

        <div class="field">
          <label for="csnInput3">-</label>
          <input id="csnInput3" type="text" pInputText maxlength="2" [(ngModel)]="csnInput3">
        </div>

        <button pButton label="Search" (click)="search()"></button>
      </div>
    </p-card>
  </div>
<!-- grid 1 end -->
</div>

<form [formGroup]="companyForm">
  <!--grid 2 start-->
  <div class="grid">
    <div class="col-4" formGroupName="companyInfo">
      <p-panel>
        <ng-template pTemplate="header">
          <div>
            <span class="p-panel-title"> Company Information (Size: {{ep.companySize}}, eRegistered: {{ep.eRegistered == 'Y' ? "Yes" : "No"}})</span>
          </div>
        </ng-template>

        <div class="field grid">
          <label for="csn" class="col-3">CSN</label>
          <div class="col-9" id="csn">
            <input type="text" pInputText formControlName="csn1" id="csn1" style="width:30%" maxlength="10" [disabled]="disableForm"/>
            -
            <input type="text" pInputText formControlName="csn2" id="csn2" style="width:20%" maxlength="3" [disabled]="disableForm"/>
            -
            <input type="text" pInputText formControlName="csn3" id="csn3" style="width:20%" maxlength="2" [disabled]="disableForm"/>
          </div>
        </div>

        <div class="field grid">
          <label for="csn" class="col-3">Company Name</label>
          <div class="col-9">
            <input type="text" pInputText formControlName="name" id="companyName" class="w-full" maxlength="50" [disabled]="disableForm" />
          </div>
        </div>

        <div class="field grid">
          <label for="address" class="col-3">Address</label>
          <div class="col-9" id="address">
            <div class="field">
              <input type="text" pInputText formControlName="blk" id="blk" placeholder="Blk" style="width:25%"
                maxlength="10" [disabled]="disableForm" />
              <input type="text" pInputText formControlName="street" id="street" placeholder="Street" style="width:70%"
                maxlength="100" [disabled]="disableForm"/>
            </div>

            <div>
              <input type="text" pInputText formControlName="unit" id="unit" placeholder="Unit" style="width:40%"
                maxlength="10" [disabled]="disableForm"/>
              <input type="text" pInputText formControlName="building" id="building" placeholder="Building"
                style="width:55%" maxlength="100" [disabled]="disableForm"/>
            </div>
          </div>
        </div>

        <div class="field grid">
          <label for="postal" class="col-3">Postal Code</label>
          <div class="col-9">
            <input type="text" pInputText pKeyFilter="pint" formControlName="postal" id="postal" maxlength="6" [disabled]="disableForm"/>
          </div>
        </div>
      </p-panel>
    </div>

    <div class="col-8">
      <div formArrayName="officers">
        <p-panel>
          <ng-template pTemplate="header">
            <div>
              <span class="p-panel-title" icTranslate="nspay2App.administration.employer-profile.companyRepresentative.header"></span>
            </div>
          </ng-template>
          <!-- companyForm.controls.officers['controls'] -->
          <p-table #officersTb [value]="officersFormArray.controls" styleClass="p-datatable-gridlines">
            <ng-template pTemplate="caption">
              <div class="p-d-flex">
                <button type="button" pButton pRipple icon="pi pi-file-o" (click)="exportOfficerCSV(officersTb)" pTooltip="CSV"
                  tooltipPosition="bottom"></button>
              </div>
            </ng-template>
            <ng-template pTemplate="header">
              <tr>
                <th scope="col" style="width: 7%"></th>
                <th scope="col" pSortableColumn="value.nric">NRIC<p-sortIcon field="value.nric"></p-sortIcon></th>
                <th scope="col" pSortableColumn="value.salutation" style="width: 13%">Salutation<p-sortIcon field="value.salutation"></p-sortIcon></th>
                <th scope="col" pSortableColumn="value.name">Name<p-sortIcon field="value.name"></p-sortIcon></th>
                <th scope="col" pSortableColumn="value.appointment">Appointment<p-sortIcon field="value.appointment"></p-sortIcon></th>
                <th scope="col" pSortableColumn="value.email">Email<p-sortIcon field="value.email"></p-sortIcon></th>
                <th scope="col" pSortableColumn="value.contactNo">Contact No.<p-sortIcon field="value.contactNo"></p-sortIcon></th>
              </tr>
            </ng-template>
            <ng-template pTemplate="body" let-officer let-i="rowIndex">
              <tr [formGroupName]="i">
                <td>
                  {{officer.value.officerRole}}
                </td>
                <td>
                  <input type="text" pInputText formControlName="nric" class="w-full" [disabled]="disableForm"/>
                  <span *ngIf="officer.get('nric').hasError('required') && officer.get('nric').dirty"
                    class="p-error">Required</span>
                  <span *ngIf="officer.get('nric').hasError('nric') && officer.get('nric').dirty"
                    class="p-error">Invalid NRIC Format</span>
                </td>
                <td>
                  <p-dropdown formControlName="salutation" [options]="salutationOptions" optionLabel="label"
                    optionValue="value" placeholder="&nbsp;" styleClass="full-width" [disabled]="disableForm">
                  </p-dropdown>
                  <span *ngIf="officer.get('salutation').hasError('required') && officer.get('salutation').dirty"
                    class="p-error">Required</span>
                </td>
                <td>
                  <input type="text" pInputText formControlName="name" id="name{{i}}" class="w-full" [disabled]="disableForm"/>
                  <span *ngIf="officer.get('name').hasError('maxlength')" class="p-error">Maxmium length is 50</span>
                  <span *ngIf="officer.get('name').hasError('required') && officer.get('name').dirty"
                    class="p-error">Required</span>
                </td>
                <td>
                  <input type="text" pInputText formControlName="appointment" class="w-full" [disabled]="disableForm"/>
                  <span *ngIf="officer.get('appointment').hasError('maxlength')" class="p-error">Maxmium length is
                    30</span>
                </td>
                <td>
                  <input type="text" pInputText formControlName="email" class="w-full" [disabled]="disableForm"/>
                  <span *ngIf="officer.get('email').hasError('required') && officer.get('email').dirty"
                    class="p-error">Required</span>
                  <span *ngIf="officer.get('email').hasError('email') && officer.get('email').dirty"
                    class="p-error">Invalid Email Format</span>
                </td>
                <td>
                  <input type="text" pInputText formControlName="contactNo" class="w-full" [disabled]="disableForm" maxlength="8"/>
                  <span *ngIf="officer.get('contactNo').hasError('required') && officer.get('contactNo').dirty"
                    class="p-error">Required</span>
                </td>
              </tr>
            </ng-template>
          </p-table>
        </p-panel>
      </div>
    </div>
    <!-- grid 2 end -->
  </div>
</form>

<form [formGroup]="directSchemeForm">
  <div class="grid">
    <div class="col-8 col-offset-4" formArrayName="directSchemeHistory">
      <p-panel>
        <ng-template pTemplate="header">
          <div>
            <span class="p-panel-title" icTranslate="nspay2App.administration.employer-profile.directSchemeHistory.header"></span>
          </div>
        </ng-template>
        <!-- directSchemeForm.controls.directSchemeHistory['controls'] -->
        <p-table #directSchemeTb [value]="directSchemeFormArray.controls" styleClass="p-datatable-gridlines">
          <ng-template pTemplate="caption">
            <div class="p-d-flex">
              <button type="button" pButton pRipple icon="pi pi-file-o" (click)="exportDSCSV(directSchemeTb)" pTooltip="CSV"
                tooltipPosition="bottom"></button>
            </div>
          </ng-template>
          <ng-template pTemplate="header">
            <tr>
              <th scope="col" style="width: 7%">S/N</th>
              <th scope="col">Date Joined</th>
              <th scope="col" style="width: 13%">Submitted By</th>
              <th scope="col">Submitted On</th>
              <th scope="col">Date Left</th>
              <th scope="col">Ceased By</th>
              <th scope="col">Ceased On</th>
              <th scope="col">Bank Account.</th>
            </tr>
          </ng-template>
          <ng-template pTemplate="body" let-data let-i="rowIndex">
            <tr>
              <td>{{i+1}}</td>
              <td>{{data.value.joinDt}}</td>
              <td>{{data.value.submittedBy}}</td>
              <td>{{data.value.submittedDt}}</td>
              <td>
                <p-calendar id="dateLeft{{i}}"
                  formControlName="dateLeft"
                  dateFormat="dd-mm-yy"
                  [readonlyInput]="true"
                  placeholder="Select a date"
                  appendTo="body"
                  class="inputfield w-full">
              </p-calendar>
              </td>
              <td>{{data.value.ceasedBy}}</td>
              <td>{{data.value.ceasedDt}}</td>
              <td>{{data.value.bankAccount}}</td>
            </tr>
          </ng-template>
        </p-table>
      </p-panel>
    </div>
  </div>
</form>

<div class="flex justify-content-end align-items-center">
  <div>
    <button pButton type="button" label="Cancel" (click)="reset()" class="p-button-danger"></button>
    <p-confirmPopup key='confirmPopup'></p-confirmPopup>
    <button pButton type="button" label="Submit" (click)="confirm($event, 'confirmPopup', null)" [disabled]="disableForm"></button>
  </div>
</div>
