import {Component, Input, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import {AbstractControl, FormArray, FormBuilder, FormControl, FormGroup} from "@angular/forms";
import { AnnualIncomeWD } from 'app/claim/se-claim/model/annual-income-model';
import { InsurAveWD } from 'app/claim/se-claim/model/insur-ave.model';
import {SaveSEClaimRequest} from "app/claim/se-claim/model/save-se-claim-request.model";
import {DateUtilsService} from "app/core/util/date-utils.service";
import { DateValidator } from 'app/shared/validator/date-validator.directive';
import { AccountService } from 'app/core/auth/account.service';
import { NSPAY2_ROLES, NSPAY2_STATION } from 'app/nspay2.constants';

@Component({
  selector: 'n2-annual-income',
  templateUrl: './annual-income.component.html',
  styleUrls: ['./annual-income.component.scss']
})
export class AnnualIncomeComponent implements OnInit, OnChanges {

  @Input() annualIncomeFormArray: FormArray;
  @Input() seHardCopyDetails: any = {};
  @Input() isActive = false;
  //meant for routing from enquiry
  @Input() readOnly: boolean;
  @Input() station: string;
  
  rowHeaders: string[] = [
    "Source",
    "Accounting Period",
    "Occupation Nature",
    "Revenue",
    "Adj Profit",
    "Actual NIP"
  ]

  sourceCodeType: any[] = [];

  constructor(private formBuilder: FormBuilder,
              public dateUtilsService: DateUtilsService,
              public accountService: AccountService,
              ) { console.log("test");}

  
  static mapAnnualIncomeWDToFormGroup(annualIncomeWD: AnnualIncomeWD, formBuilder: FormBuilder, dateUtilsService: DateUtilsService): FormGroup{
    const annualIncomeFormGroup: FormGroup = formBuilder.group({
      seq: new FormControl(annualIncomeWD.seq, null),
      source: new FormControl(annualIncomeWD.source, null),
      assessment: new FormControl(annualIncomeWD.assessment === null? null :dateUtilsService.convertStrToDate(annualIncomeWD.assessment, "DD-MM-YYYY"), null),
      acctgPeriodStrt: new FormControl(annualIncomeWD.acctgStartDate === null? null :dateUtilsService.convertStrToDate(annualIncomeWD.acctgStartDate, "DD-MM-YYYY"), null),
      acctgPeriodEnd: new FormControl(annualIncomeWD.acctgEndDate === null? null :dateUtilsService.convertStrToDate(annualIncomeWD.acctgEndDate, "DD-MM-YYYY"), null),
      occupationNature: new FormControl(annualIncomeWD.occupationNature, null),
      revenue: new FormControl(annualIncomeWD.revenue, null),
      adjProfit: new FormControl(annualIncomeWD.adjProfit, null),
    },{ validators:[
      DateValidator.dateLessThan('acctgPeriodStrt', 'acctgPeriodEnd'),
    ]});
    return annualIncomeFormGroup;
  }

  ngOnInit(): void {
    console.log("test");
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes.seHardCopyDetails?.currentValue) {
      const x = changes.seHardCopyDetails.currentValue;
      const date: Date = this.dateUtilsService.convertStrToDate(x.trgStartDte, "DD-MM-YYYY");
      this.sourceCodeType = [];
      this.sourceCodeType.push({
        name: "NA ".concat((date.getFullYear() - 1).toString().substring(2,4)),
        code: Number((date.getFullYear() - 1).toString().substring(2,4))
      });
      this.sourceCodeType.push({
        name: "NA ".concat((date.getFullYear()).toString().substring(2,4)),
        code: Number(date.getFullYear().toString().substring(2,4))
      });
      this.sourceCodeType.push({
        name: "NA ".concat((date.getFullYear() + 1).toString().substring(2,4)),
        code: Number((date.getFullYear() + 1).toString().substring(2,4))
      });
    }
  }

  getFormControl(control: AbstractControl, name: string): FormControl{
    return control.get(name) as FormControl;
  }

  getEmptyColumns(): any[]{
    return new Array(2 - this.annualIncomeFormArray.length);
  }

  isValidNumber(obj: any): boolean{
    return !isNaN(obj);
  }

  insertNewAnnualIncome(): void{
    const annualIncomeFormGroup: FormGroup = this.formBuilder.group({
      source: new FormControl(null, null),
      assessment: new FormControl(null, null),
      acctgPeriodStrt: new FormControl(null, null),
      acctgPeriodEnd: new FormControl(null, null),
      occupationNature: new FormControl(null, null),
      revenue: new FormControl(null, null),
      adjProfit: new FormControl(null, null),
    },{ validators:[
      DateValidator.dateLessThan('acctgPeriodStrt', 'acctgPeriodEnd'),
    ]});
    this.annualIncomeFormArray.push(annualIncomeFormGroup);
  }

  deleteSecondAnnualIncome(): void {
    this.annualIncomeFormArray.removeAt(1);
  }

  calculateActualNip(annualIncome : AbstractControl): number{
    const calcNip = annualIncome.get('adjProfit').value/annualIncome.get('revenue').value * 100;
    return calcNip? calcNip: 0;
  }

  
  //allows input on need basis
  hasValidAuthorityAndStation(): boolean{
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_VERIFIER) && 
      this.station === NSPAY2_STATION.STATION_VERIFICATION){
        return true;
      }
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) && 
    this.station === NSPAY2_STATION.STATION_CERTIFICATION) {
      return false;
    }
    if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) && 
    this.station === NSPAY2_STATION.STATION_APPROVAL) {
      return false;
    }
    return false;
  }

}
<p-panel [style] = "{'width':'100%'}">
  <ng-template pTemplate="header">
    <div class = "flex col-6 align-items-center">
      <div [style] = "{'font-weight':'bold','margin-left':'0.5em'}">Annual Income</div>
    </div>
    <div class = "flex col-6 align-items-center justify-content-end">
      <p-button *ngIf = "this.annualIncomeFormArray.length !== 2 && isActive && !readOnly  && hasValidAuthorityAndStation()" (click) = "insertNewAnnualIncome()">Insert</p-button>
    </div>
  </ng-template>
  <form>
   <p-table [value]="rowHeaders" styleClass="p-datatable-gridlines">
  <ng-template pTemplate="body" let-rowHeader let-rowIndex = "rowIndex">
    <tr class = "annual" *ngIf = "rowIndex === 0">
      <td>{{rowHeader}}</td>
      <td *ngFor = "let annualIncome of annualIncomeFormArray.controls; let i = index">
        <div *ngIf = "isActive && !readOnly && hasValidAuthorityAndStation(); else labelSource">
          <p-dropdown [formControl]="getFormControl(annualIncome,'source')"
                      [options] = "sourceCodeType"
                      [autoDisplayFirst] = false
                      optionLabel="name"
                      optionValue="code">
          </p-dropdown>
          <p-calendar [formControl]="getFormControl(annualIncome,'assessment')" dateFormat="dd-mm-yy" appendTo ="body" [style] = "{'width':'120px'}"></p-calendar>
          <p-button *ngIf = "i == 1" (click) = "deleteSecondAnnualIncome()" icon="pi pi-times" [style]= "{'margin-left': '.5em'}"></p-button>
        </div>
        <ng-template #labelSource>
          {{getFormControl(annualIncome,'source').value === null? "-" : "NA ".concat(annualIncome.get('source').value)}}
          {{ this.dateUtilsService.convertDateToStr(getFormControl(annualIncome,'assessment').value,  'DD-MM-YYYY') }}
        </ng-template>
      </td>
      <td *ngFor = "let col of getEmptyColumns()"></td>
    </tr>
    <tr class = "annual" *ngIf = "rowIndex === 1">
      <td>{{rowHeader}}</td>
      <td *ngFor = "let annualIncome of annualIncomeFormArray.controls">
        <div *ngIf = "isActive && !readOnly && hasValidAuthorityAndStation(); else labelAcctgPeriod">
          <div class="field grid" [style]="{'align-items':'center'}">
            <div class="col-5">
              <p-calendar [formControl]="getFormControl(annualIncome,'acctgPeriodStrt')" dateFormat="dd-mm-yy" appendTo ="body" [style] = "{'width':'120px'}"></p-calendar>
            </div>
            <div [style]="{'align-items': 'center'
            , 'justify-content':'center', 'display':'flex'}">
              to
            </div>
            <div class="col-5">
              <p-calendar [formControl]="getFormControl(annualIncome,'acctgPeriodEnd')" dateFormat="dd-mm-yy" appendTo ="body" [style] = "{'width':'120px'}"></p-calendar>
            </div>
          </div>
        </div>
        <div class = "grid col-12"*ngIf = "annualIncome?.errors?.['invalidDateLessThan'] &&  (annualIncome?.get('acctgPeriodStrt')?.touched ||annualIncome?.get('acctgPeriodEnd')?.touched)">
          <div class = "col">
              <small 
              class="p-error" icTranslate="nspay2App.claim.validation.CLM-V004"
              [translateValues]="{ endField: 'Accounting Period End', startField : 'Accounting Period Start' }"></small>
          </div>
        </div>
        <ng-template #labelAcctgPeriod>
          {{annualIncome.get('acctgPeriodStrt').value === null? "-" :this.dateUtilsService.convertDateToStr(annualIncome.get('acctgPeriodStrt').value, "DD-MM-YYYY").concat(" to ")}}
          {{annualIncome.get('acctgPeriodEnd').value === null? "" :this.dateUtilsService.convertDateToStr(annualIncome.get('acctgPeriodEnd').value, "DD-MM-YYYY")}}
        </ng-template>
      </td>
      <td *ngFor = "let col of getEmptyColumns()"></td>
    </tr>
    <tr class = "annual"  *ngIf = "rowIndex === 2">
      <td>{{rowHeader}}</td>
      <td *ngFor = "let annualIncome of annualIncomeFormArray.controls">
        <div *ngIf = "isActive && !readOnly && hasValidAuthorityAndStation(); else labelOccupationNature">
          <input [formControl]="getFormControl(annualIncome,'occupationNature')" pInputText/>
        </div>
        <ng-template #labelOccupationNature>
          {{annualIncome.get('occupationNature').value === null? "-": annualIncome.get('occupationNature').value}}
        </ng-template>
      </td>
      <td *ngFor = "let col of getEmptyColumns()"></td>
    </tr>
    <tr class = "annual"  *ngIf = "rowIndex === 3">
      <td>{{rowHeader}}</td>
      <td *ngFor = "let annualIncome of annualIncomeFormArray.controls">
        <div *ngIf = "isActive && !readOnly && hasValidAuthorityAndStation(); else labelRevenue">
          <p-inputNumber [inputStyle]= "{'text-align':'right'}"
                         [formControl]="getFormControl(annualIncome,'revenue')"
                         mode="decimal"
                         [minFractionDigits]="2">
          </p-inputNumber>
        </div>
        <ng-template #labelRevenue>
          {{annualIncome.get('revenue').value === null? "-" : annualIncome.get('revenue').value}}
        </ng-template>
      </td>
      <td *ngFor = "let col of getEmptyColumns()"></td>
    </tr>
    <tr class = "annual"  *ngIf = "rowIndex === 4">
      <td>{{rowHeader}}</td>
      <td *ngFor = "let annualIncome of annualIncomeFormArray.controls">
        <div *ngIf = "isActive && !readOnly && hasValidAuthorityAndStation(); else labelAdjProfit">
          <p-inputNumber [inputStyle]= "{'text-align':'right'}"
                         [formControl]="getFormControl(annualIncome,'adjProfit')"
                         mode="decimal"
                         [minFractionDigits]="2">
          </p-inputNumber>
        </div>
        <ng-template #labelAdjProfit>
          {{annualIncome.get('adjProfit').value === null? "-" : annualIncome.get('adjProfit').value}}
        </ng-template>
      </td>
      <td *ngFor = "let col of getEmptyColumns()"></td>
    </tr>
    <tr class = "annual"  *ngIf = "rowIndex === 5">
      <td>{{rowHeader}}</td>
      <td *ngFor = "let annualIncome of annualIncomeFormArray.controls"
          [style]= "{'text-align':'right'}">
        <div [style] = "{'margin-right':'6.25em'}">
        {{calculateActualNip(annualIncome) | number : '1.0-0'}}%
        </div>
      </td>
      <td *ngFor = "let col of getEmptyColumns()"></td>
    </tr>
  </ng-template>
</p-table>
  </form>
</p-panel>
