import {Injectable} from '@angular/core';
import {HttpClient, HttpResponse} from '@angular/common/http';
import {Observable} from 'rxjs';

import {IPublicHoliday} from '../public-holiday.model';
import {ApplicationConfigService} from 'app/core/config/application-config.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';

export type EntityResponseType = HttpResponse<IPublicHoliday>;
export type EntityArrayResponseType = HttpResponse<IPublicHoliday[]>;

@Injectable({providedIn: 'root'})
export class PublicHolidayService {
  public resourceUrl = this.applicationConfigService.getEndpointFor('api/public-holiday');


  constructor(protected http: HttpClient, 
    private applicationConfigService: ApplicationConfigService,
    private ns2Http: Ns2HttpService) {}

  get(): Observable<EntityResponseType> {
    return this.http.get<IPublicHoliday>(this.resourceUrl, {observe: 'response'});
  }

  getCurrent(): Observable<EntityResponseType> {
    return this.http.get<IPublicHoliday>(this.resourceUrl + "/current", {observe: 'response'});
  }

  createUpdate(requestList: IPublicHoliday[]): Observable<EntityResponseType> {
    return this.http.post<IPublicHoliday>(this.resourceUrl + "/create-update", requestList, {observe: 'response'});
  }

  getCurrentOneYear(options: any) : Observable<any>{
    const path = this.resourceUrl + '/current_one_year';
    return this.ns2Http.get(path, options);
  }

  getHolidayByYear(options: any) : Observable<any>{
    const path = this.resourceUrl + '/get_holiday';
    return this.ns2Http.get(path, options);
  }

  
}
