export function validateEmail(str: string): any {

  const parts = str.split("@");
  const dot = parts[1].indexOf(".");
  const dotSplits = parts[1].split(".");
  const dotCount = dotSplits.length - 1;

  // Check if . is present, and that minimum 1 character after @
  if (dot === -1 || dot < 2 || dotCount > 2) {
    return false;
  }

  // Check if . is not the last character and dots are not repeated
  for (let i = 0; i < dotSplits.length; i++) {
    if (dotSplits[i].length === 0) {
      return false;
    }
  }

  return true;

}
