  static staticConstructConsolidatedHeaderData(dataList, consolidated, empty, newForm, yrmthList) {
    //logic
    //12 columns of 12 mths from claimstartdate/trgStartDte - 13 mths to trgStartDte - 1 mths
    const sanitizedDataList = [];
    let sortedData;
    if (consolidated || empty || newForm) {
      yrmthList.forEach((yrmth, colIndex) => {
        dataList.push({
          yrmthReCon: yrmth
        });
      });
      sortedData = dataList;
    } else {
      yrmthList.forEach(yrmth => {
        for (let i = 0; i < dataList.length; i++) {
          if (dataList[i].yrmth === yrmth && sanitizedDataList.findIndex(x => x.yrmth === yrmth) < 0) {
            sanitizedDataList.push(dataList[i]);
          }
        }
      });
      yrmthList.forEach(yrmth => {
        const index = sanitizedDataList.findIndex(x => x.yrmth === yrmth);
        if (index < 0) {
          sanitizedDataList.push({
            yrmthReCon: yrmth
          });
        } else {
          sanitizedDataList[index].yrmthReCon = yrmth;
        }
      });
      sortedData = sanitizedDataList.sort((a, b) => (0, moment_1.default)(a.yrmthReCon, "MM-YYYY") < (0, moment_1.default)(b.yrmthReCon, "MM-YYYY") ? -1 : 1);
    }
    return sortedData;
  }
  static staticGenerateYrMth(data, dateUtilsService, yrmthListCtrl) {
