sourcesContent:
import { formatDate } from '@angular/common';
import { HttpParams } from '@angular/common/http';
import { ChangeDetectorRef, Component, DoCheck, EventEmitter, Inject, Input, KeyValueDiffers, LOCALE_ID, OnChanges, OnInit, Output, SimpleChanges, ViewChild } from '@angular/core';
import { AbstractControl, FormArray, FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { faDisease } from '@fortawesome/free-solid-svg-icons';
import { InsurITXSubWD } from 'app/claim/se-claim/model/insur-income-tax-sub-data.model';
import { InsurITXWD } from 'app/claim/se-claim/model/insur-income-tax.model';
import { SaveSEClaimRequest } from 'app/claim/se-claim/model/save-se-claim-request.model';
import { SEClaimService } from 'app/claim/se-claim/service/se-claim.service';

import { DateUtilsService } from 'app/core/util/date-utils.service';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import moment from 'moment';
import { ConfirmationService } from 'primeng/api';
import { Table } from 'primeng/table';
import { filter, startWith, Subscription } from 'rxjs';
import claim from '../../../../../../i18n/en/claim.json';
import { AccountService } from 'app/core/auth/account.service';
import { NSPAY2_ROLES, NSPAY2_STATION } from 'app/nspay2.constants';

@Component({
  selector: 'n2-insurance-income-tax',
  templateUrl: './insurance-income-tax.component.html',
  styleUrls: ['./insurance-income-tax.component.scss']
})
export class InsuranceIncomeTaxComponent implements OnInit, OnChanges {

  radioOptions: any[] = [{name: 'Monthly', key: 'MTH'}, {name: 'Consolidated', key: 'CON'}];
  loading = true;
  cols: any[];
  currHardCopyDetails: any = {};
  currIrasDetails : any = {};
  currSelectedOption: any;
  @Input() seHardCopyDetails:any = {};
  @Input() selectedWDOption:any = {};
  @Input() itemDetailsSection: any = {};
  @Input() workingDataWithChanges: Map<string, any>;
  @Input() newUnsavedWorkingData: Map<string, any>;
  @Input() saveSEClaimRequest: SaveSEClaimRequest;
  @Input() irasDetails: any = {};
  @Input() trgStartDte: string;
  //meant for routing from enquiry
  @Input() readOnly: boolean;
  @Input() station: string;

  subscr:Subscription = new Subscription();

  yrmthList: string[] = [];

  readonly PEND_VERIFY_STATUS = "PVE";
  readonly PEND_ACTION_STATUS = "PAC";

  @Output()  totalAmtPayEvent: EventEmitter<any> = new EventEmitter<any>();

  //impt
  year: number;
  colMthRange:any[] = [];
  dataSet: any = {
  }
  actualNipComputation = "";
  formGroupObj:any = undefined;
  overridingCommissionPerc = 0;
  displayActualNip = false;
  annualtotalComm = 0;
  tradeIncome = 0;
  proposedNip=0;
  actualNip=0;
  overridingCommission=0;
  claimJson: any;

  incomeTaxInsurForm: FormGroup  = this.formBuilder.group({
    id: new FormControl(null, null),
    workingDataId: new FormControl('',null),
    consolidated: new FormControl(false, null),
    incomeTaxInsurArrayMthly: this.formBuilder.array([]),
    incomeTaxInsurArrayConsol: this.formBuilder.array([]),
    year: 0,
    captionGroup: this.formBuilder.group({
      radioOption: new FormControl(this.radioOptions[0].key, null)
    }),
    footerGroup:this.formBuilder.group({
      claimNip: new FormControl(0, null),
    }),
    yrmthList: new FormControl(null, null),

  });
  @ViewChild('mthlyTable', {static: false})
  private mthlyTable!: Table;
  @ViewChild('consolTable', {static: false})
  private consolTable!: Table;

  constructor(
    private formBuilder: FormBuilder,
    public dateUtilsService: DateUtilsService,
    private nspConfirmService: NspConfirmationService,
    private seClaimService: SEClaimService,
    private toastService: CustomToastService,
    private confirmationService: ConfirmationService,
    private exportUtilsService: ExportUtilsService,
    public accountService: AccountService,
    private cd: ChangeDetectorRef,
    @Inject(LOCALE_ID) protected localeId: string

  ) {
    this.claimJson = claim;

  }

  static createNewITXInsurFormGroup(selectedYearOption: number, formBuilder: FormBuilder, dateUtilsService: DateUtilsService): FormGroup{
    console.log("mila");
    const incomeTaxInsurFormGroup: FormGroup  = formBuilder.group({
      id: new FormControl(null, null),
      workingDataId: new FormControl('',null),
      consolidated: new FormControl(false, null),
      incomeTaxInsurArrayMthly: formBuilder.array([]),
      incomeTaxInsurArrayConsol: formBuilder.array([]),
      year: 0,
      captionGroup: formBuilder.group({
        radioOption: new FormControl('MTH', null)
      }),
      footerGroup:formBuilder.group({
        claimNip: new FormControl(0, null),
        proposedNip: new FormControl(0, null),
      }),
      yrmthList:new FormControl([], null)
    });

    //range is jan to dec of year of working data
    const claimStartYr = selectedYearOption;
    const claimStartDt = moment(new Date(claimStartYr, 0,1));
    const claimEndDt = moment(new Date(claimStartYr, 11,31));
    const colStartDt = claimStartDt.clone();
    const colEndDt = claimEndDt.clone();

    const colMthRange = [];
    for(let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')){
      colMthRange.push(mth.format("DD-MM-YYYY"));
    }
    const yrmthList  = colMthRange.map(x=>  moment(dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
    incomeTaxInsurFormGroup.get('yrmthList')?.setValue(yrmthList);
    const newDataList:any[] = [];
    let dataRecon:any[] = [];

    // construct empty for mthly view
    dataRecon = InsuranceIncomeTaxComponent.staticConstructConsolidatedHeaderData(newDataList, false, true, false,incomeTaxInsurFormGroup.get('yrmthList').value);
    InsuranceIncomeTaxComponent.staticConstructForm(newDataList, incomeTaxInsurFormGroup.get('incomeTaxInsurArrayMthly') as FormArray,incomeTaxInsurFormGroup.get('consolidated').value,  formBuilder);

    //construct empty for consolidated view
    dataRecon = InsuranceIncomeTaxComponent.staticConstructConsolidatedHeaderData(newDataList, true, true, false, incomeTaxInsurFormGroup.get('yrmthList').value);
    InsuranceIncomeTaxComponent.staticConstructForm(newDataList, incomeTaxInsurFormGroup.get('incomeTaxInsurArrayConsol') as FormArray, incomeTaxInsurFormGroup.get('consolidated').value, formBuilder);

    return incomeTaxInsurFormGroup;
  }

  static mapInsurITXFormGroupToWD(fg:FormGroup, obj: any): InsurITXWD{
    const dataList: InsurITXSubWD[] = [];
    const insurITXWD: InsurITXWD = {
      id: fg.get('id')?.value,
      workingDataId: obj.workingDataId,
      workingDataTypeCode: obj.workingDataTypeCode,
      year: fg?.get('year')?.value || obj.year,
      consolidated:  fg?.get('consolidated')?.value,
      claimNip: fg?.get('footerGroup.claimNip')?.value,
      archiveDt: obj.archiveDt,
      dataList
    };
    if(fg!==undefined && fg!==null){
      if(fg.get('consolidated').value) {
        const control = (fg.get('incomeTaxInsurArrayConsol') as FormArray).controls[0];
        const subWD: InsurITXSubWD = {
          firstYrCommission: control.get("firstYrCommission").value,
          singlePremium: control.get("singlePremium").value,
          prdCommission:control.get("prdCommission").value,
          generalInsurance: control.get("generalInsurance").value,
          otherCommission: control.get("otherCommission").value,
          prdBonus: control.get("prdBonus").value,
          renewalCommission: control.get("renewalCommission").value,
          overridingCommission: control.get("overridingCommission").value,
          otherNonPayable: control.get("otherNonPayable").value,
        }
        dataList.push(subWD);
      }else {
        console.log("the real data", (fg.get('incomeTaxInsurArrayMthly') as FormArray).controls);
        (fg.get('incomeTaxInsurArrayMthly') as FormArray).controls.forEach((control: AbstractControl) => {
          const subWD: InsurITXSubWD = {
            yrmth: control.get("yrmthReCon").value,
            firstYrCommission: control.get("firstYrCommission").value,
            singlePremium: control.get("singlePremium").value,
            prdCommission:control.get("prdCommission").value,
            generalInsurance: control.get("generalInsurance").value,
            otherCommission: control.get("otherCommission").value,
            prdBonus: control.get("prdBonus").value,
            renewalCommission: control.get("renewalCommission").value,
            overridingCommission: control.get("overridingCommission").value,
            otherNonPayable: control.get("otherNonPayable").value,
          }
          dataList.push(subWD);
        }
        );
      }
    }
    console.log("whole saved is", insurITXWD)
    return insurITXWD;
  }

  static mapInsurITXWDToFormGroup(insurITXWD: InsurITXWD, formBuilder: FormBuilder, dateUtilsService: DateUtilsService): FormGroup{
    const incomeTaxInsurForm: FormGroup  = formBuilder.group({
      workingDataId: new FormControl(insurITXWD.workingDataId),
      id: new FormControl(insurITXWD.id, null),
      consolidated: new FormControl(false, null),
      incomeTaxInsurArrayMthly: formBuilder.array([]),
      incomeTaxInsurArrayConsol: formBuilder.array([]),
      year: 0,
      captionGroup: formBuilder.group({
        radioOption: new FormControl('MTH', null)
      }),
      footerGroup: formBuilder.group({
        claimNip: new FormControl(0, null),
      }),
      yrmthList: new FormControl(null, null),

    });
    InsuranceIncomeTaxComponent.staticGenerateYrMth(insurITXWD,dateUtilsService, incomeTaxInsurForm.get('yrmthList'));
    let dataRecon:any[] = [];
    const newDataList:any[] = [];

    if(insurITXWD.consolidated === true){
      //expect 1
      if(insurITXWD.dataList){
        incomeTaxInsurForm.get('captionGroup.radioOption').setValue('CON');

        // construct populated for consolidated view
        dataRecon = InsuranceIncomeTaxComponent.staticConstructConsolidatedHeaderData(insurITXWD.dataList, true, false, false, incomeTaxInsurForm.get('yrmthList').value);
        InsuranceIncomeTaxComponent.staticConstructForm(dataRecon, incomeTaxInsurForm.get('incomeTaxInsurArrayConsol') as FormArray, incomeTaxInsurForm.get('consolidated').value, formBuilder);

        // construct empty for mthly view
        dataRecon = InsuranceIncomeTaxComponent.staticConstructConsolidatedHeaderData(newDataList, false, true, false,incomeTaxInsurForm.get('yrmthList').value);
        InsuranceIncomeTaxComponent.staticConstructForm(newDataList, incomeTaxInsurForm.get('incomeTaxInsurArrayMthly') as FormArray,incomeTaxInsurForm.get('consolidated').value,  formBuilder);
      }
    }else{
      //list of mthly records
      if(insurITXWD.dataList){
        incomeTaxInsurForm.get('captionGroup.radioOption').setValue('MTH');

        // construct populated for mthly view
        dataRecon = InsuranceIncomeTaxComponent.staticConstructConsolidatedHeaderData(insurITXWD.dataList, false, false, false, incomeTaxInsurForm.get('yrmthList').value);
        InsuranceIncomeTaxComponent.staticConstructForm(dataRecon, incomeTaxInsurForm.get('incomeTaxInsurArrayMthly') as FormArray, incomeTaxInsurForm.get('consolidated').value, formBuilder);

        //construct empty for consolidated view
        dataRecon = InsuranceIncomeTaxComponent.staticConstructConsolidatedHeaderData(newDataList, true, true, false, incomeTaxInsurForm.get('yrmthList').value);
        InsuranceIncomeTaxComponent.staticConstructForm(newDataList, incomeTaxInsurForm.get('incomeTaxInsurArrayConsol') as FormArray, incomeTaxInsurForm.get('consolidated').value, formBuilder);
      }
    }
    incomeTaxInsurForm.get('footerGroup.claimNip').setValue(insurITXWD?.claimNip);
    incomeTaxInsurForm.get('consolidated').setValue(insurITXWD?.consolidated);
    return incomeTaxInsurForm;
  }

  static staticConstructConsolidatedHeaderData(dataList: any[], consolidated: boolean, empty: boolean, newForm:boolean, yrmthList: any[]): any[]{
    //logic
    //12 columns of 12 mths from claimstartdate/trgStartDte - 13 mths to trgStartDte - 1 mths
    const sanitizedDataList:any[] =[];
    let sortedData;
     if(consolidated || empty || newForm){
      yrmthList.forEach((yrmth:any, colIndex:number)=>{
        dataList.push({yrmthReCon: yrmth});
      });
       sortedData = dataList;
     }else{
       yrmthList.forEach(yrmth =>{
         for(let i=0; i< dataList.length; i++){
           if(dataList[i].yrmth===yrmth && sanitizedDataList.findIndex(x=>x.yrmth === yrmth)<0){
             sanitizedDataList.push(dataList[i]);
           }
         }
       });
       yrmthList.forEach(yrmth =>{
         const index = sanitizedDataList.findIndex(x=> x.yrmth ===  yrmth);
         if(index<0){
           sanitizedDataList.push({yrmthReCon: yrmth});
         }else{
           sanitizedDataList[index].yrmthReCon = yrmth;
         }
       })
       sortedData = sanitizedDataList.sort((a:any, b:any)=>moment(a.yrmthReCon,"MM-YYYY") < moment(b.yrmthReCon,"MM-YYYY") ? -1 : 1);
     }
     return sortedData;
   }

  static staticGenerateYrMth(data: any, dateUtilsService: DateUtilsService, yrmthListCtrl: AbstractControl): void{
    //range is jan to dec of year of working data
    const claimStartYr =  data.year;
    const claimStartDt = moment(new Date(claimStartYr, 0,1));
    const claimEndDt = moment(new Date(claimStartYr, 11,31));
    const colStartDt = claimStartDt.clone();
    const colEndDt = claimEndDt.clone();
    let yrmthList:string[] = [];
    const colMthRange = [];
    for(let mth = colStartDt; mth.isSameOrBefore(colEndDt); mth.add(1, 'month')){
      colMthRange.push(mth.format("DD-MM-YYYY"));
    }
    yrmthList = colMthRange.map(x=>  moment(dateUtilsService.convertStrToDate(x, "DD-MM-YYYY")).format("MM-YYYY"));
    yrmthListCtrl?.setValue(yrmthList);
}

