  retrieveInsurWD():void{
    //if no changes were recorded, or newly archived take from db, otherwise take from local unsaved copy
    if((!this.currSelectedOption?.changes && !this.currSelectedOption.newWD)
    || (this.currSelectedOption.newArchived && (this.currSelectedOption?.obj.workingDataId!==null))) {
      console.log("newly archived is ", this.currSelectedOption.newArchived )

      let params = new HttpParams();
      params = params.append("workingDataId", this.currSelectedOption?.obj.workingDataId);
      this.seClaimService.retrieveInsurWD({params}).subscribe((resp:any) => {
        if(resp){
            if(resp.status === 200 && resp.body.data){
              this.generateYrMth(resp.body.data);
              this.populateIncomeTaxInsurForm(resp.body.data, false, null);
              this.valueChangeSubscribe();

            }
          }
        },
        (error: any) => {
          console.log("error is" + String(error?.error?.error_desc));
          this.toastService.error(String(error?.error?.error_desc));
        });

    }else if(this.currSelectedOption.changes && !this.currSelectedOption.newWD) {
      console.log("get from changes WD Map", this.workingDataWithChanges.get(this.currSelectedOption?.obj.workingDataId))
      this.incomeTaxInsurForm = this.workingDataWithChanges.get(this.currSelectedOption?.obj?.workingDataId).formGroup as FormGroup;
      this.yrmthList = this.incomeTaxInsurForm.get('yrmthList').value;
      this.setRadioOption(this.incomeTaxInsurForm.get('consolidated').value);
      this.loading = false;

    }else if(this.currSelectedOption.newWD) {
      console.log("get from new WD Map");
      console.log("selected key",this.currSelectedOption?.key.toString())
      this.incomeTaxInsurForm = this.newUnsavedWorkingData.get(this.currSelectedOption?.key.toString()).formGroup as FormGroup;
      if(this.incomeTaxInsurForm.get('consolidated')){
        this.setRadioOption(this.incomeTaxInsurForm.get('consolidated').value);
        //defaulted to monthly
      }else this.setRadioOption(false);
      this.loading = false;

    }



  }

  async confirmSwitch(e: Event,  type: string) {
    const value = (e.target as HTMLInputElement).value;
    console.log("incoming value",value)
    if (value !== this.incomeTaxInsurForm.get('captionGroup.radioOption').value) {
      e.preventDefault();
      const confirm = await this.showConfirm(e,type);
      if (!confirm) {
        return;
      }else{
        this.incomeTaxInsurForm.get('captionGroup.radioOption').setValue(value);
        this.incomeTaxInsurForm.get('consolidated').setValue(value === 'CON')
        this.clearForm(value);
      }
    }
