  refreshOtherClaim(): void{
    let params = new HttpParams();
    params = params.append("nric", this.currHardCopyDetails?.headerDto.nsManNric);
    params = params.append("yrmthList", JSON.stringify(this.yrmthList));
    params = params.append("caseId", this.currHardCopyDetails?.headerDto.caseId);

    this.seClaimService.retrieveRefreshOtherInsurClaims({params}).subscribe((resp:any) => {
      if(resp){
        if(resp.status === 200 && resp.body.data){
          resp.body.data.forEach((y:any)=> {
            const ctrl = this.incomeTaxInsurArrayMthly.controls.find(x=> x.get('yrmthReCon').value === y.yrmth)
            this.refreshOtherClaimSetValue(ctrl, y);
          });
          const unrefreshed = this.incomeTaxInsurArrayMthly.controls.filter(x=> !(resp.body.data.map((y:any)=> y.yrmth as string)).includes(x.get('yrmthReCon').value));
          unrefreshed.forEach(ctrl=> {
            this.refreshOtherClaimSetValue(ctrl, null);
          })
        }
      }
    });
  }

