  getSumOfCurrRow(formCtrlName: string): number
  {
    let sum = 0;

    for(let colIndex=0; colIndex<= this.incomeTaxInsurArrayMthly?.controls.length-1; colIndex++ ){
      sum+=this.incomeTaxInsurArrayMthly?.controls[colIndex].get(formCtrlName).value;
    }
    return sum;
  }

  getTotalPayableCol(colIndex: number, formArr: FormArray):number{
    let sum = 0;
    sum =
    Number(formArr.controls[colIndex].get('firstYrCommission').value|| 0 )
    +  Number(formArr.controls[colIndex].get('singlePremium').value|| 0 )
    +  Number(formArr.controls[colIndex].get('prdCommission').value|| 0 )
    +  Number(formArr.controls[colIndex].get('generalInsurance').value|| 0 )
    +  Number(formArr.controls[colIndex].get('otherCommission').value|| 0 )
    +  Number(formArr.controls[colIndex].get('prdBonus').value|| 0 )

      return sum;
  }

  getSumOfTotalPayable(): number
  {
    let sum = 0;
    sum =
    Number(this.getSumOfCurrRow('firstYrCommission'))
    + Number(this.getSumOfCurrRow('singlePremium'))
    + Number(this.getSumOfCurrRow('prdCommission'))
    + Number(this.getSumOfCurrRow('generalInsurance'))
    + Number(this.getSumOfCurrRow('otherCommission'))
    + Number(this.getSumOfCurrRow('prdBonus'))

    return sum;
  }

  getTotalNonPayableCol(colIndex: number, formArr: FormArray):number{
    let sum = 0;
    sum =
    Number(formArr.controls[colIndex]?.get('renewalCommission').value|| 0 )
    +  Number(formArr.controls[colIndex]?.get('overridingCommission').value|| 0 )
    +  Number(formArr.controls[colIndex]?.get('otherNonPayable').value|| 0 )


      return sum;
  }

  getSumOfTotalNonPayable(): number
  {
    let sum = 0;
    sum =
    Number(this.getSumOfCurrRow('renewalCommission'))
    + Number(this.getSumOfCurrRow('overridingCommission'))
    + Number(this.getSumOfCurrRow('otherNonPayable'))

    return sum;
  }

  //priority is Actual then provisional for ITX
  getActualNip(): number{
    this.displayActualNip = true;
    let irasRecord;
    let actualNip = 0;
    let actualNipComputation = "";
    let proposedNip = 0;
    let annualtotalComm = 0;
    let tradeIncome = 0;
    let displayActualNip = false;
    if(this.incomeTaxInsurForm.get('captionGroup.radioOption').value==='MTH'){

      annualtotalComm = this.getSumOfTotalNonPayable() +this. getSumOfTotalPayable();

      //if actual
      if(this.getOverridingCommPerc()<20 ){
        if(this.irasDetails?.filter((x:any)=> x.basis === "2").length>0){
          irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "2")[0];
          if(irasRecord.incomeYr === this.incomeTaxInsurForm.get('year').value){
            tradeIncome = irasRecord.tradeIncome;
            actualNip =  tradeIncome/annualtotalComm * 100 || 0;
            actualNipComputation = `${tradeIncome}/${annualtotalComm} * 100%`;
            proposedNip = actualNip>=80? 80: actualNip;
            displayActualNip = true;
          }else {
            proposedNip = 75;
          }
        //if provisional
        }else if(this.irasDetails?.filter((x:any)=> x.basis === "1").length>0){
          irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "1")[0];
          if(irasRecord.incomeYr === this.incomeTaxInsurForm.get('year').value){
            tradeIncome = irasRecord.tradeIncome;
            actualNip =  this.tradeIncome/annualtotalComm * 100 || 0;
            actualNipComputation = `${tradeIncome}/${annualtotalComm} * 100%`;
            proposedNip = actualNip;
            displayActualNip = true;
