      if(this.getOverridingCommPerc()<20 ){
        if(this.irasDetails?.filter((x:any)=> x.basis === "2").length>0){
          irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "2")[0];
          if(irasRecord.incomeYr === this.incomeTaxInsurForm.get('year').value){
            tradeIncome = irasRecord.tradeIncome;
            actualNip =  tradeIncome/annualtotalComm * 100|| 0;
            actualNipComputation = `${tradeIncome}/${annualtotalComm} * 100%`;
            proposedNip = actualNip>=80? 80: actualNip;
            displayActualNip = true;
          }else {
            proposedNip = 75;
          }
        //if provisional
        }else if(this.irasDetails?.filter((x:any)=> x.basis === "1").length>0){
          irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "1")[0];
          if(irasRecord.incomeYr === this.incomeTaxInsurForm.get('year').value){
            tradeIncome = irasRecord.tradeIncome;
            actualNip =  tradeIncome/annualtotalComm * 100|| 0;
            actualNipComputation = `${tradeIncome}/${annualtotalComm} * 100%`;
            proposedNip = actualNip;
            displayActualNip = true;
          }else {
            proposedNip = 75;
          }
        }else{
          proposedNip = 75;

        }

      }else if(this.getOverridingCommPerc()>=20){
        if(this.irasDetails?.filter((x:any)=> x.basis === "2").length>0){
          irasRecord =this.irasDetails?.filter((x:any)=> x.basis === "2")[0];
          if(irasRecord.incomeYr === this.incomeTaxInsurForm.get('year').value){
            tradeIncome = irasRecord.tradeIncome;
            actualNip =  (tradeIncome/annualtotalComm * 100 || 0) + (100 - tradeIncome/annualtotalComm *100 || 0)/2;
            actualNipComputation = `[${tradeIncome}/${annualtotalComm} * 100%] + [100%- ${tradeIncome}/${annualtotalComm} * 100%]/2`;
            proposedNip = actualNip>=80? 80: actualNip;
            displayActualNip = true;
          }else{
            proposedNip = 80;
          }
        //if provisional
        }else if(this.irasDetails?.filter((x:any)=> x.basis === "1").length>0){
          irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "1")[0];
          if(irasRecord.incomeYr === this.incomeTaxInsurForm.get('year').value){
            tradeIncome = irasRecord.tradeIncome;
            annualtotalComm = this.getTotalNonPayableCol(0, this.incomeTaxInsurArrayConsol) + this.getTotalPayableCol(0, this.incomeTaxInsurArrayConsol)
            actualNip =  (tradeIncome/annualtotalComm * 100|| 0) + (100 - tradeIncome/annualtotalComm *100|| 0)/2;
            actualNipComputation = `[${tradeIncome}/${annualtotalComm} * 100%] + [100%- ${tradeIncome}/${annualtotalComm} * 100%]/2`;
            proposedNip =actualNip;
            displayActualNip = true;
          }else{
            proposedNip = 80;
          }
        }else{
          proposedNip = 80;
        }

      }


    }
    this.proposedNip = this.roundVal(proposedNip);
    this.actualNipComputation = actualNipComputation;
    this.displayActualNip = displayActualNip;
    return this.roundVal(actualNip);

  }

  getOverridingCommPerc(): number{
    let rslt = 0;
    if(this.incomeTaxInsurForm.get('captionGroup.radioOption').value==='MTH'){
      rslt = this.getSumOfCurrRow('overridingCommission')/(this.getSumOfTotalNonPayable() +this. getSumOfTotalPayable()) * 100 || 0;
    }else{
      rslt = this.incomeTaxInsurArrayConsol.controls[0].get('overridingCommission').value/
      (this.getTotalNonPayableCol(0, this.incomeTaxInsurArrayConsol) + this.getTotalPayableCol(0, this.incomeTaxInsurArrayConsol)) * 100 || 0
    }
    return this.roundVal(rslt);
  }

  getTotalAmtPayable(): number{
    let amtPayable = 0;
    if(this.incomeTaxInsurForm.get('captionGroup.radioOption').value==='MTH'){
      amtPayable = this.footerGroup.get('claimNip').value /100 *
      this.getSumOfTotalPayable() + 0.42 * this.getSumOfCurrRow('firstYrCommission');
    }else {
      amtPayable = this.footerGroup.get('claimNip').value /100 * this.getTotalPayableCol(0, this.incomeTaxInsurArrayConsol) + 0.42 *
      this.incomeTaxInsurArrayConsol.controls[0].get('firstYrCommission').value
    }
    return this.roundVal(amtPayable);
  }

  getTotalAmtPayableDesc(): string{
    let amtPayableDesc = "";
    if(this.incomeTaxInsurForm.get('captionGroup.radioOption').value==='MTH'){
      amtPayableDesc = `${String(this.footerGroup.get('claimNip').value)} % * TPC(${String(this.getSumOfTotalPayable())})
      + 42% * FYC (${this.getSumOfCurrRow('firstYrCommission')}  `;
    }else{
      amtPayableDesc = `${String(this.footerGroup.get('claimNip').value)} % * TPC(${String(this.getTotalPayableCol(0, this.incomeTaxInsurArrayConsol))})
      + 42% * FYC (${String(this.incomeTaxInsurArrayConsol.controls[0].get('firstYrCommission').value)}) `;
    }
    return amtPayableDesc;

  }

  isTradeIncomeMorethanTotalCommision(): boolean{
    let irasRecord;
    //if actual
    if(this.irasDetails?.filter((x:any)=> x.basis === "2").length>0){
      irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "2")[0];
    //if provisional
    }else if(this.irasDetails?.filter((x:any)=> x.basis === "1").length>0){
      irasRecord = this.irasDetails?.filter((x:any)=> x.basis === "1")[0];
    }
    if(this.incomeTaxInsurForm.get('captionGroup.radioOption').value==='MTH'){
      return irasRecord?.tradeIncome> (this.getSumOfTotalNonPayable() + this.getSumOfTotalPayable());
    }else{
      return irasRecord?.tradeIncome > (this.getTotalNonPayableCol(0, this.incomeTaxInsurArrayConsol) + this.getTotalPayableCol(0, this.incomeTaxInsurArrayConsol));
    }
  }

  isEmpty(input: string):boolean{
    return input===null || input===undefined || input === "";
  }

  isWorkingDataTotalPayableNotEqualItemAnnualAmt(): boolean{
    const claimItemList = this.itemDetailsSection.itemForm?.get('otherSEItemArr') as FormArray;
    let itxItem = null;
    let annualtotalPayable = 0;
    if(claimItemList.length>0){
      itxItem = claimItemList?.controls.find(x=> x.get('itemType').value === 'ITX');
      const annualIncome = (itxItem?.get('otherSEItemTableArr') as FormArray).controls[0].get('annualIncome')?.value;
      if(this.incomeTaxInsurForm.get('captionGroup.radioOption').value==='MTH'){
        annualtotalPayable = this. getSumOfTotalPayable();
      }else {
        annualtotalPayable = this.getTotalPayableCol(0, this.incomeTaxInsurArrayConsol);
      }
      return annualtotalPayable !== annualIncome;

    }else return false;
  }

  exportToCSV(dataTable: Table): void{
    console.log("export to csv");
    const columns =
    [{field: 'rowHeader', header: " ", index: 0, customExportHeader: " "},
    {field: 'annualAccount', header: "Annual", index: 1}]

    for(let i = 0; i< this.yrmthList.length; i++ ){
      columns.push({field: String(this.yrmthList[i]), header:String(this.yrmthList[i]), index: i});
    }
    const dtoList:any[] = [
      {rowHeader: 'First Year Commission'},
      {rowHeader: 'Single Premium'},
      {rowHeader: 'Production Commission'},
      {rowHeader: "General Insurance"},
      {rowHeader: "Other Commission"},
      {rowHeader: "Production Bonus"},
      {rowHeader: "Total Payable Commission"},
      {rowHeader: "Renewal Commission"},
      {rowHeader: "Overriding Commission"},
      {rowHeader: "Other Non-Payable Commission"},
      {rowHeader: "Total Non-Payable Components"},
      {rowHeader: "Total Commission"},

    ];
    const dataList: InsurITXSubWD[] = [];
    let subWD: InsurITXSubWD;
    if(this.incomeTaxInsurForm?.get('captionGroup.radioOption').value==='CON'){
      this.exportAddConsol(dataList, subWD, dtoList);
    }else{
      this.exportAddMthly(dataList, subWD, dtoList);
    }
    this.exportUtilsService.exportCSV(dataTable, dtoList,10, columns,
      "insurance_income_tax_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
  }

  exportAddConsol(dataList: InsurITXSubWD[], subWD: InsurITXSubWD, dtoList: any[]){
    const control = (this.incomeTaxInsurForm.get('incomeTaxInsurArrayConsol') as FormArray).controls[0];
    subWD = {
      firstYrCommission: control.get("firstYrCommission").value,
      singlePremium: control.get("singlePremium").value,
      prdCommission:control.get("prdCommission").value,
      generalInsurance: control.get("generalInsurance").value,
      otherCommission: control.get("otherCommission").value,
      prdBonus: control.get("prdBonus").value,
      renewalCommission: control.get("renewalCommission").value,
      overridingCommission: control.get("overridingCommission").value,
      otherNonPayable: control.get("otherNonPayable").value,
    }
    dataList.push(subWD);

    dataList.forEach(x=>{

      //add consol
      dtoList[0]['annualAccount'] = x.firstYrCommission;
      dtoList[1]['annualAccount'] = x.singlePremium;
      dtoList[2]['annualAccount'] = x.prdCommission;
      dtoList[3]['annualAccount'] = x.generalInsurance;
      dtoList[4]['annualAccount'] = x.otherCommission;
      dtoList[5]['annualAccount'] = x.prdBonus;
      dtoList[6]['annualAccount'] = x.firstYrCommission + x.singlePremium + x.prdCommission + x.generalInsurance + x.otherCommission + x.prdBonus;//total payable commission
      dtoList[7]['annualAccount'] = x.renewalCommission;
      dtoList[8]['annualAccount'] = x.overridingCommission;
      dtoList[9]['annualAccount'] = x.otherNonPayable;
      dtoList[10]['annualAccount'] = x.renewalCommission + x.overridingCommission + x.otherNonPayable;
      dtoList[11]['mtannualAccounthAccount'] = +dtoList[6]['annualAccount'] + +dtoList[10]['annualAccount']

    });
  }


  exportAddMthly(dataList: InsurITXSubWD[], subWD: InsurITXSubWD, dtoList: any[]){
    //mthly
    console.log("the lada ", this.incomeTaxInsurForm.get('incomeTaxInsurArrayMthly'));
    (this.incomeTaxInsurForm.get('incomeTaxInsurArrayMthly') as FormArray).controls.forEach((control: AbstractControl) => {
      subWD = {
        yrmth: control.get("yrmthReCon").value,
        firstYrCommission: control.get("firstYrCommission").value,
        singlePremium: control.get("singlePremium").value,
        prdCommission:control.get("prdCommission").value,
        generalInsurance: control.get("generalInsurance").value,
        otherCommission: control.get("otherCommission").value,
        prdBonus: control.get("prdBonus").value,
        renewalCommission: control.get("renewalCommission").value,
        overridingCommission: control.get("overridingCommission").value,
        otherNonPayable: control.get("otherNonPayable").value,
      }
      dataList.push(subWD);
    });

     dataList.forEach(x=>{
      dtoList[0][x.yrmth] = x.firstYrCommission;
      dtoList[1][x.yrmth] = x.singlePremium;
      dtoList[2][x.yrmth] = x.prdCommission;
      dtoList[3][x.yrmth] = x.generalInsurance;
      dtoList[4][x.yrmth] = x.otherCommission;
      dtoList[5][x.yrmth] = x.prdBonus;
      dtoList[6][x.yrmth] = x.firstYrCommission + x.singlePremium + x.prdCommission + x.generalInsurance + x.otherCommission + x.prdBonus;
      dtoList[7][x.yrmth] = x.renewalCommission;
      dtoList[8][x.yrmth] = x.overridingCommission;
      dtoList[9][x.yrmth] = x.otherNonPayable;
      dtoList[10][x.yrmth] = x.renewalCommission + x.overridingCommission + x.otherNonPayable;
      dtoList[11][x.yrmth] = +dtoList[6][x.yrmth] + +dtoList[10][x.yrmth]
    });
    dtoList[0]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.firstYrCommission}));
    dtoList[1]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.singlePremium}));
    dtoList[2]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.prdCommission}));
    dtoList[3]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.generalInsurance}));
    dtoList[4]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.otherCommission}));
    dtoList[5]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.prdBonus}));
    dtoList[6]['annualAccount'] =  +dtoList[0]['annualAccount'] + +dtoList[1]['annualAccount'] + +dtoList[2]['annualAccount'] + +dtoList[3]['annualAccount'] + +dtoList[4]['annualAccount'] + +dtoList[5]['annualAccount'];//total payable commission
    dtoList[7]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.renewalCommission}));
    dtoList[8]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.overridingCommission}));
    dtoList[9]['annualAccount'] = this.getSumByField(dataList.map(x=> {return x.otherNonPayable}));
    dtoList[10]['annualAccount'] = +dtoList[7]['annualAccount'] + +dtoList[8]['annualAccount'] + +dtoList[9]['annualAccount'];
    dtoList[11]['annualAccount'] = +dtoList[6]['annualAccount'] + +dtoList[10]['annualAccount'];

  }

  getSumByField(fieldArray: number[]): number{
    const initValue = 0;
    const sum = fieldArray.reduce((accumulator, currentValue) => accumulator + currentValue, initValue);
    return sum;
  }

    //allows input on need basis
    hasValidAuthorityAndStation(): boolean{
      if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_VERIFIER) &&
        this.station === NSPAY2_STATION.STATION_VERIFICATION){
          return true;
        }
      if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) &&
      this.station === NSPAY2_STATION.STATION_CERTIFICATION) {
        return false;
      }
      if(this.accountService.hasAnyAuthority(NSPAY2_ROLES.CLAIMS_APPROVER) &&
      this.station === NSPAY2_STATION.STATION_APPROVAL) {
        return false;
      }
      return false;
    }

}
