                            </tr>
                            <tr  *ngIf="rowIndex== 11">
                                <td  style="width:200px">{{ cols[11] }}</td>
                                <td [style]="{'border-bottom': '2px solid black', 'border-top': '2px solid black'}" *ngFor="let data of incomeTaxInsurArrayConsol.controls; let i = index" [formGroupName] = "i">
                                    <div style="text-align:right">
                                        {{ getTotalNonPayableCol(i, incomeTaxInsurArrayConsol) + getTotalPayableCol(i, incomeTaxInsurArrayConsol) | number : '1.2-2'}} <span *ngIf="i===0">&nbsp;pa</span>
                                    </div>
                                </td>
                            </tr>
                    </ng-template>
                </p-table>
            </ng-template>
            <div class="grid" style="margin-top:1em; margin-left: 220px">
                <form [formGroup] = "footerGroup">
                    <div class = "col-12 justify-content-start gap-4 flex align-items-center" >
                        <div>Claim NIP:
                            <p-inputNumber *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation();else labelClaimNip"
                            [maxlength]=3 [inputStyle]="{'width': '5em', 'text-align':'right'}" formControlName = "claimNip"
                            suffix="%">
                            </p-inputNumber>
                            <small *ngIf="incomeTaxInsurForm.controls['footerGroup'].get('claimNip')?.touched && !isValidNumber(incomeTaxInsurForm.controls['footerGroup'].get('claimNip')?.value)"
                            class="p-error" icTranslate="nspay2App.claim.validation.CLM-V002"
                            [translateValues]="{ field: 'Claim NIP' }">
                            </small>
                            <ng-template #labelClaimNip>
                                {{  incomeTaxInsurForm.controls['footerGroup'].get('claimNip').value }}%
                            </ng-template>
                        </div>

                        <div>Proposed NIP: {{ proposedNip }} %</div>
                        <div>Actual NIP:

                                {{ getActualNip()}}% <span *ngIf = "!isEmpty(actualNipComputation)">({{ actualNipComputation }})</span>

                        </div>
                        <div>Overriding Commission: {{ getOverridingCommPerc() }} %</div>
                    </div>
                    <div class = "col-12 justify-content-start gap-4 flex align-items-center" >
                        <div>Total Amount Payable:
                            {{getTotalAmtPayable()}}
                            ({{ getTotalAmtPayableDesc()}})
                    </div>
                    </div>
                    <div class = "col" *ngIf = "isActive() && !readOnly && hasValidAuthorityAndStation()" >
                        <!-- (click)="tester()" -->
                        <p-button label="Copy to Item"  (click)="copyToItem($event)" [disabled] = "!enableCopytoItem()"
                        ></p-button>
                    </div>
                </form>
            </div>

        </div>
    </div>
</form>
